    "contracts/Pool/BhavishNoLossPool.sol": {
      "content": "// SPDX-License-Identifier: BSD-4-Clause
pragma solidity 0.8.13;
import { AbstractPool, Address, DateTimeLibrary } from "./AbstractPool.sol";
import { BhavishNoLossGameToken } from "../Tokens/BhavishNoLossGameToken.sol";
import { BhavishRewardToken } from "../Tokens/BhavishRewardToken.sol";

contract BhavishNoLossPool is AbstractPool, BhavishNoLossGameToken {
    using Address for address;

    BhavishRewardToken public rewardToken;

    constructor(
        address _farm,
        address _rewardToken,
        uint8 _decimal,
        address _validate,
        address _admin
    ) AbstractPool(_farm, _admin) BhavishNoLossGameToken("Bhavish Game NoLoss Chips", "BGNToken", _decimal, _validate) {
        rewardToken = BhavishRewardToken(_rewardToken);
        rewardDistribution = RewardDistribution({ protocol: 10, provider: 45, winner: 45 });
        poolData.liquidityLockupDuration = 1 days;
    }

    function getAPYRewards(address _provider) public view override returns (uint256 rewards) {
        if (_calcAmount(_provider) > providers[_provider].amount) {
            rewards = _calcAmount(_provider) - providers[_provider].amount;
        }
    }

    function getBalance(address _provider) public view override returns (uint256 amount) {
        amount = balanceOf(_provider);
    }

    function _calcAmount(address _provider) internal view returns (uint256) {
        return convertToAssets(providers[_provider].shares);
    }

    // separate method for redeeming BRN tokens
    // as there is lockup for apy rewards
    // but no lockup for redeeming BRN tokens
    function claimWinningRewards(address _user) external override returns (uint256 rewards) {
        address user_;
        if (address(msg.sender).isContract()) {
            require(depositAllowedContracts[msg.sender] || msg.sender == admin, "invalid caller");
            user_ = _user;
        } else {
            require(msg.sender == _user, "Buyer and msg.sender cannot be different");
            user_ = msg.sender;
        }
        rewards = getWinningRewards(user_);
        rewardToken.burn(user_, rewardToken.balanceOf(user_));
        poolData.rewardPool -= rewards;
        _withdrawFromFarm(rewards, msg.sender);

        emit ClaimWinnings(user_, rewards);
    }

    function getWinningRewards(address _provider) public view override returns (uint256 rewards) {
        if (rewardToken.balanceOf(_provider) > 0) {
            rewards = (rewardToken.balanceOf(_provider) * poolData.rewardPool) / rewardToken.totalSupply();
        }
    }

    function afterDeposit(address _user) internal override {
        _mint(_user, msg.value * poolData.multiplier);
    }

    function afterWithdraw(address _user, uint256 _amount) internal override {
        _burn(_user, _amount);
    }

    function withdraw(address _user, uint256 _amount) external nonReentrant {
        address user_ = _validateUser(_user);
        ProviderDetails memory provider = providers[_user];
        require(provider.date + poolData.liquidityLockupDuration <= block.timestamp, "cannot withdraw with in lockup");
        if (balanceOf(_user) < provider.amount * poolData.multiplier) _reset(_user);
        require(provider.amount > 0, "no liquidity for user");
        // this withdraw is used for admin contract withdraw as well, without provider.amount
        _withdraw(user_, _amount);
        _updateDetails(user_, _amount);
    }

    function reset() external {
        require(
            providers[msg.sender].date + poolData.liquidityLockupDuration <= block.timestamp,
            "reset not available"
        );
        if (balanceOf(msg.sender) < providers[msg.sender].amount * poolData.multiplier) {
            providers[msg.sender].date = block.timestamp;
            _reset(msg.sender);
        }
    }

    function _reset(address _user) private {
        _mint(_user, providers[_user].amount * poolData.multiplier - balanceOf(_user));
    }
}
"
