    "contracts/Pool/AbstractPool.sol": {
      "content": "// SPDX-License-Identifier: BSD-4-Clause
pragma solidity 0.8.13;

import { IYieldFarming } from "../Interface/IYieldFarming.sol";
import { DateTimeLibrary } from "../Libs/DateTimeLibrary.sol";
import { Ownable } from "@openzeppelin/contracts/access/Ownable.sol";
import { Address } from "@openzeppelin/contracts/utils/Address.sol";
import { ReentrancyGuard } from "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import { SafeERC20 } from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import { Math } from "@openzeppelin/contracts/utils/math/Math.sol";
import { IERC20Extended } from "../Libs/IERC20Extended.sol";

abstract contract AbstractPool is Ownable, ReentrancyGuard {
    using Address for address;
    using Math for uint256;
    using SafeERC20 for IERC20Extended;

    event Deposit(address indexed _account, uint256 _amount);
    event Withdraw(address indexed _account, uint256 _amount);
    event ClaimAPY(address indexed _account, uint256 _amount);
    event ClaimWinnings(address indexed _account, uint256 _amount);
    event ClaimFee(address indexed _account, uint256 _amount);
    event DistributeRewards(uint256 _protocol, uint256 _provider, uint256 _winner);

    struct ProviderDetails {
        uint256 amount;
        uint256 shares;
        uint256 date;
        uint256 lastClaimed;
    }

    struct RewardDistribution {
        uint8 protocol;
        uint8 provider;
        uint8 winner;
    }

    struct PoolData {
        uint256 totalLiquidity;
        uint256 shares;
        uint256 multiplier;
        uint256 latestLiquidityEvent;
        uint256 liquidityLockupDuration;
        uint256 rewardLockupDuration;
        uint256 providerPoolProfit;
        uint256 rewardPool;
        uint256 protocolPool;
        uint256 minDeposit;
    }

    IYieldFarming public farm;
    PoolData public poolData;
    mapping(address => bool) public depositAllowedContracts;
    address public admin;
    RewardDistribution public rewardDistribution;

    mapping(address => ProviderDetails) public providers;

    // Implement following virtual methods ----

    function getAPYRewards(address _provider) public view virtual returns (uint256 rewards);

    function getBalance(address _provider) public view virtual returns (uint256 amount);

    // separate method for redeeming BRN tokens
    // as there is lockup for apy rewards
    // but no lockup for redeeming BRN tokens
    function claimWinningRewards(address _user) external virtual returns (uint256 rewards);

    function getWinningRewards(address _provider) public view virtual returns (uint256 rewards);

    function afterDeposit(address _user) internal virtual;

    function afterWithdraw(address _user, uint256 _amount) internal virtual;

    constructor(address _farm, address _admin) {
        farm = IYieldFarming(_farm);
        poolData.multiplier = 100;
        poolData.rewardLockupDuration = 7 days;
        poolData.minDeposit = 0.1 ether;
        admin = _admin;
    }

    function _validateUser(address _user) internal view returns (address user_) {
        if (address(msg.sender).isContract()) {
            require(depositAllowedContracts[msg.sender], "invalid caller");
            user_ = _user;
        } else {
            require(msg.sender == _user, "Buyer and msg.sender cannot be different");
            user_ = msg.sender;
        }
    }

    function deposit(address _user) external payable nonReentrant {
        require(msg.value >= poolData.minDeposit, "amount < minDeposit");
        address user_ = _validateUser(_user);
        uint256 shares = convertToShares(msg.value);
        ProviderDetails storage provider = providers[user_];
        if (provider.amount == 0 || provider.date + poolData.liquidityLockupDuration < block.timestamp)
            provider.date = block.timestamp;
        provider.amount += msg.value;
        provider.shares += shares;
        poolData.totalLiquidity += msg.value;
        poolData.shares += shares;

        // deposit for apy
        farm.deposit{ value: msg.value }();

        afterDeposit(user_);

        emit Deposit(user_, msg.value);
    }

    function convertToShares(uint256 _amount) public view returns (uint256) {
        uint256 supply = poolData.shares;
        return (_amount == 0 || supply == 0) ? _amount : _amount.mulDiv(supply, totalAssets(), Math.Rounding.Down);
    }

    function totalAssets() public view returns (uint256) {
        return poolData.totalLiquidity + poolData.providerPoolProfit;
    }

    function _withdraw(address _user, uint256 _amount) internal {
        require(_amount <= getBalance(_user), "cannot withdraw > token balance");
        uint256 rewards = getAPYRewards(_user);
        uint256 balance = _amount / poolData.multiplier;
        if (providers[_user].date + poolData.rewardLockupDuration <= block.timestamp) {
            _withdrawFromFarm(rewards + balance, _user);
            // deduct provider pool profit
            emit ClaimAPY(_user, rewards);
        } else {
            _withdrawFromFarm(balance, _user);
            poolData.protocolPool += rewards;
        }
        // update pool data
        poolData.totalLiquidity -= balance;
        poolData.providerPoolProfit -= rewards;
        afterWithdraw(_user, _amount);

        emit Withdraw(_user, _amount);
    }

    function _updateDetails(address _user, uint256 _amount) internal {
        ProviderDetails storage provider = providers[_user];
        provider.date = block.timestamp;
        _amount = _amount / poolData.multiplier;
        uint256 shares;
        // use max(provider.amount, getBalance(_user)) while calc shares
        if (getBalance(_user) > provider.amount * poolData.multiplier)
            shares = (provider.shares * _amount) / getBalance(_user);
        else shares = (provider.shares * _amount) / provider.amount;

        poolData.shares -= shares;
        provider.shares -= shares;
        provider.amount -= _amount;
    }

    function convertToAssets(uint256 _shares) public view virtual returns (uint256) {
        uint256 supply = poolData.shares;
        return (supply == 0) ? _shares : _shares.mulDiv(totalAssets(), supply, Math.Rounding.Down);
    }

    function getProfit() public view returns (uint256 profit) {
        profit =
            IERC20Extended(farm.getYieldToken()).balanceOf(address(this)) -
            (poolData.totalLiquidity + poolData.providerPoolProfit + poolData.rewardPool + poolData.protocolPool);
    }

    // aTokens will be minted in pool contract while deposit
    // this method is called every day to distribute the rewards
    function distributeRewards() external {
        uint256 profit = getProfit();
        if (profit == 0) return;

        poolData.providerPoolProfit += (profit * rewardDistribution.provider) / 100;
        if (rewardDistribution.protocol > 0) poolData.protocolPool += (profit * rewardDistribution.protocol) / 100;
        if (rewardDistribution.winner > 0) poolData.rewardPool += (profit * rewardDistribution.winner) / 100;
        emit DistributeRewards(
            (profit * rewardDistribution.provider) / 100,
            (profit * rewardDistribution.protocol) / 100,
            (profit * rewardDistribution.winner) / 100
        );
    }

    function claimFee() external {
        uint256 fee = poolData.protocolPool;
        poolData.protocolPool = 0;
        require(fee > 0, "no rewards");
        _withdrawFromFarm(fee, admin);
        emit ClaimFee(admin, fee);
    }

    function _withdrawFromFarm(uint256 _amount, address _user) internal {
        IERC20Extended yieldToken = IERC20Extended(farm.getYieldToken());
        yieldToken.transfer(address(farm), _amount);
        farm.withdraw(_amount, _user);
    }

    function getMultiplier() external view returns (uint256) {
        return poolData.multiplier;
    }

    function getProviderAmount(address _user) external view returns (uint256) {
        return providers[_user].amount;
    }

    function setDepositAllowedContracts(address _addr) external onlyOwner {
        require(_addr.isContract(), "invalid address");
        depositAllowedContracts[_addr] = true;
    }

    function removeDepositAllowedContracts(address _addr) external onlyOwner {
        require(depositAllowedContracts[_addr], "disabled/invalid address");
        depositAllowedContracts[_addr] = false;
    }

    function setLiquidityLockupDuration(uint256 _duration) external onlyOwner {
        require(_duration >= 0 && _duration <= 30 days, "invalid duration");
        poolData.liquidityLockupDuration = _duration;
    }

    function setRewardLockupDuration(uint256 _duration) external onlyOwner {
        require(_duration >= 1 days && _duration <= 30 days, "invalid duration");
        poolData.rewardLockupDuration = _duration;
    }

    function setMultiplier(uint256 _multiplier) external onlyOwner {
        poolData.multiplier = _multiplier;
    }

    function setMinDeposit(uint256 _amount) external onlyOwner {
        require(_amount >= 0.1 ether && _amount <= 10 ether, "invalid amount");
        poolData.minDeposit = _amount;
    }

    function setAdmin(address _admin) external onlyOwner {
        require(_admin != address(0), "invalid address");
        admin = _admin;
    }

    function setRewardDistribution(
        uint8 _protocol,
        uint8 _provider,
        uint8 _winner
    ) external onlyOwner {
        require(_protocol + _provider + _winner == 100, "invalid distribution");
        rewardDistribution = RewardDistribution({ protocol: _protocol, provider: _provider, winner: _winner });
    }
}
"
