    "contracts/GsoMarketPlace.sol": {
      "content": "//SPDX-License-Identifier: MIT
pragma solidity 0.8.4;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/utils/Counters.sol";
import "@openzeppelin/contracts/token/ERC721/utils/ERC721Holder.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/Address.sol";
import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import "./MarketStorage.sol";
import "./common/TransferHelper.sol";
import "./common/AccessController.sol";

contract GsoMarketPlace is AccessController, ERC721Holder, ReentrancyGuard, Pausable {
  using Counters for Counters.Counter;
  using SafeERC20 for IERC20;
  using Address for address;
  using ECDSA for bytes32;

  MarketStorage private _storageAddress;
  mapping(uint256 => uint256) private creatorFeeByNftType;
  mapping(uint256 => address) private creatorAddressByNftType;
  mapping(uint256 => address) private commissionAddressByNftType;
  mapping(uint256 => uint256) private commissionFeeByCurrencyType;

  constructor(address _storage) {
    _storageAddress = MarketStorage(_storage);
    _setupRole(DEFAULT_ADMIN_ROLE, _msgSender());
  }
  
  struct PriceByTimestamp {
    uint256 currentPrice;
    uint256 timestamp;
  }

  event SELLORDER(uint256 orderId, uint256 nftId, uint256 price, address seller, uint nftType, uint256 timeStamp, uint256 ranking, uint256 currencyType, bool isExchangePrice);
  event BUYMARKETITEM(uint256 orderId, uint256 nftId, uint256 price, address seller, address buyer, uint nftType, uint256 currencyType, uint256 transactionFee, uint256 timeStamp);
  event CANCELSELLORDER(uint256 orderId, uint256 nftId, uint256 price, address seller, uint nftType, uint256 timeStamp);

  function pause() external onlyAdmin {
      _pause();
  }

  function unpause() external onlyAdmin {
      _unpause();
  }

  function setCommissionFeeByCurrencyType(uint256[] calldata currencyExchanges, uint256[] calldata feePerCurrency) external onlyAdmin {
    require(currencyExchanges.length == feePerCurrency.length, "Invalid input");
    for(uint256 index = 0; index < currencyExchanges.length; index++){
      commissionFeeByCurrencyType[currencyExchanges[index]] = feePerCurrency[index];
    }
  }

  function setCommissionAddressByNftType(uint256[] calldata _nftTypes, address[] calldata _commissionAddresses) external onlyAdmin {
    require(_nftTypes.length == _commissionAddresses.length, "Invalid input");
    for(uint256 index = 0; index < _nftTypes.length; index++){
      commissionAddressByNftType[_nftTypes[index]] = _commissionAddresses[index];
    }
  }

  function setCreatorFeeByNftType(uint256[] calldata _nftTypes, uint256[] calldata _creatorFees, address[] calldata _creatorAddresses) external onlyAdmin {
    require(_nftTypes.length == _creatorFees.length && _nftTypes.length == _creatorAddresses.length, "Invalid Input: Array length mismatch");
    for(uint256 index = 0; index < _nftTypes.length; index++){
      require(_creatorFees[index] > 0 && _creatorAddresses[index] != address(0) , "Invalid input") ;
      creatorFeeByNftType[_nftTypes[index]] = _creatorFees[index];
      creatorAddressByNftType[_nftTypes[index]] = _creatorAddresses[index];
    }
  }
  
  function createMartketItem(
    uint256 _nftId,
    uint256 _price,
    uint256 _nftType,
    uint256 currencyType,
    bool isExchangePrice
  ) external nonReentrant whenNotPaused {
    MarketStorage.StorageInfor memory info = _storageAddress.getInfor();
    require(currencyType < info.countCurrency, "Invalid currency type");
    IERC721 _nftAddress = _storageAddress.getNftAddress(_nftType);
    require(_nftType < info.countNft, "INVALID_NFT_TYPE");
    require(_nftAddress.ownerOf(_nftId) == _msgSender(), "Owner token: not owner!");
    require(_nftId > 0, "Token id must be greater than 0");
    require(_storageAddress.isBlockAddress(_msgSender()) == false, "Address: Address can not impact it!");
    address seller = _msgSender();
    require(_price > 0, "Order price must be greater than 0");
    MarketStorage.OrderItem memory currentItem = _storageAddress.createItemOrder(_nftId, _price, _nftType, seller);
    _transferNFT(seller, address(this), _nftId, _nftType);
    emit SELLORDER(currentItem.id, _nftId, _price, seller, _nftType, block.timestamp, 0, currencyType, isExchangePrice);
  }

  function buyMartketItem(
    uint256 _nftId,
    uint256 _ranking,
    string memory _orderId,
    bytes memory _signature,
    uint256 _nftType,
    uint256 _currencyType,
    uint256 _sellOrderId,
    PriceByTimestamp memory priceByTimestamp
  ) external nonReentrant whenNotPaused {
    MarketStorage.StorageInfor memory info = _storageAddress.getInfor();
    require(priceByTimestamp.timestamp + info.maxWaitingTime > block.timestamp, "EXCEED_WAITING_TIME");
    require(_nftType < info.countNft, "INVALID_NFT_TYPE");
    require(_storageAddress.isExecutedOrder(_orderId) == false, "ORDER_IS_EXECUTED");
    require(_validatorSignature(_nftId, _sellOrderId, _ranking, _orderId, _signature, _msgSender(), _nftType, priceByTimestamp.currentPrice, priceByTimestamp.timestamp, _currencyType, info.adminVerify), "INVALID_PARAM");
    require(_nftId > 0, "Index must be greater than 0");
    require(_storageAddress.isBlockAddress(_msgSender()) == false, "Address: Address can not impact it!");
    address buyer = _msgSender();
    _storageAddress.setExecutedOrder(_orderId, true);
    MarketStorage.OrderItem memory order = _storageAddress.getOrder(_nftId, _nftType);
    address token = _storageAddress.getCurrencyExchange(_currencyType);
    uint256 fees = commissionFeeByCurrencyType[_currencyType];
    require(order.id == _sellOrderId && order.itemId == _nftId, "Order does not exist or invalid order");
    require(_ranking >= order.ranking, "Ranking: not enough!");
    require(order.seller != address(0), "Invalid seller");
    require(order.seller != buyer, "Buy by seller");
    require(priceByTimestamp.currentPrice > 0, "Current price must be greater than 0");
    TransferHelper.safeEnoughTokenApproved(token, _msgSender(), address(this), priceByTimestamp.currentPrice);
    _storageAddress.deleteSellOrder(_nftId, _nftType);
    _payment(token, priceByTimestamp.currentPrice, buyer, order.seller, _nftType, fees);
    _transferNFT(address(this), buyer, order.itemId, _nftType);
    emit BUYMARKETITEM(order.id, order.itemId,  priceByTimestamp.currentPrice, order.seller, buyer, _nftType, _currencyType, fees, block.timestamp);
  }

  function cancelOrder(uint256 _nftId, uint256 _nftType) external nonReentrant {
    MarketStorage.StorageInfor memory info = _storageAddress.getInfor();
    require(_nftType < info.countNft, "INVALID_NFT_TYPE");
    require(_nftId > 0, "Index must be greater than 0");
    address sender = _msgSender();
    MarketStorage.OrderItem memory order = _storageAddress.getOrder(_nftId, _nftType);
    require(order.id != 0 && order.itemId == _nftId, "Order does not exist or invalid order");
    require(order.seller == sender, "The sender must be the seller");
     _storageAddress.deleteSellOrder(_nftId, _nftType);
    _transferNFT(address(this), order.seller, _nftId, _nftType);
    emit CANCELSELLORDER(order.id, _nftId, order.unitPrice, order.seller, _nftType, block.timestamp);
  }

  function _payment(
    address currencyExchange,
    uint256 paymentValue,
    address sender,
    address seller,
    uint256 nftType,
    uint256 fee
  ) private {
    uint256 tax = paymentValue * fee / 1000;
    TransferHelper.safeTransferFrom(currencyExchange, sender, commissionAddressByNftType[nftType], tax);
    if(creatorFeeByNftType[nftType] != 0){
      uint256 creatorFee = paymentValue * creatorFeeByNftType[nftType] / 1000;
      TransferHelper.safeTransferFrom(currencyExchange, sender, creatorAddressByNftType[nftType], creatorFee);
      TransferHelper.safeTransferFrom(currencyExchange, sender, seller, paymentValue - tax - creatorFee);
    } else {
      TransferHelper.safeTransferFrom(currencyExchange, sender, seller, paymentValue - tax);
    }
  }

  function _transferNFT(
    address from,
    address to,
    uint256 nftId,
    uint256 _nftType
  ) private {
    IERC721 _nftAddress = _storageAddress.getNftAddress(_nftType);
    return _nftAddress.safeTransferFrom(from, to, nftId);
  }

  function _validatorSignature(
    uint256 _nftId,
    uint256 _sellOrderId,
    uint256 _ranking,
    string memory _orderId,
    bytes memory signature,
    address _wallet,
    uint256 _nftType,
    uint256 _price,
    uint256 timestamp,
    uint256 _currencyType,
    address verifyAddress
  ) private pure returns (bool) {
    bytes32 hashValue = keccak256(abi.encodePacked(_nftId, _sellOrderId, _orderId, _ranking, _wallet, _nftType, _price, _currencyType, timestamp));
    address recover = hashValue.toEthSignedMessageHash().recover(signature);
    return recover == verifyAddress;
  }
}"
