    "contracts/MarketStorage.sol": {
      "content": "//SPDX-License-Identifier: MIT
pragma solidity 0.8.4;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/utils/Counters.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/Address.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import "./CommonItem.sol";
import "./IEnums.sol";

contract MarketStorage is AccessControl, ReentrancyGuard, Pausable {
  using Counters for Counters.Counter;
  using SafeERC20 for IERC20;
  using Address for address;
  using SafeMath for uint256;
  using ECDSA for bytes32;

  uint256 private _maxWaitingTime;
  address[] private _currencyExchanges;
  uint256[] private _feePerCurrency;
  address private _receiveAddress;
  bytes32 private constant OPERATOR = keccak256("OPERATOR");
  bytes32 private constant MARKET = keccak256("MARKET");
  bytes32 private constant USER_MANAGEMENT = keccak256("USER_MANAGEMENT");
  address public adminVerify;
  mapping(address => bool) private _addressLocked;
  mapping(string => bool) private _executedOrderIds;
  mapping(uint256 => Counters.Counter) private _orderIds;
  mapping(IEnums.MarketType => mapping(uint256 => Counters.Counter)) private _orderERC1155Ids;
  IERC721[] private _nftAddress;
  address private _officalAddress;
  address private _commonItem;

  constructor() {
    _receiveAddress = msg.sender;
    adminVerify = msg.sender;
    _setupRole(DEFAULT_ADMIN_ROLE, msg.sender);
    _setupRole(USER_MANAGEMENT, msg.sender);
  }

  struct OrderItem {
    uint256 id;
    uint256 itemId;
    uint256 nftType;
    uint256 unitPrice;
    address seller;
    uint256 ranking;
  }

  struct OrderERC1155 {
    uint256 id;
    uint256 itemId;
    uint256 unitPrice;
    address seller;
    uint256 ranking;
    uint256 amount;
  }

  struct StorageInfor {
    uint256 countNft;
    address receiveAddress;
    uint256 maxWaitingTime;
    address adminVerify;
    address officalAddress;
    uint256 countCurrency;
  }

  mapping(uint256 => mapping(uint256 => OrderItem)) public orders;
  mapping(uint256 => mapping(uint256 => mapping(uint256 => OrderERC1155))) public orderERC1155;

  modifier onlyUserManagement() {
    require(hasRole(USER_MANAGEMENT, msg.sender), "Restricted to user management.");
    _;
  }

  modifier onlyOperator() {
    require(hasRole(OPERATOR, msg.sender), "Restricted to OPERATOR.");
    _;
  }

  modifier onlyAdmin() {
    require(hasRole(DEFAULT_ADMIN_ROLE, msg.sender), "Restricted to admin.");
    _;
  }

  modifier onlyMarket() {
    require(hasRole(MARKET, msg.sender), "Restricted to market.");
    _;
  }

  function setCurrencyExchangesAndFee(address[] memory currencyExchanges, uint256[] memory feePerCurrency) external onlyAdmin {
    require(currencyExchanges.length == feePerCurrency.length, "Invalid input");
    for(uint256 index =0; index < currencyExchanges.length; index++){
      _currencyExchanges.push(currencyExchanges[index]);
      _feePerCurrency.push(feePerCurrency[index]);
    }
  }

  function reloadExchangesAndFee(address[] memory currencyExchanges, uint256[] memory feePerCurrency) external onlyAdmin {
    require(currencyExchanges.length > 0, "INVALD_WALLET");
    require(_currencyExchanges.length >= currencyExchanges.length, "INVALD_PARAMS");
    for(uint256 i = 0; i < currencyExchanges.length; i++) {
        _currencyExchanges[i] = currencyExchanges[i];
        _feePerCurrency[i] = feePerCurrency[i];
    }
  }

  function setNftAddress(IERC721[] memory nftAddresses) external onlyAdmin {
    for(uint index = 0;index < nftAddresses.length;index++) {
      _nftAddress.push(nftAddresses[index]);
    }
  }

  function reloadNftAddress(IERC721[] memory nftAddresses) external onlyAdmin {
    require(nftAddresses.length > 0, "INVALD_WALLET");
    require(_nftAddress.length >= nftAddresses.length, "INVALD_PARAMS");
    for(uint256 i = 0; i < nftAddresses.length; i++) {
        _nftAddress[i] = nftAddresses[i];
    }
  }

  function setMaxWaitingTime(uint256 maxWaitingTime) external onlyAdmin {
    _maxWaitingTime = maxWaitingTime;
  }

  function setReceiveAddress(address receiveAddress) external onlyAdmin {
    _receiveAddress = receiveAddress;
  }

  function setAdminVerify(address admin) external onlyAdmin {
    adminVerify = admin;
  }

  function setOperator(address _operator) external onlyAdmin {
    _setupRole(OPERATOR, _operator);
  }

  function setUserManagement(address _user) external onlyAdmin {
    _setupRole(USER_MANAGEMENT, _user);
  }

  function setMarket(address _market) external onlyAdmin {
    _setupRole(MARKET, _market);
  }

  function setOfficalAddress(address _wallet) external onlyAdmin {
    _officalAddress = _wallet;
  }

  function setCommonItem(address _address) external onlyAdmin {
    _commonItem = _address;
  }

  function blockUser(address _addressBlock) external onlyUserManagement {
    require(hasRole(DEFAULT_ADMIN_ROLE, msg.sender) || hasRole(USER_MANAGEMENT, msg.sender), "PERMISSION_DENIED");
    require(_addressBlock != address(0), "INVALID_ADDRESS");
    require(_addressLocked[_addressBlock] == false, "ADDRESS_LOCKED");
    _addressLocked[_addressBlock] = true;
  }

  function unlockUser(address _addressBlock) external onlyUserManagement {
    require(hasRole(DEFAULT_ADMIN_ROLE, msg.sender) || hasRole(USER_MANAGEMENT, msg.sender), "PERMISSION_DENIED");
    require(_addressBlock != address(0), "INVALID_ADDRESS");
    require(_addressLocked[_addressBlock] == true, "ADDRESS_NOT_FOUND");
    _addressLocked[_addressBlock] = false;
    delete _addressLocked[_addressBlock];
  }

  function isBlockAddress(address userWallet) external view returns (bool) {
    return _addressLocked[userWallet];
  }

  function createItemOrder(
    uint256 _nftId,
    uint256 _price,
    uint256 _nftType,
    address _seller
  ) external onlyMarket nonReentrant whenNotPaused returns (OrderItem memory) {
    OrderItem memory existedOrder = orders[_nftId][_nftType];
    require(existedOrder.id == 0, "Sell order is already existed");
    _orderIds[_nftType].increment();
    uint256 orderId = _orderIds[_nftType].current();
    orders[_nftId][_nftType] = OrderItem({
      id: orderId,
      unitPrice: _price,
      nftType: _nftType,
      itemId: _nftId,
      seller: _seller,
      ranking: 0
    });
    return orders[_nftId][_nftType];
  }

  function createOrderERC1155(
    uint256 _nftId,
    uint256 _price,
    uint256 _nftType,
    uint256 _amount,
    uint256 _ranking,
    address _seller,
    IEnums.MarketType _type
  ) external onlyMarket nonReentrant whenNotPaused returns (OrderERC1155 memory) {
    _orderERC1155Ids[_type][_nftType].increment();
    uint256 orderId = _orderERC1155Ids[_type][_nftType].current();
    orderERC1155[_nftType][orderId][_nftId] = OrderERC1155({
      id: orderId,
      unitPrice: _price,
      itemId: _nftId,
      seller: _seller,
      ranking: _ranking,
      amount: _amount
    });
    return orderERC1155[_nftType][orderId][_nftId];
  }

  function deleteSellOrder(uint256 _nftId, uint256 _nftType) external onlyMarket {
    orders[_nftId][_nftType].id = 0;
    delete orders[_nftId][_nftType];
  }

  function deleteSellOrderERC1155(uint256 _nftId, uint256 _nftType, uint256 _sellOrder) external onlyMarket {
    orderERC1155[_nftType][_sellOrder][_nftId].id = 0;
    delete orderERC1155[_nftType][_sellOrder][_nftId];
  }

  function getNftAddress(uint256 _nftType) external onlyMarket view returns (IERC721) {
    return _nftAddress[_nftType];
  }

  function getCurrencyExchangeAndFee(uint256 currencyType) external onlyMarket view returns (address, uint256) {
    return (_currencyExchanges[currencyType], _feePerCurrency[currencyType]);
  }

  function getCurrencyExchange(uint256 currencyType) external view returns (address) {
    return _currencyExchanges[currencyType];
  }

  function isExecutedOrder(string memory _orderId) external onlyMarket view returns (bool) {
    return _executedOrderIds[_orderId];
  }

  function getOrder(uint256 _nftId, uint256 _nftType) external onlyMarket view returns (OrderItem memory) {
    return orders[_nftId][_nftType];
  }

  function getOrderERC1155(uint256 _nftType, uint256 _sellOrder, uint256 _nftId) external onlyMarket view returns (OrderERC1155 memory) {
    return orderERC1155[_nftType][_sellOrder][_nftId];
  }

  function setExecutedOrder(string memory _orderId, bool _status) external onlyMarket {
    _executedOrderIds[_orderId] = _status;
  }

  function getInfor() external onlyMarket view returns (StorageInfor memory) {
    StorageInfor memory info = StorageInfor({
      countNft: _nftAddress.length,
      receiveAddress: _receiveAddress,
      maxWaitingTime: _maxWaitingTime,
      adminVerify: adminVerify,
      officalAddress: _officalAddress,
      countCurrency: _currencyExchanges.length
    });
    return info;
  }
}
"
    },
    "contracts/common/TransferHelper.sol": {
      "content": "//SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

library TransferHelper {
    function safeApprove(
        address token,
        address to,
        uint256 value
    ) internal {
        // bytes4(keccak256(bytes('approve(address,uint256)')));
        (bool success, bytes memory data) = token.call(abi.encodeWithSelector(0x095ea7b3, to, value));
        require(
            success && (data.length == 0 || abi.decode(data, (bool))),
            'TransferHelper::safeApprove: approve failed'
        );
    }
    function safeTransfer(
        address token,
        address to,
        uint256 value
    ) internal {
        // bytes4(keccak256(bytes('transfer(address,uint256)')));
        (bool success, bytes memory data) = token.call(abi.encodeWithSelector(0xa9059cbb, to, value));
        require(
            success && (data.length == 0 || abi.decode(data, (bool))),
            'TransferHelper::safeTransfer: transfer failed'
        );
    }
    function safeTransferFrom(
        address token,
        address from,
        address to,
        uint256 value
    ) internal {
        // bytes4(keccak256(bytes('transferFrom(address,address,uint256)')));
        (bool success, bytes memory data) = token.call(abi.encodeWithSelector(0x23b872dd, from, to, value));
        require(
            success && (data.length == 0 || abi.decode(data, (bool))),
            'TransferHelper::transferFrom: transferFrom failed'
        );
    }

    function safeEnoughTokenApproved(
        address token,
        address owner,
        address spender,
        uint256 amount
    ) internal {
        // bytes4(keccak256(bytes('allowance(address,address)')));
        (bool success, bytes memory data) = token.call(abi.encodeWithSelector(0xdd62ed3e, owner, spender));
        require( success && (abi.decode(data, (uint256)) >= amount), "Exchange currency allowance of user is too low");
        safeEnoughBalance(token, owner, amount);
    }

    function safeEnoughBalance(
        address token,
        address owner,
        uint256 amount
    ) internal {
        (bool success, bytes memory data) = token.call(abi.encodeWithSelector(0x70a08231, owner));
        require( success && (abi.decode(data, (uint256)) > amount), "Exchange currency balance of user is too low");
    }
}
"
    },
    "contracts/common/AccessController.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity 0.8.4;

import "@openzeppelin/contracts/access/AccessControl.sol";

contract AccessController is AccessControl {
  bytes32 public constant OPERATOR = keccak256("OPERATOR");

  modifier onlyAdmin() {
    require(hasRole(DEFAULT_ADMIN_ROLE, _msgSender()), "Caller is not the admin");
    _;
  }

  modifier onlyOperator() {
    require(hasRole(OPERATOR, _msgSender()), "Caller is not the operator");
    _;
  }

  function addOperator(address operator) external onlyAdmin {
    _setupRole(OPERATOR, operator);
  }

  function removeOperator(address operator) external onlyAdmin {
    revokeRole(OPERATOR, operator);
  }
}
"
    },
