    "contracts/CommonItem.sol": {
      "content": "//SPDX-License-Identifier: MIT
pragma solidity 0.8.4;

import "@openzeppelin/contracts/token/ERC1155/ERC1155.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";

contract CommonItem is ERC1155, AccessControl {
  using ECDSA for bytes32;
  bytes32 private constant OPERATOR = keccak256("OPERATOR");
  uint256[] private _mintedAddress;

  constructor() ERC1155("http://api-market.genso.game/game/{id}.json") {
    _setupRole(DEFAULT_ADMIN_ROLE, msg.sender);
  }

  modifier onlyOperator() {
    require(hasRole(OPERATOR, msg.sender), "Restricted to OPERATOR.");
    _;
  }

  modifier onlyAdmin() {
    require(hasRole(DEFAULT_ADMIN_ROLE, msg.sender), "Restricted to admin.");
    _;
  }

  function setOperator(address _operator) external onlyAdmin {
    _setupRole(OPERATOR, _operator);
  }

  function mintItemFromMarket(uint256 _nftId, uint256 _amount, address _wallet) external onlyOperator {
    _mint(_wallet, _nftId, _amount, "");
    if (existedId(_nftId) != true) {
      _mintedAddress.push(_nftId);
    }
  }

  function existedId(uint256 _nftId) private view returns (bool) {
    bool isExisted = false;
    for (uint256 i = 0; i < _mintedAddress.length; i++) {
      if (_mintedAddress[i] == _nftId) {
        isExisted = true;
      }
    }
    return isExisted;
  }

  function burnItem(uint256 _nftId, uint256 _amount, address _wallet) external {
    require(hasRole(OPERATOR, msg.sender) || hasRole(DEFAULT_ADMIN_ROLE, msg.sender), "PERMISSION_DENIED");
    _burn(_wallet, _nftId, _amount);
  }

  function uri(uint256 _tokenId) override public pure returns (string memory) {
    return string(
      abi.encodePacked("http://api-market.genso.game/game/", Strings.toString(_tokenId), ".json")
    );
  }

  function name() public pure returns(string memory) {
    return "GENSOKISHI COLLECTION";
  }

  function getTokensOfUser(address _user)
    external
    view
    returns
    (uint256[] memory, uint256[] memory, string[] memory)
  {
    uint256 _counter;
    for (uint256 i = 0; i < _mintedAddress.length; i++) {
      if (balanceOf(_user, _mintedAddress[i]) != 0) {
        _counter++;
      }
    }

    uint256[] memory ticketIds = new uint256[](_counter);
    string[] memory metadata = new string[](_counter);
    uint256[] memory balances = new uint256[](_counter);
    uint256 index;

    for (uint256 i = 0; i < _mintedAddress.length; i++) {
      uint256 balance = balanceOf(_user, _mintedAddress[i]);
      if (balance != 0) {
        ticketIds[index] = _mintedAddress[i];
        metadata[index] = uri(_mintedAddress[i]);
        balances[index] = balance;
        index++;
      }
    }
    return (ticketIds, balances, metadata);
  }

  function supportsInterface(bytes4 interfaceId)
    public
    view
    virtual
    override(AccessControl, ERC1155)
    returns (bool)
  {
    return super.supportsInterface(interfaceId);
  }
}
"
    },
    "contracts/IEnums.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.4;

interface IEnums {
    enum MarketType {
        Ticket,
        Consumption
    }
    enum GachaGacha {
        Normal,
        Rare
    }
}
"
