contract NaturaToken is ERC20, Ownable, ERC20Snapshot, ERC20Permit, ERC20Votes {
    using SafeMath for uint256;

    uint256 private GOLD_RESERVE = 0;
    uint256 private BITCOIN_RESERVE = 0;
    uint256 private MISC_RESERVE = 0;
    uint256 private OTHER_NATURA_RELEASED = 0;

    uint256 private NATURA_CIRCULATION_PERCENTAGE = 300; // percentage in bips 10000 = 100%

    AggregatorV3Interface internal priceFeed;
    IUniswapV2Router02 public immutable uniswapV2Router;
    address public immutable uniswapV2Pair;
    address public constant deadAddress = address(0xdead);
    Staking public stakingContract;

    bool private swapping;

    address public marketingWallet = 0xF572a157622C260186b6de19F999a328E035910D;

    address public devWallet = 0xF572a157622C260186b6de19F999a328E035910D;
    
    uint256 public swapTokensAtAmount;

    bool public swapEnabled = true;

    uint256 public buyTotalFees;
    uint256 public buyMarketingFee;
    uint256 public buyLiquidityFee;
    uint256 public buyDevFee;
    uint256 public buyBurnFee;
    
    uint256 public sellTotalFees;
    uint256 public sellMarketingFee;
    uint256 public sellLiquidityFee;
    uint256 public sellDevFee;
    uint256 public sellBurnFee;
    
    uint256 private tokensForMarketingFee;
    uint256 private tokensForLiquidity;
    uint256 private tokensForDev;
    uint256 private tokensForBurn;
    
    /******************/

    // exlcude from fees and max transaction amount
    mapping (address => bool) private _isExcludedFromFees;

    // store addresses that a automatic market maker pairs. Any transfer *to* these addresses
    // could be subject to a maximum transfer amount
    mapping (address => bool) public automatedMarketMakerPairs;

    event UpdateUniswapV2Router(address indexed newAddress, address indexed oldAddress);

    event ExcludeFromFees(address indexed account, bool isExcluded);

    event SetAutomatedMarketMakerPair(address indexed pair, bool indexed value);

    event marketingWalletUpdated(address indexed newWallet, address indexed oldWallet);

    event SwapAndLiquify(
        uint256 tokensSwapped,
        uint256 ethReceived,
        uint256 tokensIntoLiquidity
    );

    constructor() ERC20("Natura Token", "Natura") ERC20Permit("Natura Token") {

        address newOwner = address(owner());
        /**
        * Network: Polygon
        * Aggregator: BTC/USD
        * Address: 0xc907E116054Ad103354f2D350FD2514433D57F6f
        */
        
        /**
        * Network: Mumbai Testnet
        * Aggregator: BTC/USD
        * Address: 0x007A22900a3B98143368Bd5906f8E17e9867581b
        */

        priceFeed = AggregatorV3Interface(
            0x007A22900a3B98143368Bd5906f8E17e9867581b
        );

        // 0xa5E0829CaCEd8fFDD4De3c43696c57F7D7A678ff // Polygon mainnet 
        // 0xbdd4e5660839a088573191A9889A262c0Efc0983 // Polygon Testnet 

        IUniswapV2Router02 _uniswapV2Router = IUniswapV2Router02(0xbdd4e5660839a088573191A9889A262c0Efc0983);
        
        uniswapV2Router = _uniswapV2Router;
        
        uniswapV2Pair = IUniswapV2Factory(_uniswapV2Router.factory()).createPair(address(this), _uniswapV2Router.WETH());
        _setAutomatedMarketMakerPair(address(uniswapV2Pair), true);
        
        uint256 _buyMarketingFee = 4;
        uint256 _buyDevFee = 0;
        uint256 _buyLiquidityFee = 1;
        uint256 _buyBurnFee = 0;

        
        uint256 _sellMarketingFee = 4;
        uint256 _sellDevFee = 0;
        uint256 _sellLiquidityFee = 1;
        uint256 _sellBurnFee = 0;
        
        uint256 totalSupply = 88888888 * 10**18;
        
        swapTokensAtAmount = 88888 *10**18;


        buyMarketingFee = _buyMarketingFee;
        buyDevFee = _buyDevFee;
        buyLiquidityFee = _buyLiquidityFee;
        buyBurnFee = _buyBurnFee;
        buyTotalFees = buyMarketingFee + buyLiquidityFee + buyDevFee + buyBurnFee;
        
        sellMarketingFee = _sellMarketingFee;
        sellDevFee = _sellDevFee;
        sellLiquidityFee = _sellLiquidityFee;
        sellBurnFee = _sellBurnFee;
        sellTotalFees = sellMarketingFee + sellLiquidityFee + sellDevFee + sellBurnFee;
        

        // exclude from paying fees or having max transaction amount
        excludeFromFees(newOwner, true);
        excludeFromFees(address(this), true);
        excludeFromFees(address(0xdead), true);
        excludeFromFees(marketingWallet, true);
        excludeFromFees(devWallet, true);
        
        /*
            _mint is an internal function in ERC20.sol that is only called here,
            and CANNOT be called ever again
        */
        _mint(newOwner, totalSupply);
        transferOwnership(newOwner);
    }

    receive() external payable {

  	}
    
    // change the minimum amount of tokens to sell from fees
    function updateSwapTokensAtAmount(uint256 newAmount) external onlyOwner returns (bool){
  	    require(newAmount >= totalSupply() * 1 / 100000, "Swap amount cannot be lower than 0.001% total supply.");
  	    require(newAmount <= totalSupply() * 1 / 1000, "Swap amount cannot be higher than 0.1% total supply.");
  	    swapTokensAtAmount = newAmount;
  	    return true;
  	}
    
    // only use to disable contract sales if absolutely necessary (emergency use only)
    function updateSwapEnabled(bool enabled) external onlyOwner(){
        swapEnabled = enabled;
    }
    
    function updateBuyFees(uint256 _marketingFee, uint256 _liquidityFee, uint256 _devFee, uint256 _burnFee) external onlyOwner {
        buyMarketingFee = _marketingFee;
        buyLiquidityFee = _liquidityFee;
        buyDevFee = _devFee;
        buyBurnFee = _burnFee;
        buyTotalFees = buyMarketingFee + buyLiquidityFee + buyDevFee + buyBurnFee;
        require(buyTotalFees <= 15, "Must keep fees at 15% or less");
    }
    
    function updateSellFees(uint256 _marketingFee, uint256 _liquidityFee, uint256 _devFee, uint256 _burnFee) external onlyOwner {
        sellMarketingFee = _marketingFee;
        sellLiquidityFee = _liquidityFee;
        sellDevFee = _devFee;
        sellBurnFee = _burnFee;
        sellTotalFees = sellMarketingFee + sellLiquidityFee + sellDevFee + sellBurnFee;
        require(sellTotalFees <= 15, "Must keep fees at 15% or less");
    }

    function updateWallets(address _devWallet, address _marketingWallet) external onlyOwner {
        devWallet = _devWallet;
        marketingWallet = _marketingWallet;
    }

    function excludeFromFees(address account, bool excluded) public onlyOwner {
        _isExcludedFromFees[account] = excluded;
        emit ExcludeFromFees(account, excluded);
    }

    function setAutomatedMarketMakerPair(address pair, bool value) public onlyOwner {
        require(pair != uniswapV2Pair, "The pair cannot be removed from automatedMarketMakerPairs");

        _setAutomatedMarketMakerPair(pair, value);
    }

    function _setAutomatedMarketMakerPair(address pair, bool value) private {
        automatedMarketMakerPairs[pair] = value;

        emit SetAutomatedMarketMakerPair(pair, value);
    }

    function isExcludedFromFees(address account) public view returns(bool) {
        return _isExcludedFromFees[account];
    }

    function _transfer(
        address from,
        address to,
        uint256 amount
    ) internal override {
        require(from != address(0), "ERC20: transfer from the zero address");
        require(to != address(0), "ERC20: transfer to the zero address");
        
         if(amount == 0) {
            super._transfer(from, to, 0);
            return;
         }
        
        
        
		uint256 contractTokenBalance = balanceOf(address(this));
        
        bool canSwap = contractTokenBalance >= swapTokensAtAmount;

        if( 
            canSwap &&
            swapEnabled &&
            !swapping &&
            !automatedMarketMakerPairs[from] &&
            !_isExcludedFromFees[from] &&
            !_isExcludedFromFees[to]
        ) {
            swapping = true;
            
            swapBack();

            swapping = false;
        }
        
        bool takeFee = !swapping;

        // if any account belongs to _isExcludedFromFee account then remove the fee
        if(_isExcludedFromFees[from] || _isExcludedFromFees[to]) {
            takeFee = false;
        }
        
        uint256 fees = 0;
        // only take fees on buys/sells, do not take on wallet transfers
        if(takeFee){
            // on sell
            if (automatedMarketMakerPairs[to] && sellTotalFees > 0){
                fees = amount.mul(sellTotalFees).div(100);
                tokensForLiquidity += fees * sellLiquidityFee / sellTotalFees;
                tokensForMarketingFee += fees * sellMarketingFee / sellTotalFees;
                tokensForDev += fees * sellDevFee / sellTotalFees;
                tokensForBurn += fees * sellBurnFee / sellTotalFees;
            }
            // on buy
            else if(automatedMarketMakerPairs[from] && buyTotalFees > 0) {
        	    fees = amount.mul(buyTotalFees).div(100);
        	    tokensForLiquidity += fees * buyLiquidityFee / buyTotalFees;
                tokensForMarketingFee += fees * buyMarketingFee / buyTotalFees;
                tokensForDev += fees * buyDevFee / buyTotalFees;
                tokensForBurn += fees * buyBurnFee / buyTotalFees;
            }
            
            if(tokensForLiquidity > 0){    
                super._transfer(from, address(this), tokensForLiquidity);
            }

            if(tokensForMarketingFee > 0){
                super._transfer(from, marketingWallet, tokensForMarketingFee);
                
            }

            if(tokensForBurn > 0){
                super._transfer(from, address(deadAddress), tokensForBurn);
            }

            if(tokensForDev > 0){
                super._transfer((from), devWallet, tokensForDev);
            }
        	
            tokensForLiquidity = 0;
            tokensForMarketingFee = 0;
            tokensForDev = 0;
            tokensForBurn = 0;

        	amount -= fees;
        }

        super._transfer(from, to, amount);
    }

    function swapTokensForEth(uint256 tokenAmount) private {

        // generate the uniswap pair path of token -> weth
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = uniswapV2Router.WETH();

        _approve(address(this), address(uniswapV2Router), tokenAmount);

        // make the swap
        uniswapV2Router.swapExactTokensForETHSupportingFeeOnTransferTokens(
            tokenAmount,
            0, // accept any amount of ETH
            path,
            address(this),
            block.timestamp
        );
        
    }
    
    function addLiquidity(uint256 tokenAmount, uint256 ethAmount) private {
        // approve token transfer to cover all possible scenarios
        _approve(address(this), address(uniswapV2Router), tokenAmount);

        // add the liquidity
        uniswapV2Router.addLiquidityETH{value: ethAmount}(
            address(this),
            tokenAmount,
            0, // slippage is unavoidable
            0, // slippage is unavoidable
            deadAddress,
            block.timestamp
        );
    }

    function swapBack() private {
        uint256 contractBalance = balanceOf(address(this));
        uint256 totalTokensToSwap = swapTokensAtAmount;
        
        if(contractBalance == 0 || totalTokensToSwap == 0) {return;}
        
        // Halve the amount of liquidity tokens
        uint256 liquidityTokens = contractBalance / 2;
        uint256 amountToSwapForETH = contractBalance.sub(liquidityTokens);
        
        uint256 initialETHBalance = address(this).balance;

        swapTokensForEth(amountToSwapForETH); 
        
        uint256 ethBalance = address(this).balance.sub(initialETHBalance);
        
        
        uint256 ethForLiquidity = ethBalance;
        
        
    
        addLiquidity(liquidityTokens, ethForLiquidity);
        emit SwapAndLiquify(amountToSwapForETH, ethForLiquidity, tokensForLiquidity);
        
    }

    function withdrawStuckBNB() external onlyOwner{
        require (address(this).balance > 0, "Can't withdraw negative or zero");
        payable(owner()).transfer(address(this).balance);
    }

    function removeStuckToken(address _address) external onlyOwner {
        require(_address != address(this), "Can't withdraw tokens destined for liquidity");
        require(IERC20(_address).balanceOf(address(this)) > 0, "Can't withdraw 0");

        IERC20(_address).transfer(owner(), IERC20(_address).balanceOf(address(this)));
    }

    function snapshot() public onlyOwner {
        _snapshot();
    }

    function _beforeTokenTransfer(address from, address to, uint256 amount)
        internal
        override(ERC20, ERC20Snapshot)
    {
        super._beforeTokenTransfer(from, to, amount);
    }

    function _afterTokenTransfer(address from, address to, uint256 amount)
        internal
        override(ERC20, ERC20Votes)
    {
        super._afterTokenTransfer(from, to, amount);
    }

    function _mint(address to, uint256 amount)
        internal
        override(ERC20, ERC20Votes)
    {
        super._mint(to, amount);
    }

    function _burn(address account, uint256 amount)
        internal
        override(ERC20, ERC20Votes)
    {
        super._burn(account, amount);
    }

    function _getGoldReserves() public view returns(uint256) {
        return GOLD_RESERVE;
    }

    function _setGoldReserves(uint256 _goldReserves) public onlyOwner {
        GOLD_RESERVE = _goldReserves;
    }

    function _getBitcoinReserves() public view returns(uint256) {
        return BITCOIN_RESERVE;
    }

    function _setBitcoinReserves(uint256 _bitcoinReserves) public onlyOwner {
        BITCOIN_RESERVE = _bitcoinReserves;
    }

    function _getMiscReserves() public view returns(uint256) {
        return MISC_RESERVE;
    }

    function _setMiscReserves(uint256 _miscReserves) public onlyOwner {
        MISC_RESERVE = _miscReserves;
    }

    function _getNaturaCirculationPercentage() public view returns(uint256) {
        return NATURA_CIRCULATION_PERCENTAGE;
    }

    function _setNaturaCirculationPercentage(uint256 _percentage) public onlyOwner {
        NATURA_CIRCULATION_PERCENTAGE = _percentage;
    }

    function _getOtherNaturaReleased() public view returns(uint256) {
        return OTHER_NATURA_RELEASED;
    }

    function _setOtherNaturaReleased(uint256 _naturaReleased) public onlyOwner {
        OTHER_NATURA_RELEASED = _naturaReleased;
    }

    function setTerraStaking(address _stakingAddress) external onlyOwner {
        stakingContract = Staking(_stakingAddress);
    }

    function getLatestPrice() public view returns (int) {
        (
            /* uint80 roundID */,
            int price,
            /*uint startedAt*/,
            /*uint timeStamp*/,
            /*uint80 answeredInRound*/
        ) = priceFeed.latestRoundData();
        return price;
    }

    function getBtcReserveValue() public view returns (uint256) {
        uint256 btcUsd = uint256(getLatestPrice());
        uint256 btc_res = BITCOIN_RESERVE.div(10**18);
        uint256 btc_usd = btcUsd.div(10**8);
        return btc_res.mul(btc_usd);
    }


    function getNaturaPrice() public view returns (uint256) {
        uint256 _totNftPrices = 0;
        uint256 _stakingRewardsClaimed = 0;
        if(address(stakingContract) != address(0)) {
            _totNftPrices = stakingContract.totNftPrice();
            _totNftPrices = _totNftPrices.div(10**6);
            _stakingRewardsClaimed = stakingContract.totalRewardsClaimed();
        }
        uint256 btcReservesValue = getBtcReserveValue();
        
        uint256 tot_supply = totalSupply();

        tot_supply = tot_supply.div(10**18);

        uint256 circulation = tot_supply.mul(NATURA_CIRCULATION_PERCENTAGE).div(10000);

        // sum of circulation, other natura released and natura distributed as a reward from staking contract
        uint256 sum_circulation_other_natura_released = circulation.add(OTHER_NATURA_RELEASED.div(10**18)).add(_stakingRewardsClaimed.div(10**18));

        // sum of V1, V2, V3 and V4
        uint256 sumOfAll = _totNftPrices.add(GOLD_RESERVE).add(btcReservesValue).add(MISC_RESERVE);
        uint256 result = sumOfAll.mul(10**6).div(sum_circulation_other_natura_released);

        return result;
    }
     
}
