pragma solidity 0.8.17;

// import "@openzeppelin/contracts/token/ERC20/IERC20.sol";





//import "@openzeppelin/contracts/utils/math/SafeMath.sol";

interface TSupply {
    function totalSupply() external view returns(uint256);
}

contract TerraStaking is Ownable, ReentrancyGuard, IERC721Receiver {
    using SafeMath for uint256;
    using SafeERC20 for NaturaToken;

    NaturaToken reward_token;

    struct StakeInfo {
        uint24 tokenId;
        address owner;
        bool initialized;
        uint256 timestamp;
    }

    struct PoolInfo {
        address nftAddress;
        uint256 cost;
        uint256 totalSupply;
    }

    uint256 public totalStaked;
    uint256 public totNftPrice;
    uint256 public totalRewardsClaimed;

    PoolInfo[] public poolInfo;

    mapping(uint256 => mapping(uint24 => StakeInfo)) public vault; // pid => (tokenId => StakeInfo)


    event NFTStaked(address indexed nftAddress, address indexed owner, uint256 indexed tokenId, uint256 value);
    event NFTUnstaked(address indexed nftAddress, address indexed owner, uint256 indexed tokenId, uint256 value);
    event Claimed(address indexed owner, uint256 amount);

    constructor(NaturaToken _reward_token) {
        reward_token = _reward_token;
    }

    function poolLength() external view returns (uint256) {
        return poolInfo.length;
    }

    // add nft contract to the pool
    function add(address _nftAddress, uint256 _cost, uint256 _totalSupply) public onlyOwner {
        require(_nftAddress != address(0),  "Nft address is zero");
        uint256 totalCost = _totalSupply.mul(_cost);
        totNftPrice += totalCost;
        poolInfo.push(PoolInfo({
        nftAddress: _nftAddress,
        cost: _cost,
        totalSupply: _totalSupply
        }));
    }

    // update cost of given pool
    function set(uint256 _pid, uint256 _cost) public onlyOwner {
        uint256 oldPoolPrice = poolInfo[_pid].totalSupply.mul(poolInfo[_pid].cost);
        uint256 newPoolPrice = poolInfo[_pid].totalSupply.mul(_cost);
        totNftPrice = totNftPrice.sub(oldPoolPrice).add(newPoolPrice);
        poolInfo[_pid].cost = _cost;
    }

    //stake nft
    function stake(uint256[] calldata _pids, uint24[] calldata _tokenIds) external {
        require(_tokenIds.length > 0, "no token ids supplied");
        require(_pids.length > 0, "no pids ids supplied");
        require(_pids.length ==  _tokenIds.length, "pids length and token length is not matched");

        uint24 tokenId;
        uint256 pid;
        totalStaked += _tokenIds.length;
        for (uint i = 0; i < _tokenIds.length; i++) {
            tokenId = _tokenIds[i];
            pid = _pids[i];
            PoolInfo memory pooldata = poolInfo[pid];
            require(IERC721(pooldata.nftAddress).ownerOf(tokenId) == msg.sender, "not your token");
            require(vault[pid][tokenId].initialized == false, 'already staked');

            IERC721(pooldata.nftAddress).safeTransferFrom(msg.sender, address(this), tokenId);
            emit NFTStaked(pooldata.nftAddress, msg.sender, tokenId, block.timestamp);

            vault[pid][tokenId] = StakeInfo({
                tokenId: tokenId,
                owner: msg.sender,
                initialized: true,
                timestamp: block.timestamp
            });
        }
    }

    // unstake nfts
    function _unstakeMany(uint256[] calldata _pids, uint24[] calldata _tokenIds) external nonReentrant {
        require(_tokenIds.length > 0, "no token ids supplied");
        require(_pids.length > 0, "no pids ids supplied");
        require(_pids.length ==  _tokenIds.length, "pids length and token length is not matched");

        uint24 tokenId;
        uint256 pid;
        totalStaked -= _tokenIds.length;
        uint256 earned = 0;
        for (uint i = 0; i < _tokenIds.length; i++) {
            tokenId = _tokenIds[i];
            pid = _pids[i];
            StakeInfo memory staked = vault[pid][tokenId];
            require(staked.owner == msg.sender, "not an owner");
            earned += getPeriodReward(pid, tokenId); 
            delete vault[pid][tokenId];
            emit NFTUnstaked(poolInfo[pid].nftAddress, msg.sender, tokenId, block.timestamp);
            IERC721(poolInfo[pid].nftAddress).safeTransferFrom(address(this), msg.sender, tokenId);
        }
        if(earned > 0) {
            reward_token.safeTransferFrom(owner(), msg.sender, earned);
            totalRewardsClaimed += earned;
            emit Claimed(msg.sender, earned);
        }
    }

    //claim rewards
    function claimRewards(uint256[] calldata _pids, uint24[] calldata _tokenIds) external nonReentrant {
        require(_tokenIds.length > 0, "no token ids supplied");
        require(_pids.length > 0, "no pids ids supplied");
        require(_pids.length ==  _tokenIds.length, "pids length and token length is not matched");

        uint24 tokenId;
        uint256 pid;
        uint256 rewards;
        for (uint i = 0; i < _tokenIds.length; i++) {
            tokenId = _tokenIds[i];
            pid = _pids[i];
            StakeInfo memory staked = vault[pid][tokenId];
            require(staked.owner == msg.sender, "not an owner");
            uint256 earned = getPeriodReward(pid, tokenId);
            if (earned > 0) {
                staked.timestamp = block.timestamp;
                vault[pid][tokenId] = staked;
                rewards += earned;
            }
        }
        if (rewards > 0) {
            reward_token.safeTransferFrom(owner(), msg.sender, rewards);
            totalRewardsClaimed += rewards;
            emit Claimed(msg.sender, rewards);
        }
        
    }

    function getRewardsPerDay(uint256 _pid) public view returns(uint256) {
        PoolInfo memory pool = poolInfo[_pid];
        uint256 cost = pool.cost;
        uint256 cost_per_day =  cost.div(365);
        uint256 tokenPrice = reward_token.getNaturaPrice();
        uint256 tokenRate = (10**18)/tokenPrice;
        uint256 rewards_per_day = tokenRate.mul(cost_per_day);
        
        return rewards_per_day;
    }

    function getPeriodReward(uint256 _pid, uint24 _tokenId) internal view returns(uint256) {
        StakeInfo memory staked = vault[_pid][_tokenId];
        uint256 rewards_per_day = getRewardsPerDay(_pid);
        uint256 diff = block.timestamp.sub(staked.timestamp);
        uint256 noOfDays = diff.div(60*60*24);
        noOfDays = noOfDays > 0 ? noOfDays : 0;
        return rewards_per_day.mul(noOfDays);
    }

    

    function totalbalanceOf(address account) public view returns (uint256[] memory) {
        uint256[] memory balance = new uint[](poolInfo.length);
        for(uint i=0; i < poolInfo.length; i++) {
            uint256 supply = TSupply(poolInfo[i].nftAddress).totalSupply();
            for(uint24 j = 1; j <= supply; j++) {
                if (vault[i][j].owner == account) {
                    balance[i] += 1;
                }
            }
        }
        return balance;
    }

    function balanceOf(uint256 _pid, address _account) public view returns (uint256) {
        uint256 balance = 0;
        uint256 supply = TSupply(poolInfo[_pid].nftAddress).totalSupply();
        for(uint24 i = 1; i <= supply; i++) {
            if (vault[_pid][i].owner == _account) {
                balance += 1;
            }
        }
        return balance;
    } 

    function getUserStakedTokens(uint256 _pid, address _account) public view returns (uint256[] memory) {
        uint256 balance = balanceOf(_pid, _account);
        uint256[] memory tokenIds = new uint[](balance);
        uint256 supply = TSupply(poolInfo[_pid].nftAddress).totalSupply();
        uint256 index = 0;
        for(uint24 i = 1; i <= supply; i++) {
            if (vault[_pid][i].owner == _account) {
                tokenIds[index] = vault[_pid][i].tokenId;
                index++;
            }
        }
        return tokenIds;
    } 

    function onERC721Received(
        address,
        address,
        uint256,
        bytes calldata
    ) external pure override returns (bytes4) {
      //require(from == address(0x0), "Cannot send nfts to Vault directly");
      return IERC721Receiver.onERC721Received.selector;
    }

    fallback() external payable {}

    receive() external payable {}

}
