pragma solidity 0.8.11;
import "hardhat/console.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/token/ERC1155/ERC1155.sol";
import "@openzeppelin/contracts/token/ERC1155/utils/ERC1155Holder.sol";
import "@openzeppelin/contracts/token/ERC721/utils/ERC721Holder.sol";

contract GameStorage is Ownable, ERC721Holder, ERC1155Holder {
    using SafeMath for uint256;

    mapping(address => bool) public whiteListAddress;
    mapping(address => mapping(bytes32 => bool)) public userOwnedNFT;
    mapping(bytes32 => NFT) public nftInfo;
    mapping(address => bool) public minter;
    mapping(address => mapping(address => uint256)) public userOwnedToken;
    mapping(address => mapping(address => uint256)) public userLockToken;

    mapping(address => mapping(uint256 => address)) public whoOwnerNFT;

    GameStorage public oldGameStorage;
    struct NFT {
        IERC721 nft;
        uint256 tokenId;
        address owner;
    }

    event UpdateWhiteList(address user, address nft, bool isWhiteList);
    event UserDepositToken(address user, address token, uint256 amount);
    event UserDepositNFT(address user, address nft, uint256[] tokenIds);
    event UserWithdrawToken(address user, address token, uint256 amount);
    event FailedWithdrawToken(
        address user,
        address token,
        uint256 amount,
        string reason
    );
    event DecreaseToken(address user, address token, uint256 amount);
    event IncreaseToken(address user, address token, uint256 amount);
    event UserWithdrawNFT(address user, address nft, uint256[] tokenIds);
    event UseNFT(address user, address nft, uint256 tokenId);
    event AddNFT(address user, address nft, uint256 tokenId);
    event LockNFT(address user, address nft, uint256 tokenId);
    event UnlockNFT(address user, address nft, uint256 tokenId);
    event LockUserToken(address user, address token, uint256 amount);
    event UnlockUserToken(address user, address token, uint256 amount);
    event UserDepositMultiToken(
        address user,
        address token,
        uint256 tokenId,
        uint256 amount
    );
    event UserWithdrawMultiToken(
        address user,
        address token,
        uint256 tokenId,
        uint256 amount
    );
    event FailedWithdrawMultiToken(
        address user,
        address token,
        uint256 tokenId,
        uint256 amount,
        string reason
    );

    modifier allowInWhiteList(address _address) {
        require(whiteListAddress[_address], "address is not allow");
        _;
    }

    modifier allowInWhiteLists(address[] memory _address) {
        for (uint256 i = 0; i < _address.length; i++) {
            require(whiteListAddress[_address[i]], "address is not allow");
        }
        _;
    }

    modifier onlyMinter() {
        require(minter[msg.sender], "only minter.");
        _;
    }

    constructor(GameStorage _oldGameStorage) {
        oldGameStorage = _oldGameStorage;
    }

    function setMinter(address _minter, bool _isMinter) external onlyOwner {
        minter[_minter] = _isMinter;
    }

    function setWhiteListAddress(address _address, bool _isWhiteList)
        external
        onlyOwner
    {
        whiteListAddress[_address] = _isWhiteList;

        emit UpdateWhiteList(msg.sender, _address, _isWhiteList);
    }

    function getKey(address _address, uint256 _tokenId)
        external
        pure
        returns (bytes32)
    {
        return keccak256(abi.encodePacked(_address, _tokenId));
    }

    function depositToken(address _erc20, uint256 _amount)
        external
        allowInWhiteList(_erc20)
    {
        require(
            ERC20(_erc20).allowance(msg.sender, address(this)) >= _amount,
            "Allowance is'n enough."
        );
        ERC20(_erc20).transferFrom(msg.sender, address(this), _amount);

        emit UserDepositToken(msg.sender, _erc20, _amount);
    }

    function withdrawToken(
        address _receiver,
        address _erc20,
        uint256 _amount
    ) external allowInWhiteList(_erc20) onlyMinter {
        require(ERC20(_erc20).balanceOf(address(this)) >= _amount);
        ERC20(_erc20).transfer(_receiver, _amount);

        emit UserWithdrawToken(_receiver, _erc20, _amount);
    }

    function withdrawTokens(
        address[] memory _receivers,
        address[] memory _erc20,
        uint256[] memory _amounts
    ) external onlyMinter {
        for (uint256 i = 0; i < _receivers.length; i++) {
            try ERC20(_erc20[i]).transfer(_receivers[i], _amounts[i]) {
                emit UserWithdrawToken(_receivers[i], _erc20[i], _amounts[i]);
            } catch Error(string memory _err) {
                emit FailedWithdrawToken(
                    _receivers[i],
                    _erc20[i],
                    _amounts[i],
                    _err
                );
                revert(_err);
            }
        }
    }

    function depositMultiTokens(
        address[] memory _erc1155s,
        uint256[] memory _tokenIds,
        uint256[] memory _amounts
    ) external allowInWhiteLists(_erc1155s) {
        for (uint256 i = 0; i < _erc1155s.length; i++) {
            ERC1155(_erc1155s[i]).safeTransferFrom(
                msg.sender,
                address(this),
                _tokenIds[i],
                _amounts[i],
                ""
            );

            emit UserDepositMultiToken(
                msg.sender,
                _erc1155s[i],
                _tokenIds[i],
                _amounts[i]
            );
        }
    }

    function withdrawMultiToken(
        address _receiver,
        address _erc1155,
        uint256 _tokenId,
        uint256 _amount
    ) public allowInWhiteList(_erc1155) onlyMinter {
        require(
            ERC1155(_erc1155).balanceOf(address(this), _tokenId) >= _amount
        );
        ERC1155(_erc1155).safeTransferFrom(
            address(this),
            _receiver,
            _tokenId,
            _amount,
            ""
        );

        emit UserWithdrawMultiToken(_receiver, _erc1155, _tokenId, _amount);
    }

    function withdrawMultiTokens(
        address[] memory _receivers,
        address[] memory _erc1155s,
        uint256[] memory _tokenIds,
        uint256[] memory _amounts
    ) external onlyMinter {
        for (uint256 index = 0; index < _receivers.length; index++) {
            try
                ERC1155(_erc1155s[index]).safeTransferFrom(
                    address(this),
                    _receivers[index],
                    _tokenIds[index],
                    _amounts[index],
                    ""
                )
            {
                emit UserWithdrawMultiToken(
                    _receivers[index],
                    _erc1155s[index],
                    _tokenIds[index],
                    _amounts[index]
                );
            } catch Error(string memory _err) {
                emit FailedWithdrawMultiToken(
                    _receivers[index],
                    _erc1155s[index],
                    _tokenIds[index],
                    _amounts[index],
                    _err
                );

                revert(_err);
            }
        }
    }

    function depositNFT(address _address, uint256[] memory _tokenIds)
        external
        allowInWhiteList(_address)
    {
        for (uint256 index = 0; index < _tokenIds.length; index++) {
            IERC721(_address).transferFrom(
                msg.sender,
                address(this),
                _tokenIds[index]
            );

            whoOwnerNFT[_address][_tokenIds[index]] = msg.sender;
        }
        emit UserDepositNFT(msg.sender, _address, _tokenIds);
    }

    function withdrawNFT(
        address _receiver,
        address _nft,
        uint256[] memory _tokenIds
    ) public onlyMinter {
        for (uint256 index = 0; index < _tokenIds.length; index++) {
            if (
                IERC721(_nft).ownerOf(_tokenIds[index]) ==
                address(oldGameStorage)
            ) {
                try
                    oldGameStorage.adminWithDrawNFT(
                        _nft,
                        _tokenIds[index],
                        _receiver
                    )
                {} catch Error(string memory _err) {
                    revert(_err);
                }
            } else {
                try
                    IERC721(_nft).transferFrom(
                        address(this),
                        _receiver,
                        _tokenIds[index]
                    )
                {} catch Error(string memory _err) {
                    revert(_err);
                }
            }
            emit UserWithdrawNFT(_receiver, _nft, _tokenIds);
        }
    }

    function withdrawNFTs(
        address[] memory _receivers,
        address[] memory _nfts,
        uint256[][] memory _tokenIds
    ) external onlyMinter {
        for (uint256 i = 0; i < _receivers.length; i++) {
            for (uint256 j = 0; j < _tokenIds[i].length; j++) {
                if (
                    IERC721(_nfts[i]).ownerOf(_tokenIds[i][j]) ==
                    address(oldGameStorage)
                ) {
                    try
                        oldGameStorage.adminWithDrawNFT(
                            _nfts[i],
                            _tokenIds[i][j],
                            _receivers[i]
                        )
                    {} catch Error(string memory _err) {
                        revert(_err);
                    }
                } else {
                    try
                        IERC721(_nfts[i]).transferFrom(
                            address(this),
                            _receivers[i],
                            _tokenIds[i][j]
                        )
                    {} catch Error(string memory _err) {
                        revert(_err);
                    }
                }

                emit UserWithdrawNFT(_receivers[i], _nfts[i], _tokenIds[i]);
            }
        }
    }

    // Emergency Function
    function adminWithDrawNFT1155(
        address _address,
        uint256 _tokenId,
        address _receiver,
        uint256 _amount
    ) external onlyMinter {
        ERC1155(_address).safeTransferFrom(
            address(this),
            _receiver,
            _tokenId,
            _amount,
            ""
        );
    }

    function adminWithDrawNFT(
        address _address,
        uint256 _tokenId,
        address _receiver
    ) external onlyMinter {
        IERC721(_address).transferFrom(address(this), _receiver, _tokenId);
    }

    function adminWithdrawToken(
        address _address,
        uint256 _amount,
        address _receiver
    ) external onlyMinter {
        ERC20(_address).transfer(_receiver, _amount);
    }
}
