      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.12;

import "./Guestbook__v1_2.sol";

contract Guestbook__v1_3 is Guestbook__v1_2 {
  using ECDSA for bytes32;

  /// @custom:oz-upgrades-unsafe-allow constructor
  constructor() {}

  // Overidden to guard against which users can access
  function _authorizeUpgrade(address newImplementation) internal virtual override onlyOwner {}

  // Guestbook__v1_2 => Guestbook__v1_3 upgrade initializer
  function upgradeTo__v1_3() public onlyOwner upgradeVersion(3) {}

  // Deploying Guestbook__v1_3 initializer
  function initialize__v1_3(string memory _name, string memory _symbol) public initializer {
    Guestbook__v1_2.initialize__v1_2(_name, _symbol);
    upgradeTo__v1_3();
  }

  function getMintMulipleBatchesSigningHash(
    uint256 blockExpiry,
    address[] memory receivers,
    uint256[][] memory ids,
    uint256[][] memory amounts
  ) public view virtual returns (bytes32) {
    bytes memory idsBytes;
    bytes memory amountsBytes;

    for (uint256 i = 0; i < receivers.length; i++) {
      for (uint256 j = 0; j < ids[i].length; j++) {
        idsBytes = abi.encodePacked(idsBytes, ids[i][j]);
        amountsBytes = abi.encodePacked(amountsBytes, amounts[i][j]);
      }
    }

    return keccak256(abi.encodePacked(blockExpiry, receivers, idsBytes, amountsBytes, address(this)));
  }

  function mintMultipleBatchesWithSig(
    address[] memory receivers,
    uint256[][] memory ids,
    uint256[][] memory amounts,
    bytes memory data,
    bytes memory sig,
    uint256 blockExpiry
  ) external virtual {
    require(receivers.length == ids.length, "Mismatch of receivers and ids length");
    require(receivers.length == amounts.length, "Mismatch of accounts and amounts length");

    bytes32 message = getMintMulipleBatchesSigningHash(blockExpiry, receivers, ids, amounts).toEthSignedMessageHash();
    require(ECDSA.recover(message, sig) == signVerifier, "Permission to call this function failed");
    require(block.number < blockExpiry, "Sig expired");

    for (uint256 i = 0; i < receivers.length; ++i) {
      require(ids[i].length == amounts[i].length, "Mismatch of token ids and amounts");
      _mintBatch(receivers[i], ids[i], amounts[i], data);
    }
  }
}
