pragma solidity >=0.8.0 <0.9.0;

import "erc721a/contracts/ERC721A.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "./common/meta-transactions/ContentMixin.sol";
import "./common/meta-transactions/NativeMetaTransaction.sol";

/// @title KGEventV1 NFT
/// @author kuannnn
/// @notice A NFT contract for KGEvent

contract KGEventV1 is ERC721A, Ownable, AccessControl, ContextMixin, NativeMetaTransaction {
    string public baseURI;
    string private _contractURI;

    uint256 public EVENT_START_TIMESTAMP;
    uint256 public EVENT_END_TIMESTAMP;

    uint256 public immutable MAX_SUPPLY;

    /// @notice A role for mint operators
    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");
    address private _openseaProxyAddress;

    constructor(
        string memory _name,
        string memory _symbol,
        uint256 _maxSupply,
        string memory __baseURI,
        string memory __contractURI
    ) ERC721A(_name, _symbol) {
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        MAX_SUPPLY = _maxSupply;
        baseURI = __baseURI;
        _contractURI = __contractURI;
        _openseaProxyAddress = address(0x58807baD0B376efc12F5AD86aAc70E78ed67deaE);
        _initializeEIP712(_name);
    }

    // Modifiers
    // ------------------------------------------------------------------------
    modifier validEvent() {
        require(block.timestamp >= EVENT_START_TIMESTAMP, "Event not started yet");
        require(EVENT_END_TIMESTAMP == 0 || block.timestamp <= EVENT_END_TIMESTAMP, "Event ended");
        _;
    }

    modifier limitedEdition(uint256 _amount) {
        require(_totalMinted() + _amount <= MAX_SUPPLY, "Max supply reached");
        _;
    }

    // inherritence functions
    // ------------------------------------------------------------------------

    /// @dev Start from tokenId 1 to reduce gas usage of the first mint
    /// @inheritdoc ERC721A
    function _startTokenId() internal view virtual override returns (uint256) {
        return 1;
    }

    /// @inheritdoc ERC721A
    function supportsInterface(bytes4 interfaceId) public view override(ERC721A, AccessControl) returns (bool) {
        return ERC721A.supportsInterface(interfaceId) || AccessControl.supportsInterface(interfaceId);
    }

    /// @dev Returns `true` if `account` has been granted `role` or adminRole of `role`.
    function hasRole(bytes32 role, address account) public view virtual override returns (bool) {
        bytes32 adminRole = getRoleAdmin(role);
        return super.hasRole(role, account) || super.hasRole(adminRole, account);
    }

    // Minting info functions
    // ------------------------------------------------------------------------

    /// @notice Get the total amount of minted tokens, including the ones that are burned
    function totalMinted() public view returns (uint256) {
        return _totalMinted();
    }

    // Airdrop functions
    // ------------------------------------------------------------------------

    /// @notice Airdrop tokens to a single address
    /// @param _to The address to airdrop tokens to
    /// @param _amount The amount of tokens to airdrop
    function airdropSingle(address _to, uint256 _amount)
        external
        onlyRole(MINTER_ROLE)
        validEvent
        limitedEdition(_amount)
    {
        _mint(_to, _amount);
    }

    /// @notice Airdrop tokens to multiple addresses at once
    /// @param _to The array of addresses to airdrop tokens to
    /// @param _amountPerAddress The amount of tokens to airdrop per address
    function airdropMultiple(address[] calldata _to, uint256 _amountPerAddress)
        external
        onlyRole(MINTER_ROLE)
        validEvent
        limitedEdition(_amountPerAddress * _to.length)
    {
        unchecked {
            for (uint256 i = 0; i < _to.length; ++i) {
                _mint(_to[i], _amountPerAddress);
            }
        }
    }

    // URI functions
    // ------------------------------------------------------------------------

    function _baseURI() internal view virtual override returns (string memory) {
        return baseURI;
    }

    /// @notice Set the base URI for all minted tokens
    /// @param _base The base URI for all minted tokens
    function setBaseURI(string calldata _base) external onlyRole(DEFAULT_ADMIN_ROLE) {
        baseURI = _base;
    }

    /// @dev This is the function for opensea to get the contract URI
    /// @return The contract URI for the collection
    function contractURI() public view returns (string memory) {
        return _contractURI;
    }

    /// @notice Set the URI for collection's metadata
    /// @param _contract_uri The URI for contract level metadata
    function setContractURI(string calldata _contract_uri) external onlyRole(DEFAULT_ADMIN_ROLE) {
        _contractURI = _contract_uri;
    }

    // Timestamp functions
    // ------------------------------------------------------------------------

    /// @notice Set the timestamps for the event
    /// @param _start The start timestamp for the event
    /// @param _end The end timestamp for the event, if set to 0 and also set `_period` to 0, the event will be open ended
    /// @param _period The period of the event. It won't work if `_end` is none 0 value.

    function setEventTimeStamp(
        uint256 _start,
        uint256 _end,
        uint256 _period
    ) external onlyRole(DEFAULT_ADMIN_ROLE) {
        EVENT_START_TIMESTAMP = _start;

        if (_end == 0 && _period > 0) {
            EVENT_END_TIMESTAMP = EVENT_START_TIMESTAMP + _period * 1 days;
        }

        if (_end > 0) {
            require(_end > _start, "Must provide valid end timestamp");
            EVENT_END_TIMESTAMP = _end;
        }
    }

    /**
     * This is used instead of msg.sender as transactions won't be sent by the original token owner, but by OpenSea.
     */
    function isApprovedForAll(address owner, address operator) public view override returns (bool) {
        if (address(_openseaProxyAddress) == operator) {
            return true;
        }

        return super.isApprovedForAll(owner, operator);
    }

    /**
     * This is used instead of msg.sender as transactions won't be sent by the original token owner, but by OpenSea.
     */
    function _msgSender() internal view override returns (address sender) {
        return ContextMixin.msgSender();
    }
}
"
