    "contracts/CanvasDeployer.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import "./MainCanvas.sol";
import "./CreatorCanvas.sol";

/**
 * @author Andrea Tedesco (@andreatedesco).
 * @dev This contract is responsible for deploying and managing CreatorCanvas contracts.
 * It creates and associates CreatorCanvas contracts with tiles on the MainCanvas.
 * The MainCanvas contract is created during deployment and serves as the main canvas where CreatorCanvas contracts are deployed.
 */
contract CanvasDeployer {
    using TOCUtilities for bytes;
    using TOCUtilities for address;

    // ============================================= //
    // Fields ====================================== //

    // MainCanvas created on contract deployment
    MainCanvas public immutable mainCanvas;

    // Heights supported by CreatorCanvas
    uint256[7] public supportedHeights = [4, 6, 8, 10, 12, 14, 16];

    // Addresses of deployed Creator Canvases
    mapping(uint256 => address) public canvasAddress;

    // ============================================= //
    // Events ====================================== //

    // Event triggered when a canvas is created.
    event CanvasCreated(uint256 canvasID);

    // ============================================= //
    // Functions =================================== //

    constructor(
        uint256 heightID,
        uint256 defaultMintPrice,
        uint256 updatePrice
    ) {
        require(heightID < supportedHeights.length);
        uint256 height = supportedHeights[heightID];
        uint256 width = height * 2;

        mainCanvas = new MainCanvas(
            msg.sender,
            height,
            width,
            defaultMintPrice,
            updatePrice,
            "TOC | Main Canvas",
            "Tiles On Chain was born from the idea of merging the famous 1 million pixel site with the key concepts of Web3: Decentralization, Transparency, Ownership, Verifiability, Trustlessness.\\n\\nTo do this, we developed a protocol that allows NFT metadata to be stored directly on the blockchain.\\n\\nGet your own showcase om the web3.",
            abi.encodePacked(
                "iVBORw0KGgoAAAANSUhEUgAAABgAAAAYBAMAAAASWSDLAAAAD1BMVEUAAAAxA0d5QNmCR+X///82dLBZAAAAAXRSTlMAQObYZgAAAHlJREFUGNOVkNEJwCAMRHWDBp1AXECSBWzcf6aeMa34WRG5x0W4Swg4kegKfqA/Mv0SRO543Mhj9GVBq6h0sygxizI3mlO8gDEXCcougEiqmodPgFJ1zjkUFf4DccN1QqB8T0C4FfRW8aDRYnuFWS41L3fWPhayV/UAq68ec+wfNQ0AAAAASUVORK5CYII="
            ).toSVG(24, 24),
            abi.encodePacked(
                ""
            ).toSVG(30, 20),
            "https://tilesonchain.com/"
        );
    }

    // === Public Functions ============== //

    /**
     * @dev Creates a new CreatorCanvas with the specified `canvasID`, wich is associated with the tile having the ID `canvasID` on the `mainCanvas`.
     * @param canvasID The unique identifier for the CreatorCanvas.
     * @param heightID The index of the desired height from the supportedHeights array.
     * @param defaultMintPrice The default price in wei to mint a tile on the CreatorCanvas.
     * @param updatePrice The price in wei to update the content of a tile on the CreatorCanvas.

     * Requirements:
     * -  The canvas `canvasID` must not have already been created.
     * -  The caller must be the owner of tile `tokenID` of `mainCanvas`.
     * -  `heightID` must be less than the lenght of `supportedHeights`.
     *
     * Emits a {CanvasCreated} event.
     */
    function createCanvas(
        uint256 canvasID,
        uint256 heightID,
        uint256 defaultMintPrice,
        uint256 updatePrice
    ) external {
        require(canvasAddress[canvasID] == address(0));
        require(mainCanvas.ownerOf(canvasID) == msg.sender);
        require(heightID < supportedHeights.length);

        uint256 height = supportedHeights[heightID];
        uint256 width = height * 2;

        (
            bytes memory mainTileName,
            bytes memory mainTileImage,
            bytes memory mainTileUrl
        ) = mainCanvas.getTile(canvasID);

        CreatorCanvas creatorCanvas = new CreatorCanvas(
            address(mainCanvas),
            mainCanvas.owner(),
            msg.sender,
            canvasID,
            height,
            width,
            defaultMintPrice,
            updatePrice,
            abi.encodePacked("TOC #", Strings.toString(canvasID), " | " , mainTileName),
            abi.encodePacked("Creator: ", msg.sender.toString(), "."),
            mainTileImage.toSVG(24, 24),
            abi.encodePacked(
                ""
            ).toSVG(30, 20),
            abi.encodePacked(mainTileUrl)
        );
        canvasAddress[canvasID] = address(creatorCanvas);

        mainCanvas.onCanvasCreation(canvasID, canvasAddress[canvasID]);

        emit CanvasCreated(canvasID);
    }
}
"
    },
