import "@openzeppelin/contracts/token/ERC721/extensions/ERC721Royalty.sol";
import "@openzeppelin/contracts/interfaces/IERC4906.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "./ERC721OnChainMetadata.sol";
import "./TOCUtilities.sol";

/**
 * @author Andrea Tedesco (@andreatedesco).
 * @dev This smart contract provides functionalities for managing an on-chain canvas. 
 * It allows users to mint and update tiles on the canvas, each represented by a unique token. 
 * The contract is managed by an administrator who can manage whitelist addresses, update and mint prices and withdraw the contract's balance.
 * The canvas dimensions are set during contract deployment. 
 */
abstract contract CanvasOnChain is
    IERC4906,
    ERC721Royalty,
    ERC721OnChainMetadata,
    Ownable
{
    using TOCUtilities for bytes;

    // ============================================= //
    // Fields ====================================== //

    // Width of the canvas
    uint256 public immutable width;

    // Height of the canvas
    uint256 public immutable height;

    // Maximum number of tiles that can be generated
    uint256 public immutable maxSupply;

    // Default price in wei to mint a new tile
    uint256 public immutable defaultMintPrice;

    // Currently number of tiles minted so far
    uint256 public totalSupply;

    //  Price in wei to mint a new tile
    uint256[] public mintPrices;

    // Price in wei to update an existing tile
    uint256 public updatePrice;

    // Whitelisted addresses
    address[] public whitelist;

    // Null Address used for activate the sales.
    address private constant _nullAddress =
        address(0xFFfFfFffFFfffFFfFFfFFFFFffFFFffffFfFFFfF);

    // Mapping to store the base64 image data for each token ID.
    mapping(uint256 => bytes) internal _base64Image;

    // ============================================= //
    // Events ====================================== //

    // Event triggered when prices are updated.
    event PricesUpdate();

    // Event triggered when prices are updated.
    event WhitelistUpdate();

    // Event triggered when contract metadata is updated.
    event ContractMetadataUpdate();

    // ============================================= //
    // Errors ====================================== //

    error supplyError();
    error ownerError();
    error priceError();
    error stateError();

    // ============================================= //
    // Functions =================================== //

    constructor(
        uint256 height_,
        uint256 width_,
        uint256 defaultMintPrice_,
        uint256 updatePrice_,
        bytes memory name_,
        bytes memory desciption,
        bytes memory logo,
        bytes memory banner,
        bytes memory link
    ) {
        height = height_;
        width = width_;
        maxSupply = width_ * height_;

        defaultMintPrice = defaultMintPrice_;
        updatePrice = updatePrice_;

        mintPrices = new uint256[](maxSupply);
        whitelist = new address[](maxSupply);

        _setValue(OnChainMetadataStorage.key_contract_name, name_);
        _setValue(OnChainMetadataStorage.key_contract_description, desciption);
        _setValue(OnChainMetadataStorage.key_contract_logo, logo);
        _setValue(OnChainMetadataStorage.key_contract_banner, banner);
        _setValue(OnChainMetadataStorage.key_contract_external_link, link);
    }

    // === Public Functions ============== //

    /**
     * @dev Mints `tokenID` and transfers it to the caller.
     *
     * Requirements:
     * - Sales must be active.
     * - If `tokenID` is in the whitelist, the caller must be the listed address and the value sent must be greater than or equal to `mintPrice`.
     * - If `tokenID` is not in the whitelist, the value sent must be greater than or equal to `mintPrice`.
     * - `tokenID` must respect the supply check.
     *
     * See {_setTile}.
     * See {ERC721-_safeMint}.
     */
    function mint(
        uint256 tokenID,
        string calldata name_,
        string calldata url,
        string calldata image
    ) public payable virtual {
        if (whitelist[tokenID] == address(0)) revert stateError();

        if (whitelist[tokenID] != _nullAddress) {
            if (whitelist[tokenID] != msg.sender) revert ownerError();
            if (msg.value < _mintPrice(tokenID)) revert priceError();
        } else if (msg.value < _mintPrice(tokenID)) revert priceError();

        _supplyCheck(tokenID);

        _setTile(tokenID, name_, url, image);

        _safeMint(msg.sender, tokenID);
    }

    /**
     * @dev Updates the `url` of the tile with the specified `tokenID`.
     *
     * Requirements:
     * - The caller must be the owner of tile `tokenID`.
     * - The value sent must be greater than or equal to `updatePrice`.
     *
     * Emits a {MetadataUpdate} event.
     */
    function updateUrl(uint256 tokenID, string calldata url) external payable {
        if (ownerOf(tokenID) != msg.sender) revert ownerError();
        if (msg.value < updatePrice) revert priceError();

        _updateUrl(tokenID, url);

        emit MetadataUpdate(tokenID);
    }

    /**
     * @dev Returns the metadata (name, image, url) associated with the specified `tokenID`.
     */
    function getTile(
        uint256 tokenID
    ) public view returns (bytes memory, bytes memory, bytes memory) {
        return (
            _getValue(tokenID, OnChainMetadataStorage.key_token_name),
            _base64Image[tokenID],
            _getValue(tokenID, OnChainMetadataStorage.key_token_external_url)
        );
    }

    /**
     * @dev Returns all the addresses in whitelist.
     */
    function getWhitelist() external view returns (address[] memory) {
        return whitelist;
    }

    /**
     * @dev Returns all the mint prices.
     */
    function getMintPrices() external view returns (uint256[] memory) {
        return mintPrices;
    }

    /**
     * @dev Retrieves the contract URL..
     */
    function getContractUrl() public view returns (bytes memory) {
        return _getValue(OnChainMetadataStorage.key_contract_external_link);
    }

    /**
     * @dev Returns the address of the owner for the specified `tokenID` without safety checks.
     */
    function ownerOfUnsafe(uint256 tokenID) external view returns (address) {
        return super._ownerOf(tokenID);
    }

    /**
     * @dev See {IERC165-supportsInterface}.
     */
    function supportsInterface(
        bytes4 interfaceId
    ) public view virtual override(IERC165, ERC721, ERC721Royalty) returns (bool) {
        return
            interfaceId == bytes4(0x49064906) ||
            super.supportsInterface(interfaceId);
    }

    // === Owner Functions =============== //

    /**
     * @dev Allows the contract owner to withdraw the contract's balance.
     */
    function withdraw() external payable virtual onlyOwner {
        (bool sent, ) = msg.sender.call{value: address(this).balance}("");
        require(sent);
    }

    /**
     * @dev Reset Whitelist.
     *
     * Requirements:
     * - the caller must be the admin of the contract.
     */
    function resetWhitelist() external virtual {
        _checkAdmin(msg.sender);
        whitelist = new address[](maxSupply);
        emit WhitelistUpdate();
    }

    /**
     * @dev Update the current `updatePrice`.
     *
     * Requirements:
     * - the caller must be the admin of the contract.
     */
    function setUpdatePrice(uint256 price) external {
        _checkAdmin(msg.sender);
        updatePrice = price;
    }

    /**
     * @dev Updates the whitelist of addresses that can mint tokens.
     *
     * Requirements:
     * - the caller must be the admin of the contract.
     * -  `ids` and `addresses` must have the same legth.
     */
    function updateWhitelist(
        uint256[] calldata ids,
        address[] calldata addresses
    ) public {
        _checkAdmin(msg.sender);
        require(ids.length == addresses.length);
        for (uint256 i = 0; i < addresses.length; ++i)
            whitelist[ids[i]] = addresses[i];
        emit WhitelistUpdate();
    }

    /**
     * @dev Updates the mint prices.
     *
     * Requirements:
     * - the caller must be the admin of the contract.
     * - `ids` and `prices` must have the same legth.
     */
    function updateMintPrices(
        uint256[] calldata ids,
        uint256[] calldata prices
    ) public {
        _checkAdmin(msg.sender);
        require(ids.length == prices.length);
        for (uint256 i = 0; i < ids.length; ++i) mintPrices[ids[i]] = prices[i];
        emit PricesUpdate();
    }

    /**
     * @dev Updates the mint prices and the whitelist of addresses that can mint tokens.
     *
     * Requirements:
     * - the caller must be the admin of the contract.
     * - `ids`,`prices` and `addresses` must have the same legth.
     */
    function updateWhitelistAndMintPrices(
        uint256[] calldata ids,
        uint256[] calldata prices,
        address[] calldata addresses
    ) external {
        _checkAdmin(msg.sender);
        require(ids.length == prices.length && ids.length == addresses.length);
        for (uint256 i = 0; i < ids.length; ++i) {
            uint id = ids[i];
            mintPrices[id] = prices[i];
            whitelist[id] = addresses[i];
        }
        emit PricesUpdate();
        emit WhitelistUpdate();
    }

    /**
     * @dev Updates the mint prices and the whitelist of addresses that can mint tokens.
     *
     * Requirements:
     * See {updateMintPrices}.
     * See {updateWhitelist}.
     */
    function updateMintPricesAndWhitelist(
        uint256[] calldata priceIds,
        uint256[] calldata prices,
        uint256[] calldata addressIds,
        address[] calldata addresses
    ) external {
        updateMintPrices(priceIds, prices);
        updateWhitelist(addressIds, addresses);
    }

    /**
     * @dev Updates the contract URL.
     *
     * Requirements:
     * - The caller must be authorized.
     *
     * Emits a {ContractMetadataUpdate} event.
     */
    function updateContractUrl(string memory url) external {
        _updateContractUrl(url);
        emit ContractMetadataUpdate();
    }

    function tokenURI(
        uint256 tokenId
    ) public view virtual override(ERC721, ERC721OnChainMetadata) returns (string memory) {
        require(_exists(tokenId), "tokenId doesn't exist");
        return _createTokenURI(tokenId);
    }

    // === Internal & Private Functions ============ //

    /**
     * @dev Returns the Base64-encoded svg card associated with the specified `tokenID`.
     */
    function _tokenCard(
        uint256 tokenID,
        bytes memory image
    ) internal view virtual returns (bytes memory) {
        return image.toSVG(24, 24);
    }

    /**
     * @dev Sets the `name_`, `url` and `image` parameters of the corresponding `tokenID`.
     *
     * Emits a {MetadataUpdate} event.
     */
    function _setTile(
        uint256 tokenID,
        string calldata name_,
        string calldata url,
        string calldata image
    ) internal virtual {
        _base64Image[tokenID] = abi.encodePacked(image);

        _setValue(
            tokenID,
            OnChainMetadataStorage.key_token_name,
            abi.encodePacked(name_)
        );
        _setValue(
            tokenID,
            OnChainMetadataStorage.key_token_external_url,
            abi.encodePacked(url)
        );
        _setValue(
            tokenID,
            OnChainMetadataStorage.key_token_description,
            abi.encodePacked("Visit my site: ", url, ".")
        );
        _setValue(
            tokenID,
            OnChainMetadataStorage.key_token_image,
            _tokenCard(tokenID, _base64Image[tokenID])
        );

        emit MetadataUpdate(tokenID);
    }

    /**
     * @dev Updates the URL for the specified `tokenID`.
     */
    function _updateUrl(uint256 tokenID, string calldata url) internal virtual {
        _setValue(
            tokenID,
            OnChainMetadataStorage.key_token_external_url,
            abi.encodePacked(url)
        );
        _setValue(
            tokenID,
            OnChainMetadataStorage.key_token_description,
            abi.encodePacked("Visit my site: ", url, ".")
        );
        _setValue(
            tokenID,
            OnChainMetadataStorage.key_token_image,
            _tokenCard(tokenID, _base64Image[tokenID])
        );
    }

    /**
     * @dev Returns the mint price for the specified `tokenID` stored in `mintPrices`.
     *
     * - If the stored price is 0, the return value is `defaultMintPrice`.
     * - If the stored price is the maximum value of uint256 value, the return value is 0.
     */
    function _mintPrice(uint256 tokenID) private view returns (uint256) {
        uint256 price = mintPrices[tokenID];
        return
            price == 0 ? defaultMintPrice : price == 2 ** 256 - 1 ? 0 : price;
    }

    /**
     * @dev Checks if address `account` is the contract owner.
     */
    function _checkAdmin(address account) internal view virtual {
        if (account != owner()) revert ownerError();
    }

    /**
     * @dev Internal function to update the contract URL.
     *
     * Requirements:
     * - The caller must be an admin.
     */
    function _updateContractUrl(string memory url) internal virtual {
        _checkAdmin(msg.sender);
        _setValue(
            OnChainMetadataStorage.key_contract_external_link,
            abi.encodePacked(url)
        );
    }

    /**
     * @dev Checks if `tokenID` is less than `maxSupply` and is not one of the 4 central tiles.
     */
    function _supplyCheck(uint256 tokenID) internal view virtual {
        uint256 a = maxSupply / 2;
        uint256 b = width / 2;
        if (
            tokenID >= maxSupply ||
            tokenID == a + b ||
            tokenID == a + b - 1 ||
            tokenID == a - b ||
            tokenID == a - b - 1
        ) revert supplyError();
    }

    /**
     * @dev See {ERC721-_afterTokenTransfer}.
     */
    function _afterTokenTransfer(
        address from,
        address to,
        uint256 tokenID,
        uint256 batchSize
    ) internal virtual override {
        super._afterTokenTransfer(from, to, tokenID, batchSize);
        if (from == address(0)) ++totalSupply;
    }

    /**
     * @dev See {ERC721Royalty-_burn}. This override additionally clears the royalty information for the token.
     */
    function _burn(uint256 tokenId) internal virtual override(ERC721, ERC721Royalty) {
        super._burn(tokenId);
        _resetTokenRoyalty(tokenId);
    }
}
"
    },
    "contracts/CreatorCanvas.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import "./CanvasOnChain.sol";
import "./MainCanvas.sol";

/**
 * @author Andrea Tedesco (@andreatedesco).
 * @dev This contract represents a Creator Canvas that extends the CanvasOnChain contract.
 * The contract is associated with a MainCanvas contract and specifies the canvas ID and the creator's address (the deployer of the contract).
 * The administrator is the owner of the tile with canvasID on the MainCanvas.
 * The contract implements royalty payments to the owner of the MainCanvas contract upon minting and updating of tiles.
 */
contract CreatorCanvas is CanvasOnChain {
    using TOCUtilities for address;

    // ============================================= //
    // Fields ====================================== //

    // MainCanvas Contract
    MainCanvas private immutable mainCanvas;

    // ID of the tile associated in mainCanvas
    uint256 public immutable canvasID;

    // creator address of the canvas
    address public immutable creator;

    // ============================================= //
    // Functions =================================== //

    constructor(
        address mainCanvas_,
        address owner_,
        address creator_,
        uint256 canvasID_,
        uint256 height_,
        uint256 width_,
        uint256 mintPrice_,
        uint256 updatePrice_,
        bytes memory name_,
        bytes memory desciption,
        bytes memory logo,
        bytes memory banner,
        bytes memory link
    )
        CanvasOnChain(
            height_,
            width_,
            mintPrice_,
            updatePrice_,
            name_,
            desciption,
            logo,
            banner,
            link
        )
        ERC721(string(name_), "TOC-CC")
    {
        mainCanvas = MainCanvas(mainCanvas_);
        canvasID = canvasID_;
        creator = creator_;
        _transferOwnership(owner_);
        _setDefaultRoyalty(creator, 200);
    }

    // === Public Functions ============== //

    /**
     * @dev Updates the metadata of the specified `tokenID` with the provided `name_`, `url`, and `image`.
     *
     * Requirements:
     *
     * - The caller must be the owner of tile `tokenID`.
     * - The value sent must be equal to or greater than `updatePrice`.
     *
     * See {_setTile}.
     */
    function update(
        uint256 tokenID,
        string calldata name_,
        string calldata url,
        string calldata image
    ) external payable {
        if (ownerOf(tokenID) != msg.sender) revert ownerError();
        if (msg.value < updatePrice) revert priceError();
        _setTile(tokenID, name_, url, image);
        _payFee(msg.value);
    }

    /**
     * See {CanvasOnChain-mint}.
     */
    function mint(
        uint256 tokenID,
        string calldata name_,
        string calldata url,
        string calldata image
    ) public payable override {
        super.mint(tokenID, name_, url, image);
        _payFee(msg.value);
    }

    // === Owner Functions =============== //

    /**
     * @dev Allows the contract admin to withdraw the contract's balance.
     *
     * @dev Checks if address `account` is the owner of the tile having the ID `canvasID` on the `mainCanvas`.
     */
    function withdraw() external payable override {
        address canvasOwnwer = mainCanvas.ownerOf(canvasID);
        if (msg.sender != address(mainCanvas) && msg.sender != canvasOwnwer)
            revert ownerError();
        (bool sent, ) = canvasOwnwer.call{value: address(this).balance}("");
        require(sent);
    }

    /**
     * @dev Reset Whitelist.
     *
     * Requirements:
     *
     * - the caller must be the `mainCanvas` contract.
     */
    function resetWhitelist() external override {
        if (msg.sender != address(mainCanvas)) revert ownerError();
        whitelist = new address[](maxSupply);
        emit WhitelistUpdate();
    }

    // === Internal Functions ============= //

    /**
     * @dev Checks if address `account` is the owner of the tile having the ID `canvasID` on the `mainCanvas`.
     */
    function _checkAdmin(address account) internal view override {
        if (mainCanvas.ownerOf(canvasID) != account) revert ownerError();
    }

    /**
     * @dev See {CanvasOnChain-_updateContractUrl}.
     * Only the MainCanvas contract can call this function.
     *
     * Requirements:
     * - The caller must be the MainCanvas contract.
     */
    function _updateContractUrl(string memory url) internal override {
        if (msg.sender != address(mainCanvas)) revert ownerError();
        _setValue(
            OnChainMetadataStorage.key_contract_external_link,
            abi.encodePacked(url)
        );
    }

    /**
     * @dev Private function to pay the fee.
     * It sends 4% of the value to the owner of the mainCanvas contract.
     */
    function _payFee(uint256 value) private {
        (bool sent, ) = mainCanvas.owner().call{value: (value / 10000) * 400}(
            ""
        );
        require(sent);
    }

    /**
     * See {CanvasOnChain-_tokenCard}.
     */
    function _tokenCard(
        uint256 tokenID,
        bytes memory image
    ) internal view override returns (bytes memory) {
        (bytes memory name_, , bytes memory url) = getTile(tokenID);
        (, bytes memory canvasImage, ) = mainCanvas.getTile(canvasID);
        return
            TOCUtilities.creatorCanvasCard(
                tokenID,
                image,
                name_,
                url,
                address(this),
                mainCanvas.getContractUrl(),
                canvasID,
                canvasImage,
                mainCanvas.logoImage()
            );
    }
}
"
    },
    "contracts/ERC721OnChainMetadata.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "./OnChainMetadata.sol";

/**
 * @author Andrea Tedesco (@andreatedesco).
 * @dev The tokenURI() and contractURI() methods are responsible to call _createTokenURI() and _createContractURI() methods
 * of OnChainMetadata contract, which convert metadata into a Base64-encoded json readable by OpenSea, LooksRare and many other NFT platforms.
 */
abstract contract ERC721OnChainMetadata is ERC721, OnChainMetadata {
    function tokenURI(
        uint256 tokenId
    ) public view virtual override returns (string memory) {
        require(_exists(tokenId), "tokenId doesn't exist");
        return _createTokenURI(tokenId);
    }

    function contractURI() public view virtual returns (string memory) {
        return _createContractURI();
    }
}"
    },
    "contracts/MainCanvas.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import "./CanvasOnChain.sol";
import "./CanvasDeployer.sol";

/**
 * @author Andrea Tedesco (@andreatedesco).
 * @dev This contract represents a MainCanvas, which extends the CanvasOnChain contract.
 * It serves as the main canvas where creators can deploy their own canvas contracts.
 * The MainCanvas contract is associated with a CanvasDeployer contract, which handles the creation and management of individual canvas contracts.
 */
contract MainCanvas is CanvasOnChain {
    using TOCUtilities for address;

    // ============================================= //
    // Fields ====================================== //

    // CanvasDeployer Contract
    CanvasDeployer private immutable canvasDeployer;

    // Base64-encoded string tha rapresent the logo image of the contract
    bytes public constant logoImage =
        "iVBORw0KGgoAAAANSUhEUgAAABgAAAAYBAMAAAASWSDLAAAAD1BMVEUAAAAxA0d5QNmCR+X///82dLBZAAAAAXRSTlMAQObYZgAAAHlJREFUGNOVkNEJwCAMRHWDBp1AXECSBWzcf6aeMa34WRG5x0W4Swg4kegKfqA/Mv0SRO543Mhj9GVBq6h0sygxizI3mlO8gDEXCcougEiqmodPgFJ1zjkUFf4DccN1QqB8T0C4FfRW8aDRYnuFWS41L3fWPhayV/UAq68ec+wfNQ0AAAAASUVORK5CYII=";

    // ============================================= //
    // Functions =================================== //

    constructor(
        address owner_,
        uint256 height_,
        uint256 width_,
        uint256 defaultMintPrice_,
        uint256 updatePrice_,
        bytes memory name_,
        bytes memory desciption,
        bytes memory logo,
        bytes memory banner,
        bytes memory link
    )
        CanvasOnChain(
            height_,
            width_,
            defaultMintPrice_,
            updatePrice_,
            name_,
            desciption,
            logo,
            banner,
            link
        )
        ERC721(string(name_), "TOC-MC")
    {
        canvasDeployer = CanvasDeployer(msg.sender);
        _transferOwnership(owner_);
        _setDefaultRoyalty(owner_, 400);
    }

    // === Public Functions ============== //

    /**
     * @dev Handles the event of canvas creation.
     * It updates the token description and image for the specified `tokenID`.
     *
     * Requirements:
     * - The caller must be the CanvasDeployer contract.
     *
     * Emits a {MetadataUpdate} event.
     */
    function onCanvasCreation(uint256 tokenID, address canvasAddress) external {
        if (msg.sender != address(canvasDeployer)) revert ownerError();

        bytes memory tokenDescription = abi.encodePacked(
            _getValue(tokenID, OnChainMetadataStorage.key_token_description),
            "\\n\\n",
            "Canvas deployed on: ",
            canvasAddress.toString(),
            "."
        );

        _setValue(
            tokenID,
            OnChainMetadataStorage.key_token_description,
            tokenDescription
        );
        _setValue(
            tokenID,
            OnChainMetadataStorage.key_token_image,
            abi.encodePacked(_tokenCard(tokenID, _base64Image[tokenID]))
        );

        emit MetadataUpdate(tokenID);
    }

    /**
     * See {CanvasOnChain-_tokenCard}.
     */
    function _tokenCard(
        uint256 tokenID,
        bytes memory image
    ) internal view override returns (bytes memory) {
        (bytes memory name_, , bytes memory url) = getTile(tokenID);
        address canvasAddress = canvasDeployer.canvasAddress(tokenID);
        return
            TOCUtilities.mainCanvasCard(
                tokenID,
                image,
                name_,
                url,
                getContractUrl(),
                canvasAddress,
                logoImage
            );
    }

    // === Internal Functions ============= //

    /**
     * @dev See {ERC721-_beforeTokenTransfer}.
     */
    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 tokenID,
        uint256 batchSize
    ) internal virtual override {
        super._beforeTokenTransfer(from, to, tokenID, batchSize);
        address canvasAddress = canvasDeployer.canvasAddress(tokenID);
        if (canvasAddress != address(0)) {
            CanvasOnChain(canvasAddress).withdraw();
            CanvasOnChain(canvasAddress).resetWhitelist();
        }
    }

    /**
     * @dev See {CanvasOnChain-_updateUrl}.
     * It also updates the token description and image for the specified `tokenID`.
     * If a canvas is associated with the token, it also updates the contract URL of the canvas.
     */
    function _updateUrl(
        uint256 tokenID,
        string calldata url
    ) internal override {
        address canvasAddress = canvasDeployer.canvasAddress(tokenID);
        bytes memory tokenDescription;

        if (canvasAddress != address(0)) {
            CanvasOnChain(canvasAddress).updateContractUrl(url);
            tokenDescription = abi.encodePacked(
                "Visit my site:",
                url,
                ".\\n\\n",
                "Canvas deployed on: ",
                canvasAddress.toString(),
                "."
            );
        } else {
            tokenDescription = abi.encodePacked("Visit my site:", url, ".");
        }

        _setValue(
            tokenID,
            OnChainMetadataStorage.key_token_external_url,
            abi.encodePacked(url)
        );
        _setValue(
            tokenID,
            OnChainMetadataStorage.key_token_description,
            tokenDescription
        );
        _setValue(
            tokenID,
            OnChainMetadataStorage.key_token_image,
            abi.encodePacked(_tokenCard(tokenID, _base64Image[tokenID]))
        );
    }
}
"
    },
    "contracts/OnChainMetadata.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import "@openzeppelin/contracts/utils/Strings.sol";
import "@openzeppelin/contracts/utils/Base64.sol";
import "./OnChainMetadataStorage.sol";

/**
 * @author Andrea Tedesco (@andreatedesco).
 * @dev The developer is responsible for assigning metadata for the contract
 * and tokens, by inheriting this contract and using _addValue() and _setValue() methods.
 */
abstract contract OnChainMetadata {
    using OnChainMetadataStorage for OnChainMetadataStorage.Metadata;

    // metadata for the contract
    OnChainMetadataStorage.Metadata private _contractMetadata;

    // metadata for each token
    mapping(uint256 => OnChainMetadataStorage.Metadata) private _tokenMetadata;

    /**
     * @dev Get the first value of a token metadata key.
     * @param tokenId the token identifier.
     * @param key the token metadata key.
     */
    function _getValue(
        uint256 tokenId,
        bytes32 key
    ) internal view returns (bytes memory) {
        return _tokenMetadata[tokenId].getValue(key);
    }

    /**
     * @dev Get the first value of a contract metadata key.
     * @param key the contract metadata key.
     */
    function _getValue(bytes32 key) internal view returns (bytes memory) {
        return _contractMetadata.getValue(key);
    }

    /**
     * @dev Set the values on a token metadata key.
     * @param tokenId the token identifier.
     * @param key the token metadata key.
     * @param values the token metadata values.
     */
    function _setValues(
        uint256 tokenId,
        bytes32 key,
        bytes[] memory values
    ) internal {
        _tokenMetadata[tokenId].data[key] = values;
    }

    /**
     * @dev Set a single value on a token metadata key.
     * @param tokenId the token identifier.
     * @param key the token metadata key.
     * @param value the token metadata value.
     */
    function _setValue(
        uint256 tokenId,
        bytes32 key,
        bytes memory value
    ) internal {
        bytes[] memory values = new bytes[](1);
        values[0] = value;
        _setValues(tokenId, key, values);
    }

    /**
     * @dev Set values on a token metadata key.
     * @param key the token metadata key.
     * @param values the token metadata values.
     */
    function _setValues(bytes32 key, bytes[] memory values) internal {
        _contractMetadata.data[key] = values;
    }

    /**
     * @dev Set a single value on a token metadata key.
     * @param key the token metadata key.
     * @param value the token metadata value.
     */
    function _setValue(bytes32 key, bytes memory value) internal {
        bytes[] memory values = new bytes[](1);
        values[0] = value;
        _setValues(key, values);
    }

    /**
     * @dev Returns the token URI for the specified `tokenId` as a string.
     */
    function _createTokenURI(
        uint256 tokenId
    ) internal view virtual returns (string memory) {
        return _tokenMetadata[tokenId].createTokenURI();
    }

    /**
     * @dev Returns the contract URI as a string.
     */
    function _createContractURI()
        internal
        view
        virtual
        returns (string memory)
    {
        return _contractMetadata.createContractURI();
    }
}
