    "contracts/OnChainMetadataBuilder.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import "@openzeppelin/contracts/utils/Strings.sol";
import "@openzeppelin/contracts/utils/Base64.sol";

/**
 * @author Andrea Tedesco (@andreatedesco).
 * @dev This library provides functions for creating token and contract URIs formatted according to the official NFT metadata standards.
 */
library OnChainMetadataBuilder {
    /**
     * @dev Internal function to create the token URI.
     *
     * @param name The name of the token.
     * @param description The description of the token.
     * @param image The image of the token.
     * @param external_url The external URL of the token.
     * @param trait_type The trait types associated with the token.
     * @param trait_value The trait values associated with the token.
     * @param trait_display The trait display types associated with the token.
     * @param animation_url The animation URL of the token.
     * @param youtube_url The YouTube URL of the token.
     * @param background_color The background color of the token.
     * @return The token URI as a string.
     */
    function createTokenURI(
        bytes memory name,
        bytes memory description,
        bytes memory image,
        bytes memory external_url,
        bytes[] memory trait_type,
        bytes[] memory trait_value,
        bytes[] memory trait_display,
        bytes memory animation_url,
        bytes memory youtube_url,
        bytes memory background_color
    ) external pure returns (string memory) {
        bytes memory attributes;
        if (trait_type.length > 0) {
            attributes = "[";
            for (uint256 i = 0; i < trait_type.length; i++) {
                attributes = abi.encodePacked(
                    attributes,
                    i > 0 ? "," : "",
                    "{",
                    trait_display[i].length > 0
                        ? string(
                            abi.encodePacked(
                                '"display_type": "',
                                string(trait_display[i]),
                                '",'
                            )
                        )
                        : "",
                    '"trait_type": "',
                    string(trait_type[i]),
                    '", "value": "',
                    string(trait_value[i]),
                    '"}'
                );
            }
            attributes = abi.encodePacked(attributes, "]");
        }

        return
            string(
                abi.encodePacked(
                    "data:application/json;base64,",
                    Base64.encode(
                        abi.encodePacked(
                            "{",
                            '"name": "',
                            name,
                            '", ',
                            '"description": "',
                            description,
                            '"',
                            image.length > 0
                                ? string(
                                    abi.encodePacked(
                                        ', "image": "',
                                        string(image),
                                        '"'
                                    )
                                )
                                : "",
                            animation_url.length > 0
                                ? string(
                                    abi.encodePacked(
                                        ', "animation_url": "',
                                        string(animation_url),
                                        '"'
                                    )
                                )
                                : "",
                            external_url.length > 0
                                ? string(
                                    abi.encodePacked(
                                        ', "external_url": "',
                                        string(external_url),
                                        '"'
                                    )
                                )
                                : "",
                            attributes.length > 0
                                ? string(
                                    abi.encodePacked(
                                        ', "attributes": ',
                                        attributes
                                    )
                                )
                                : "",
                            background_color.length > 0
                                ? string(
                                    abi.encodePacked(
                                        ', "background_color": ',
                                        string((background_color))
                                    )
                                )
                                : "",
                            youtube_url.length > 0
                                ? string(
                                    abi.encodePacked(
                                        ', "youtube_url": ',
                                        string((youtube_url))
                                    )
                                )
                                : "",
                            "}"
                        )
                    )
                )
            );
    }

    /**
     * @dev Internal function to create the contract URI.
     *
     * @param name The name of the contract.
     * @param description The description of the contract.
     * @param image The image of the contract.
     * @param banner The banner of the contract.
     * @param external_url The external URL of the contract.
     * @return The contract URI as a string.
     */
    function createContractURI(
        bytes memory name,
        bytes memory description,
        bytes memory image,
        bytes memory banner,
        bytes memory external_url
    ) external pure returns (string memory) {
        return
            string(
                abi.encodePacked(
                    "data:application/json;base64,",
                    Base64.encode(
                        abi.encodePacked(
                            "{",
                            '"name": "',
                            string(name),
                            '"',
                            description.length > 0
                                ? string(
                                    abi.encodePacked(
                                        ', "description": "',
                                        string(description),
                                        '"'
                                    )
                                )
                                : "",
                            image.length > 0
                                ? string(
                                    abi.encodePacked(
                                        ', "image": "',
                                        string(image),
                                        '"'
                                    )
                                )
                                : "",
                            banner.length > 0
                                ? string(
                                    abi.encodePacked(
                                        ', "banner": "',
                                        string(banner),
                                        '"'
                                    )
                                )
                                : "",
                            external_url.length > 0
                                ? string(
                                    abi.encodePacked(
                                        ', "external_link": "',
                                        string(external_url),
                                        '"'
                                    )
                                )
                                : "",
                            "}"
                        )
                    )
                )
            );
    }
}
"
    },
    "contracts/OnChainMetadataStorage.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import "@openzeppelin/contracts/utils/Strings.sol";
import "@openzeppelin/contracts/utils/Base64.sol";
import "./OnChainMetadataBuilder.sol";

/**
 * @author Andrea Tedesco (@andreatedesco).
 * @dev The library offers functions for retrieving and creating on-chain metadata for contracts and tokens.
 * With OnChainMetadataStorage, contract and token metadata can be securely stored and accessed directly on the blockchain,
 * eliminating the need for off-chain storage solutions and ensuring data integrity and immutability.
 */
library OnChainMetadataStorage {
    struct Metadata {
        mapping(bytes32 => bytes[]) data; // key => values
    }

    bytes32 constant key_contract_name = "name";
    bytes32 constant key_contract_description = "description";
    bytes32 constant key_contract_logo = "image";
    bytes32 constant key_contract_banner = "banner";
    bytes32 constant key_contract_external_link = "external_link";

    bytes32 constant key_token_name = "name";
    bytes32 constant key_token_description = "description";
    bytes32 constant key_token_image = "image";
    bytes32 constant key_token_animation_url = "animation_url";
    bytes32 constant key_token_external_url = "external_url";
    bytes32 constant key_token_background_color = "background_color";
    bytes32 constant key_token_youtube_url = "youtube_url";
    bytes32 constant key_token_attributes_trait_type = "trait_type";
    bytes32 constant key_token_attributes_trait_value = "trait_value";
    bytes32 constant key_token_attributes_display_type = "trait_display";

    /**
     * @dev Retrieves the values associated with the specified key.
     *
     * @param metadata The metadata storage.
     * @param key The key to retrieve the values for.
     * @return An array of values associated with the key.
     */
    function getValues(
        Metadata storage metadata,
        bytes32 key
    ) public view returns (bytes[] memory) {
        return metadata.data[key];
    }

    /**
     * @dev Retrieves the value associated with the specified key.
     *
     * @param metadata The metadata storage.
     * @param key The key to retrieve the value for.
     * @return The value associated with the key.
     */
    function getValue(
        Metadata storage metadata,
        bytes32 key
    ) public view returns (bytes memory) {
        bytes[] memory array = getValues(metadata, key);
        if (array.length > 0) {
            return array[0];
        } else {
            return "";
        }
    }

    /**
     * @dev Creates the token URI based on the metadata.
     *
     * @param metadata The metadata storage.
     * @return The token URI as a string.
     */
    function createTokenURI(
        Metadata storage metadata
    ) external view returns (string memory) {
        return
            OnChainMetadataBuilder.createTokenURI(
                getValue(metadata, key_token_name),
                getValue(metadata, key_token_description),
                getValue(metadata, key_token_image),
                getValue(metadata, key_token_external_url),
                getValues(metadata, key_token_attributes_trait_type),
                getValues(metadata, key_token_attributes_trait_value),
                getValues(metadata, key_token_attributes_display_type),
                getValue(metadata, key_token_animation_url),
                getValue(metadata, key_token_youtube_url),
                getValue(metadata, key_token_background_color)
            );
    }

    /**
     * @dev Creates the contract URI based on the metadata.
     *
     * @param metadata The metadata storage.
     * @return The contract URI as a string.
     */
    function createContractURI(
        Metadata storage metadata
    ) external view returns (string memory) {
        return
            OnChainMetadataBuilder.createContractURI(
                getValue(metadata, key_contract_name),
                getValue(metadata, key_contract_description),
                getValue(metadata, key_contract_logo),
                getValue(metadata, key_contract_banner),
                getValue(metadata, key_contract_external_link)
            );
    }
}
"
    },
    "contracts/TOCUtilities.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import "@openzeppelin/contracts/utils/Strings.sol";
import "@openzeppelin/contracts/utils/Base64.sol";

/**
 * @author Andrea Tedesco (@andreatedesco).
 * @dev A library contract that provides utility functions for handling SVG images and generating SVG representations for canvas cards.
 * The library includes functions for converting images to Base64-encoded SVG, converting addresses to string representation,
 * generating SVG representations for creator canvas cards and main canvas cards, and replacing ampersands in URLs.
 */
library TOCUtilities {
    using TOCUtilities for address;

    /**
     * @dev Returns the Base64-encoded svg associated with the specified Base64-encoded string `image`.
     */
    function toSVG(
        bytes memory image,
        uint256 width_,
        uint256 height_
    ) public pure returns (bytes memory) {
        return
            abi.encodePacked(
                "data:image/svg+xml;base64,",
                Base64.encode(
                    (
                        abi.encodePacked(
                            '<svg width="100%" height="100%" viewBox="0 0 ',
                            Strings.toString(width_),
                            " ",
                            Strings.toString(height_),
                            '" xmlns="http://www.w3.org/2000/svg"><image style="image-rendering:pixelated" href="data:image/png;base64,',
                            string(abi.encodePacked(image)),
                            '" x="0" y="0" width="',
                            Strings.toString(width_),
                            '" height="',
                            Strings.toString(height_),
                            '"/></svg>'
                        )
                    )
                )
            );
    }

    /**
     * @dev Returns the string representation of the specified address `account`.
     */
    function toString(address account) public pure returns (string memory) {
        bytes memory data = abi.encodePacked(account);
        bytes memory alphabet = "0123456789abcdef";

        bytes memory str = new bytes(2 + data.length * 2);
        str[0] = "0";
        str[1] = "x";
        for (uint i = 0; i < data.length; i++) {
            str[2 + i * 2] = alphabet[uint(uint8(data[i] >> 4))];
            str[3 + i * 2] = alphabet[uint(uint8(data[i] & 0x0f))];
        }
        return string(str);
    }

    /**
     * @dev This function generates the SVG representation of a creator canvas card for a given token.
     */
    function creatorCanvasCard(
        uint256 tokenID,
        bytes memory image,
        bytes memory name,
        bytes memory url,
        address contractAddress,
        bytes memory mainUrl,
        uint256 canvasID,
        bytes memory canvasImage,
        bytes memory logoImage
    ) external pure returns (bytes memory) {
        return
            abi.encodePacked(
                "data:image/svg+xml;base64,",
                Base64.encode(
                    (
                        abi.encodePacked(
                            '<?xml version="1.0" encoding="iso-8859-1"?><svg version="1.1" xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink" x="0px" y="0px" viewBox="0 0 512 512" style="enable-background:new 0 0 512 512;" xml:space="preserve"><style>.t00 {font: bold 24px bahnschrift;text-anchor: middle;fill: black;}.t01 {font: 20px bahnschrift;text-anchor: middle;fill: black;}.t02 {font: 12px bahnschrift;text-anchor: middle;fill: black;}</style><rect width="510" height="510" x="50%" y="50%" transform="translate(-255,-255)" style="fill:rgb(229,228,226);stroke-width:2;stroke:black"/><text x="256" y="55" class="t00">',
                            name,
                            '</text><text x="30" y="30" class="t01">#',
                            Strings.toString(tokenID),
                            '</text><rect width="390" height="390" x="50%" y="50%" transform="translate(-195,-190)" style="fill:rgb(229,228,226,0.05);stroke-width:1;stroke:black"/><image width="390" height="390" x="50%" y="50%" transform="translate(-195,-190)" style="image-rendering:pixelated;" href="data:image/png;base64,',
                            image,
                            '"/><a href="',
                            replaceAmpersand(url),
                            '"><rect width="30" height="30" x="475" y="7" style="fill:rgb(255,255,255,0);stroke-width:1;stroke:black"/><image width="24" height="24" x="50%" y="50%" transform="translate(222,-246)" style="image-rendering:pixelated;" href="data:image/png;base64,"/></a><a href="https://polygonscan.com/token/',
                            toString(contractAddress),
                            '"><rect width="30" height="30" x="475" y="475" style="fill:rgb(255,255,255,0);stroke-width:1;stroke:black"/><text x="490" y="472" class="t02">#',
                            Strings.toString(canvasID),
                            '</text><image width="30" height="30" x="475" y="475" style="image-rendering:pixelated" href="data:image/png;base64,',
                            canvasImage,
                            '"/></a><a href="',
                            mainUrl,
                            '"><image width="30" height="30" x="7" y="475" style="image-rendering:pixelated" href="data:image/png;base64,',
                            logoImage,
                            '"/></a></svg>'
                        )
                    )
                )
            );
    }

    /**
     * @dev This function generates the SVG representation of a main canvas card for a given token.
     */
    function mainCanvasCard(
        uint256 tokenID,
        bytes memory image,
        bytes memory name,
        bytes memory url,
        bytes memory mainUrl,
        address canvasAddress,
        bytes memory logoImage
    ) external pure returns (bytes memory) {
        return
            abi.encodePacked(
                "data:image/svg+xml;base64,",
                Base64.encode(
                    (
                        abi.encodePacked(
                            '<?xml version="1.0" encoding="iso-8859-1"?><svg version="1.1" xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink" x="0px" y="0px" viewBox="0 0 512 512" style="enable-background:new 0 0 512 512;" xml:space="preserve"><style>.t00 {font: bold 24px bahnschrift;text-anchor: middle;fill: white;}.t01 {font: 20px bahnschrift;text-anchor: middle;fill: white;}</style><rect width="510" height="510" x="50%" y="50%" transform="translate(-255,-255)" style="fill:rgb(20,18,23);stroke-width:2;stroke:white"/><text x="256" y="55" class="t00">',
                            name,
                            '</text><text x="30" y="30" class="t01">#',
                            Strings.toString(tokenID),
                            '</text><rect width="390" height="390" x="50%" y="50%" transform="translate(-195,-190)" style="fill:rgb(255,255,255,0.05);stroke-width:1;stroke:white"/><image width="390" height="390" x="50%" y="50%" transform="translate(-195,-190)" style="image-rendering:pixelated;" href="data:image/png;base64,',
                            image,
                            '"/><a href="',
                            replaceAmpersand(url),
                            '"><rect width="30" height="30" x="475" y="7" style="fill:rgb(255,255,255,0);stroke-width:1;stroke:white"/><image width="24" height="24" x="50%" y="50%" transform="translate(222,-246)" style="image-rendering:pixelated;" href="data:image/png;base64,"/></a><a href="',
                            mainUrl,
                            '"><image width="30" height="30" x="7" y="475" style="image-rendering:pixelated" href="data:image/png;base64,',
                            logoImage,
                            '"/></a>',
                            canvasAddress == address(0)
                                ? ""
                                : string(
                                    abi.encodePacked(
                                        '<a href="https://polygonscan.com/token/',
                                        toString(canvasAddress),
                                        '"><rect width="30" height="30" x="475" y="475" style="fill:rgb(255,255,255,0);stroke-width:1;stroke:white"/><image width="30" height="30" x="50%" y="50%" transform="translate(219,219)" style="image-rendering:pixelated;" href="data:image/png;base64,"/></a>'
                                    )
                                ),
                            "</svg>"
                        )
                    )
                )
            );
    }

    /**
     * @dev This function replaces any occurrences of ampersands in the inputBytes parameter with the HTML entity "&".
     */
    function replaceAmpersand(
        bytes memory inputBytes
    ) public pure returns (string memory) {
        bytes memory outputBytes = new bytes(inputBytes.length * 5); // Dimensione massima per la sostituzione

        uint256 outputIndex = 0;
        for (uint256 i = 0; i < inputBytes.length; i++) {
            if (inputBytes[i] == "&") {
                outputBytes[outputIndex++] = "&";
                outputBytes[outputIndex++] = "a";
                outputBytes[outputIndex++] = "m";
                outputBytes[outputIndex++] = "p";
                outputBytes[outputIndex++] = ";";
            } else {
                outputBytes[outputIndex++] = inputBytes[i];
            }
        }

        bytes memory finalBytes = new bytes(outputIndex);
        for (uint256 i = 0; i < outputIndex; i++) {
            finalBytes[i] = outputBytes[i];
        }

        return string(finalBytes);
    }
}
"
    }
  },
  "settings": {
    "viaIR": true,
    "optimizer": {
      "enabled": true,
