import '@openzeppelin/contracts/utils/math/SafeMath.sol';
import '@openzeppelin/contracts/access/Ownable.sol';
import '@openzeppelin/contracts/security/ReentrancyGuard.sol';
import '@openzeppelin/contracts/token/ERC721/extensions/ERC721Enumerable.sol';
import '@openzeppelin/contracts/token/ERC721/extensions/ERC721URIStorage.sol';
import '@openzeppelin/contracts/token/ERC721/extensions/ERC721Burnable.sol';
import '@openzeppelin/contracts/token/ERC721/extensions/IERC721Metadata.sol';
import '@openzeppelin/contracts/token/ERC721/extensions/ERC721Pausable.sol';
import '@openzeppelin/contracts/token/ERC721/extensions/ERC721Royalty.sol';
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import '@openzeppelin/contracts/utils/cryptography/MerkleProof.sol';
import '../libraries/DateTime.sol';

contract DunHuangPixel is ERC721, ERC721Royalty, ERC721Enumerable, ERC721URIStorage, ERC721Burnable, ERC721Pausable, Ownable, AccessControl, ReentrancyGuard {
    using Address for address;
    using SafeMath for uint256;
    using Strings for uint256;
    using DateTime for uint256;

    uint48 public whiteMinted;
    uint48 public perMintMax;
    string private baseURI;
    uint256 public MINT_FEE;
    uint48 public maxSupply;
    uint256 public currentTokenId=1;
    address private beneficiary;
    mapping(address => uint48) public whitelist;
    mapping(address => uint48) public minted;
    bytes32 public constant PAUSER_ROLE = keccak256("PAUSER_ROLE");
    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");
    address public feeToken = address(0x7ceB23fD6bC0adD59E62ac25578270cFf1b9f619);
    bool private initialized=false;

    struct whiteMintTime {
        uint256 beginTime;
        uint256 endTime;
    }
    whiteMintTime public whiteMintPeriod;

    event SetFee(uint256 oldMintFee, uint256 newMinFee);
    event Payed(address user, address token, uint256 amount, uint256 timestamp);

    modifier onlyMinter() {
        require(hasRole(MINTER_ROLE,_msgSender()), "onlyMinter: caller is not the  minter");
        _;
    }

    constructor(
        string memory _name,
        string memory _symbol,
        uint256 _mint_fee,
        uint48 _maxSupply,
        address _royaltyReceiver,
        uint96 _royaltyFee,
        uint48 _perMintMax
    ) ERC721(_name, _symbol) {

        require(_maxSupply > 0, "maxSupply is 0");
        _setupRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _setupRole(PAUSER_ROLE, msg.sender);
        _setupRole(MINTER_ROLE, msg.sender);
        if (_royaltyReceiver != address(0)) {
            _setDefaultRoyalty(_royaltyReceiver, _royaltyFee);
            beneficiary=_royaltyReceiver;
        }
        maxSupply = _maxSupply;
        MINT_FEE = _mint_fee;
        whiteMintPeriod.beginTime = DateTime.addDays(block.timestamp,0);
        whiteMintPeriod.endTime = DateTime.addHours(whiteMintPeriod.beginTime,24);
        perMintMax=_perMintMax;
    }
    function setWhitelist(address[] memory _users,uint48[] memory _maxMints) public onlyMinter{

        for (uint256 i = 0; i < _users.length; i++) {
            whitelist[_users[i]]=_maxMints[i];
        }
    }
    function batchMintDrop(
        address[] memory _tos,
        uint48[] memory _tokenAmounts
    ) public  onlyMinter {

        for (uint256 j = 0; j < _tos.length; j++) {
            address to=_tos[j];
            uint48  tokenAmount=_tokenAmounts[j];
            uint48 _total=minted[to]+tokenAmount;
            uint256 maxMint=verify(to);
            require(_total<=maxMint, "has reached the maxMint");
            for (uint256 i; i < tokenAmount; i++) {
                _mint(to,"");
            }
            minted[to]=_total;
            require(maxSupply>=totalSupply(), "has reached the maxMint");
        }
    }
    function verify(address _sender) public view returns (uint48) {
        return whitelist[_sender];
    }
    function setWhiteSaleTime(uint256 beginTime, uint256 endTime) external onlyOwner {
        whiteMintPeriod.beginTime = beginTime;
        whiteMintPeriod.endTime = endTime;
    }
    function getWhiteSaleTime() public view returns (uint256,uint256) {
        return (whiteMintPeriod.beginTime,whiteMintPeriod.endTime);
    }
    function statusSaleTime() public view returns (uint48) {
        if(block.timestamp >= whiteMintPeriod.beginTime && block.timestamp <= whiteMintPeriod.endTime){
            return 1;
        }
        if(block.timestamp > whiteMintPeriod.endTime){
            return 2;
        }
        return 0;
    }

    function setMaxMint(uint48 _perMintMax) public onlyOwner {
        perMintMax=_perMintMax;
    }
    function available(address to) public view returns (uint256) {
        uint256 _available=maxSupply-totalSupply();
        uint48 status=statusSaleTime();
        uint48 count=0;
        if(status==0){
            _available=0;
        }else if(status==1){
            count= verify(to);
            if(count<=_available){
               _available=count;
            }
        }else{
            count= verify(to);
            count=perMintMax+count;
            if(count<=_available){
                _available=count;
            }
        }
        return _available;
    }

   function hasAvailable(address to) public view returns (uint256) {
       uint256 maxMint=available(to);

       if(minted[to]>maxMint){
           return 0;
       }
       uint256 hasCount = maxMint-minted[to];
       return hasCount;
    }

    function safeMint(
        address to,
        string calldata _tokenURI
    ) external payable nonReentrant {
        uint48 _total=minted[to]+1;
        uint256 maxMint=available(to);
        require(_total<=maxMint, "has reached the maxMint");
        _pay(1);
        _mint(to, _tokenURI);
        minted[to]=_total;
        require(maxSupply>=totalSupply(), "has reached the maxMint");
    }

    function _pay(uint256 tokenAmount) internal {

        uint256 fee=MINT_FEE*tokenAmount;
        if (feeToken == address(0)) {
            require(msg.value == fee || hasRole(MINTER_ROLE,_msgSender()), "insufficient fee");
        }else{
            if(!hasRole(MINTER_ROLE,_msgSender()) && fee>0){
                IERC20(feeToken).transferFrom(_msgSender(), address(this), fee);
                emit Payed(_msgSender(), feeToken, fee, block.timestamp);
            }
        }
    }

    function batchMint(
        address to,
        uint48 tokenAmount
    ) external payable nonReentrant {
        uint48 _total=minted[to]+tokenAmount;
        uint256 maxMint=available(to);
        require(_total<=maxMint, "has reached the maxMint");
        _pay(tokenAmount);
        for (uint256 i; i < tokenAmount; i++) {
            _mint(to,"");
        }
        minted[to]=_total;
        require(maxSupply>=totalSupply(), "has reached the maxMint");
    }

    fallback() external payable {}
    receive() external payable {}

    function _mint(address to, string memory _tokenURI) internal {
        _safeMint(to, currentTokenId);
        if (bytes(_tokenURI).length > 0) {
            _setTokenURI(currentTokenId, _tokenURI);
        }
        currentTokenId++;
    }

    function walletOfOwner(address _owner) public view returns (uint256[] memory) {
        uint256 ownerTokenCount = balanceOf(_owner);
        uint256[] memory _tokenIds = new uint256[](ownerTokenCount);
        for (uint256 i; i < ownerTokenCount; i++) {
            _tokenIds[i] = tokenOfOwnerByIndex(_owner, i);
        }
        return _tokenIds;
    }

    function setFee(uint256 _mint_fee) external onlyOwner {
        emit SetFee(MINT_FEE, _mint_fee);
        MINT_FEE = _mint_fee;
    }


    function _beforeTokenTransfer(address from, address to, uint256 tokenId) internal override(ERC721, ERC721Enumerable, ERC721Pausable) {
        super._beforeTokenTransfer(from, to, tokenId);
    }

    function supportsInterface(bytes4 interfaceId) public view virtual override(ERC721, ERC721Royalty, ERC721Enumerable, AccessControl) returns (bool) {
        return super.supportsInterface(interfaceId);
    }

    function setMaxSupply(uint48 _maxSupply) external onlyOwner {
        require(!initialized, "Contract instance has already been initialized");
        initialized = true;
        maxSupply = _maxSupply;
    }

    function setFeeToken(address token) external onlyOwner {
        feeToken = token;
    }

    function setBeneficiary(address addr) external onlyOwner {
        beneficiary = addr;
    }

    function _burn(uint256 tokenId) internal override(ERC721, ERC721URIStorage, ERC721Royalty) {
        super._burn(tokenId);
    }

    function tokenURI(uint256 tokenId) public view override(ERC721, ERC721URIStorage) returns (string memory) {
        require(_exists(tokenId));
        string memory base = _baseURI();
        if (bytes(base).length > 0) {
            return string(abi.encodePacked(base, tokenId.toString(), '.json'));
        }
        return super.tokenURI(tokenId);
    }

    function _baseURI() internal view override(ERC721) returns (string memory) {
        return baseURI;
    }

    function setURIs(string memory _URI) external onlyOwner {
        require(!initialized, "Contract instance has already been initialized");
        baseURI = _URI;
    }

    function getBalance() public view returns (uint256) {
        return address(this).balance;
    }

    function getTokenBalance(address token) public view returns (uint256) {
        return IERC20(token).balanceOf(address(this));
    }

    function withdrawToken(address token) public {
        uint256 value = getTokenBalance(token);
        IERC20(token).transfer(beneficiary, value);
    }

    function withdraw() public {
        uint256 balance = address(this).balance;
        require(balance > 0, 'Balance should be more then zero');
        payable(beneficiary).transfer(balance);
    }

    function pause() public onlyRole(PAUSER_ROLE) {
        _pause();
    }

    function unpause() public onlyRole(PAUSER_ROLE) {
        _unpause();
    }
}"
    }
  }
}}
