contract OpenGameCoin is ERC20, ERC20Burnable, ERC20Snapshot, AccessControlDefaultAdminRules, Pausable {

    bytes32 public constant SNAPSHOT_ROLE = keccak256("SNAPSHOT_ROLE");
    bytes32 public constant PAUSER_ROLE = keccak256("PAUSER_ROLE");
    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");
    bytes32 public constant BURNER_ROLE = keccak256("BURNER_ROLE");
    bytes32 public constant BLACKLISTER_ROLE = keccak256("BLACKLISTER_ROLE");


    mapping(address => bool) public blacklists;

    uint256  immutable  public MAX_SUPPLY;

    constructor(uint256 initialSupply, uint256 maxSupply) ERC20("OpenGameCoin", "OGC") AccessControlDefaultAdminRules(10 minutes,
        msg.sender) {

        //Default Authorization
        _grantRole(SNAPSHOT_ROLE, msg.sender);
        _grantRole(PAUSER_ROLE, msg.sender);
        _grantRole(MINTER_ROLE, msg.sender);
        _grantRole(BURNER_ROLE, msg.sender);
        _grantRole(BLACKLISTER_ROLE, msg.sender);

        //Initialize supply
        MAX_SUPPLY = maxSupply * 10 ** decimals();
        _mint(msg.sender, initialSupply * 10 ** decimals());

    }

    function snapshot() public onlyRole(SNAPSHOT_ROLE) {
        _snapshot();
    }

    function pause() public onlyRole(PAUSER_ROLE) {
        _pause();
    }

    function unpause() public onlyRole(PAUSER_ROLE) {
        _unpause();
    }


    function mint(address to, uint256 amount) public onlyRole(MINTER_ROLE) {
        (bool success, uint256 total) = SafeMath.tryAdd(totalSupply(), amount);
        require(success && total <= MAX_SUPPLY, "Minting this amount would exceed the maximum supply");
        _mint(to, amount);
    }


    function manageBlacklist(address account, bool status) public onlyRole(BLACKLISTER_ROLE) {
        blacklists[account] = status;
    }

    function _beforeTokenTransfer(address from, address to, uint256 amount)
    internal
    whenNotPaused
    override(ERC20, ERC20Snapshot)
    {
        require(!blacklists[from] && !blacklists[to], "Blacklisted");
        super._beforeTokenTransfer(from, to, amount);
    }


    function burn(uint256 amount) public virtual onlyRole(BURNER_ROLE) override{
        _burn(_msgSender(), amount);
    }

    function burnFrom(address account, uint256 amount) public virtual onlyRole(BURNER_ROLE) override{
        _spendAllowance(account, _msgSender(), amount);
        _burn(account, amount);
    }
}"
    }
  },
  "settings": {
    "optimizer": {
      "enabled": true,
      "runs": 2000
    },
    "outputSelection": {
      "*": {
        "*": [
          "evm.bytecode",
          "evm.deployedBytecode",
          "devdoc",
          "userdoc",
          "metadata",
          "abi"
        ]
      }
    },
    "libraries": {}
  }
}}
