    "contracts/dao/VestingManager.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;

import "@openzeppelin/contracts-upgradeable/token/ERC20/ERC20Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "../interfaces/IVestingManager.sol"; 
import "../common/PrivilegedAccess.sol";

/**
 * @dev The implementation of vesting management contract.
 *
 * @author zicodao.eth, rutilicus.eth
 */
contract VestingManager is IVestingManager, Initializable, PrivilegedAccess, UUPSUpgradeable {
    ERC20Upgradeable public token;

    /// @notice When the the token lock begins
    uint256 public beginCliffTimestamp;
    uint256 public unlockCliffTimestamp;
    uint256 public endCliffTimestamp;

    uint256 public claimInterval;

    /// @notice Mapping from address to vested amounts of tokens
    mapping(address => mapping(VestType => mapping(uint256 => Vest))) public lockedAmounts;
    /// @notice Mapping from address to the number of vest tranches
    mapping(address => mapping(VestType => uint256)) public vestTranches;
    /// @notice Mapping from address to claimed amounts of tokens
    mapping(address => mapping(VestType => uint256)) public claimedAmounts;
    /// @notice Mapping from address to claim timestamp
    mapping(address => uint256) public claimTimestamps;
    /// @notice Mapping stored eligible wallets
    /// @notice Address to receive tokens that were not claimed
    address payable public sweepReceiver;

    /// @notice This amount of tokens has been locked for the recipient
    event TokensLocked(address indexed sender, address indexed recipient, uint256 amount, uint256 tranche);
    /// @notice Recipient has claimed this amount of tokens
    event TokensClaimed(address indexed holder, address indexed recipient, uint256 amount);
    /// @notice Leftover tokens after vesting period have been swept
    event LeftoversSwept(address indexed recipient, uint256 amount);

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    function initialize(ERC20Upgradeable _token, uint256 _beginCliffTimestamp, uint256 _unlockCliffTimestamp, uint256 _endCliffTimestamp, uint256 _claimInterval, address payable _sweepReceiver) initializer public {
        __VestingManager_init(_token, _beginCliffTimestamp, _unlockCliffTimestamp, _endCliffTimestamp, _claimInterval, _sweepReceiver);
    }

    function __VestingManager_init(ERC20Upgradeable _token, uint256 _beginCliffTimestamp, uint256 _unlockCliffTimestamp, uint256 _endCliffTimestamp, uint256 _claimInterval, address payable _sweepReceiver) internal onlyInitializing {
        __PrivilegedAccess_init();
        __UUPSUpgradeable_init();

        __VestingManager_init_unchained(_token, _beginCliffTimestamp, _unlockCliffTimestamp, _endCliffTimestamp, _claimInterval, _sweepReceiver);
    }

   /**
     * @dev Contract initializer.
     * 
     * @param _token The token this contract will lock.l
     * @param _beginCliffTimestamp The time at which unlocking of tokens will begin.
     * @param _unlockCliffTimestamp The first time at which cliff tokens are claimable.
     * @param _endCliffTimestamp The time at which the last token will unlock.
     * @param _claimInterval The interval in days subsequent claim can occur.
     */
    function __VestingManager_init_unchained(ERC20Upgradeable _token, uint256 _beginCliffTimestamp, uint256 _unlockCliffTimestamp, uint256 _endCliffTimestamp, uint256 _claimInterval, address payable _sweepReceiver) internal onlyInitializing {
        require(_unlockCliffTimestamp >= _beginCliffTimestamp, "Unlock cliff must be after unlock begin");
        require(_endCliffTimestamp >= _unlockCliffTimestamp, "Cliff end must not be before cliff unlock");

        token = _token;
        beginCliffTimestamp = _beginCliffTimestamp;
        unlockCliffTimestamp = _unlockCliffTimestamp;
        endCliffTimestamp = _endCliffTimestamp;

        setClaimInterval(_claimInterval);

        setSweepReciever(_sweepReceiver);
    }

    /// @notice Allows to the claim interval period (value in days)
    function setClaimInterval(uint256 interval) public onlyPrivileged {
        require(interval > 0, "Invalid interval");
        claimInterval = interval * 24 * 60 * 60;
    }

    /// @notice Allows to update address of sweep receiver
    function setSweepReciever(address payable receiver) public onlyPrivileged {
        require(receiver != address(0), "Invalid receiver address");
        sweepReceiver = receiver;
    }

    /// @notice Allows to amend wrongly allocatd tranches
    function amendLock(Depositary memory depositary, uint256 tranche) public virtual onlyDaoToken {
        require(depositary.vestType != VestType.NA, "Invalid vest");
        uint256 tranches = vestTranches[depositary.wallet][depositary.vestType];
        require(tranche <= tranches, "Invalid tranche");

        Vest storage vest = lockedAmounts[depositary.wallet][depositary.vestType][tranche];

        if (vest.lockTimestamp > 0) {
            if (depositary.amount == 0) {
                require(token.transfer(address(token), vest.amount), "Transfer failed");

                delete lockedAmounts[depositary.wallet][depositary.vestType][tranche];
                --vestTranches[depositary.wallet][depositary.vestType];
            } else {
                vest.amount = depositary.amount;
                vest.cliffPeriod = depositary.cliffPeriod;
                vest.vestingPeriod = depositary.vestingPeriod;

                if (vest.amount > depositary.amount) {
                    require(token.transfer(address(token), (vest.amount - depositary.amount)), "Transfer failed");
                } else {
                    require(token.transferFrom(_msgSender(), address(this), (depositary.amount - vest.amount)), "Transfer failed");
                }
            }
        }

    }

    function adjustTranches(address holder, VestType vestType, uint256 tranche) external onlyPrivileged {
        require(vestType != VestType.NA, "Invalid vest");
        
        unchecked {
            vestTranches[holder][vestType] = tranche;
        }
    }

    /**
     * @dev See {IVestingManager-claimableBalance}.
     */
    function claimableBalance(address holder) public view virtual returns(uint256) {
        uint256 claimable = 0;
        for (uint8 i = 0;  i <= uint8(VestType.GRADED); i++) {
            claimable += claimableBalance(holder, VestType(i));
        }
        return claimable;
    }

    /**
     * @dev See {IVestingManager-claimableBalance}.
     */
    function claimableBalance(address holder, VestType vestType) public view virtual returns(uint256) {
        if (vestType == VestType.NA || (vestType == VestType.CLIFF) && (block.timestamp < unlockCliffTimestamp)) {
            return 0;
        }

        uint256 claimable = 0;
        uint256 claimed = claimedAmounts[holder][vestType];
        uint256 tranches = vestTranches[holder][vestType];

        for (uint256 i = 0; i <= tranches; i++) {
            Vest storage vest = lockedAmounts[holder][vestType][i];
            if (vestType == VestType.CLIFF) {
                if (block.timestamp >= endCliffTimestamp) {
                    claimable = vest.amount;
                } else {
                    claimable = (vest.amount * (block.timestamp - beginCliffTimestamp)) / (endCliffTimestamp - beginCliffTimestamp);
                }
            } else {
                if (vest.lockTimestamp > 0) {
                    uint256 unlockDeadline = vest.lockTimestamp + (vest.vestingPeriod * 24 * 60 * 60);
                    uint256 unlockCliff = vest.lockTimestamp + (vest.cliffPeriod > 0 ? (vest.cliffPeriod * 24 * 60 * 60) : unlockDeadline / 2);

                    if (block.timestamp >= unlockCliff) {
                        if (block.timestamp >= unlockDeadline) {
                            claimable += vest.amount;
                        } else {
                            uint256 unlockedAmount = vest.amount / 2;
                            claimable += unlockedAmount + (unlockedAmount * (block.timestamp - unlockCliff)) / (unlockDeadline - unlockCliff);
                        }
                    }
                }
            }
        }
        return claimable - claimed;
    }

    /**
     * @dev See {IVestingManager-claimableBalance}.
     */
    function lockedBalance(address holder) public view virtual returns(uint256) {
        uint256 locked = 0;

        for (uint8 i = 0; i <= uint8(VestType.GRADED); i++) {
            VestType vestType = VestType(i);
            uint256 tranches = vestTranches[holder][vestType];

            for (uint256 j = 0; j <= tranches; j++) {
                Vest storage vest = lockedAmounts[holder][vestType][j];
                locked += vest.amount;
            }

            locked -= claimedAmounts[holder][vestType];
        }
        return locked;
    }

    /**
     * @dev See {IVestingManager-lockedAmount}.
     */
    function lockedAmount(address holder, VestType vestType, uint256 tranche) external view returns(uint256) {
        Vest storage vest = lockedAmounts[holder][vestType][tranche];
        return vest.amount;
    }

    /**
     * @dev See {IVestingManager-lockedTranches}.
     */
    function lockedTranches(address holder, VestType vestType) public view virtual returns(uint256) {
        return vestTranches[holder][vestType];
    }

    /**
     * @dev See {IVestingManager-lock}.
     */
    function lock(Depositary memory depositary) public virtual onlyDaoToken {
        require(depositary.vestType != VestType.NA, "Invalid vest");

        if (depositary.vestType == VestType.CLIFF) {
            require(block.timestamp < unlockCliffTimestamp, "Cliff period already complete");
        } 

        uint256 tranche = ++vestTranches[depositary.wallet][depositary.vestType];

        Vest storage vest = lockedAmounts[depositary.wallet][depositary.vestType][tranche];
        require(vest.lockTimestamp == 0, "Vest already locked");

        vest.amount = depositary.amount;
        vest.lockTimestamp = block.timestamp;
        vest.cliffPeriod = depositary.cliffPeriod;
        vest.vestingPeriod = depositary.vestingPeriod;
        
        require(token.transferFrom(_msgSender(), address(this), depositary.amount), "Transfer failed");

        emit TokensLocked(_msgSender(), depositary.wallet, depositary.amount, tranche);
    }

    /**
     * @dev See {IVestingManager-claim}.
     */
    function claim(address recipient, uint256 amount, VestType vestType) public nonContract {
        require(claimTimestamps[recipient] + claimInterval < block.timestamp, "Claim not allowed");

        uint256 claimable = claimableBalance(_msgSender(), vestType);
        if (amount > claimable) {
            amount = claimable;
        }

        claimedAmounts[_msgSender()][vestType] += amount;
        claimTimestamps[recipient] = block.timestamp;

        require(token.transfer(recipient, amount), "Transfer failed");
        
        emit TokensClaimed(_msgSender(), recipient, amount);
    }

    /// @notice Sends any unclaimed tokens to the sweep reciever once the claiming period is over
    function sweep() external onlyPrivileged {
        require(block.timestamp >= endCliffTimestamp, "Vesting not ended");

        uint256 leftovers = token.balanceOf(address(this));
        require(leftovers != 0, "Nothing to sweep");

        require(token.transfer(sweepReceiver, leftovers), "Sweep failed");

        emit LeftoversSwept(sweepReceiver, leftovers);
    }

    /**
     * @dev Throws if called by any account other than the spacific contract.
     */
    modifier onlyDaoToken() {
        _checkDaoToken();
        _;
    }

    /**
     * @dev Throws if the sender is not the dao token.
     */
    function _checkDaoToken() internal view virtual {
        address _sender = _msgSender();
        require(_sender == address(token) || _isPrivileged(), "Caller is not privileged");
    }

    function _authorizeUpgrade(address newImplementation)
        internal
        virtual
        override
        onlyPrivileged
    {}


    /**
     * @dev This empty reserved space is put in place to allow future versions to add new
     * variables without shifting down storage in the inheritance chain.
     * See https://docs.openzeppelin.com/contracts/4.x/upgradeable#storage_gaps
     */
    uint256[50] private __gap;

}"
    },
    "contracts/interfaces/IVestingManager.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;

/**
 * @dev Interface for managing token vesting.
 *
 * @author zicodao.eth, rutilicus.eth
 */
interface IVestingManager {

    /**
     * @dev Enumeration defining vesting types.
     */
    enum VestType {
        NA,
        CLIFF,
        GRADED
    }

    /**
     * @dev Structure used to mint/deposit tokens.
     */
    struct Depositary {
        // The adres of target wallet
        address wallet;
        // The amount of tokens to mint
        uint256 amount;
        // Cliff period in days
        uint256 cliffPeriod;
        // Vesting period in days
        uint256 vestingPeriod;
        // Vesting type
        VestType vestType;
    }

    /**
     * @dev Structure used to hold vests
     */
    struct Vest {
        uint256 amount;
        // Vesting begin timestamp
        uint256 lockTimestamp;
        // Cliff period in days
        uint256 cliffPeriod;
        // Vesting period in days
        uint256 vestingPeriod;
    }

    /**
     * @dev Returns the number of tokens available to claim by a `holder` regardless vest type.
     * 
     * @param holder The account to check the claimable balance of.
     * 
     * @return The number of tokens available to claim.
     */
    function claimableBalance(address holder) external view returns(uint256);

    /**
     * @dev Returns the number of tokens available to claim by a `holder`.
     * 
     * @param holder The account to check the claimable balance of.
     * @param vestType The vest type to check for.
     * 
     * @return The number of tokens available to claim.
     */
    function claimableBalance(address holder, VestType vestType) external view returns(uint256);

    /**
     * @dev Returns the number of tokens locked to the `holder` regardless vest type.
     * 
     * @param holder The account to check the locked balance of.
     * 
     * @return The number of tokens locked
     */
    function lockedBalance(address holder) external view returns(uint256);

    /**
     * @dev Returns the number of tokens locked to the `holder` for a given vest type and tranche.
     * 
     * @param holder The account to check the locked balance of.
     * @param vestType The vest type to check for.
     * @param tranche The tranche number.
     * 
     * @return The number of tokens locked
     */
    function lockedAmount(address holder, VestType vestType, uint256 tranche) external view returns(uint256);

    /**
     * @dev Returns the number of tranches locked to the `holder`.
     * 
     * @param holder The account to check the number of tranches locked.
     * @param vestType The vest type to check for.
     * 
     * @return The number of tranches locked
     */
    function lockedTranches(address holder, VestType vestType) external view returns(uint256);

    /**
     * @dev Transfers tokens from the caller to the token lock contract and locks them for benefit of `recipient`.
     *      Requires that the caller has authorised this contract with the token contract.
     * 
     * @param depositary The depositary the tokens will be claimable by.
     */
    function lock(Depositary memory depositary) external;

    /**
     * @dev Transfers tokens from the caller to the token lock contract and locks them for benefit of `recipient`.
     *      Requires that the caller has authorised this contract with the token contract.
     * 
     * @param depositary The depositary the tokens will be claimable by.
     */
    function amendLock(Depositary memory depositary, uint256 tranche) external;

    /**
     * @dev Claims the caller's tokens that have been unlocked, sending them to `recipient`.
     * 
     * @param recipient The account to transfer unlocked tokens to.
     * @param amount The amount to transfer. If greater than the claimable amount, the maximum is transferred.
     * @param vestType The vest type to claim.
     */
    function claim(address recipient, uint256 amount, VestType vestType) external;
}"
    }
