    "/contracts/CustomLBLERC1155Rookie.sol": {
      "content": "// SPDX-License-Identifier: Apache-2.0
pragma solidity >=0.7.0 <0.9.0;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Counters.sol";
import "@openzeppelin/contracts/token/ERC1155/ERC1155.sol";
import "@openzeppelin/contracts/token/ERC1155/extensions/ERC1155Burnable.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "./ApprovalTicketStorage.sol";
import "./PlushieMintPriorityList2.sol";

contract CustomLBLERC1155Rookie is ERC1155, Ownable, ERC1155Burnable {

    struct TokenMetadata {
        uint256 tokenId;
        uint256 tokenType;
        string uri;
        address owner;
        address contractAddress;
        bool forSale;
        uint256 price;
    }

    struct ForSale {
        bool forSale;
        uint256 price;
    }

    using SafeMath for uint256;
    using Counters for Counters.Counter;
    Counters.Counter private _tokenIds;
    ApprovalTicketStorage private _ticketStorage;
    mapping (uint256 => ForSale) _saleData;
    mapping (uint256 => address) _owners;
    bool private _claimStopped;
    address private _breedingContract;

    constructor() ERC1155("https://storagenftsjson.blob.core.windows.net/jsonrookie/{id}.json"){
        _ticketStorage = ApprovalTicketStorage(0x53d8d8E6dbF263E83eDEedD505945c57eb3D1F20);
        _claimStopped = false;
    }

    function ascension(uint256 rookieId, address owner) public {
        require(super.balanceOf(owner, rookieId) == 1, "Only owner may ascend a Plushie.");
        require(msg.sender == _breedingContract, "Only specified breeding contract may ascend a Plushie.");

        _burn(owner, rookieId, 1);
    }


    function updateBreedingContract(address newBreedingContract) onlyOwner public {
        _breedingContract = newBreedingContract;
    }

    function breedingContract() public view returns (address) {
        return _breedingContract;
    }

    function getTokenId() public view returns (uint256) {
        return _tokenIds.current();
    }

    function claimNft(bytes32 ticket, bytes memory signature) public {
        require(!_claimStopped, "Claim not available now.");
        _ticketStorage.useTicket(ticket, signature);

        uint256 newItemId = _tokenIds.current();

        _mint(msg.sender, newItemId, 1, "");
        _owners[newItemId] = msg.sender;

        _tokenIds.increment();
    }

    function setNFTForSale(uint256 _tokenId, uint256 _price) public {
        require(_tokenIds.current() > _tokenId, "Token doesn`t exists.");
        require(_owners[_tokenId] == msg.sender, "Only the token owner can put it for sale.");
        require(!_saleData[_tokenId].forSale, "Token already on sale.");
        _saleData[_tokenId].forSale = true;
        _saleData[_tokenId].price = _price;
    }

    function cancelNFTSale(uint256 _tokenId) public {
        require(_tokenIds.current() > _tokenId, "Token doesn`t exists.");
        require(_owners[_tokenId] == msg.sender, "Only the token owner can cancel the sale.");
        require(_saleData[_tokenId].forSale, "Token not on sale.");
        _saleData[_tokenId].forSale = false;
        _saleData[_tokenId].price = 0;
    }

    function buyNFT(uint256 _tokenId) public payable {
        require(_saleData[_tokenId].forSale, "Token not on sale.");
        require(msg.value == _saleData[_tokenId].price, "Sent value must be exactly the buy price.");
        require(_owners[_tokenId] != msg.sender, "This is your token cancel the sale instead of buying.");

        _safeTransferFrom(_owners[_tokenId], msg.sender, _tokenId, 1, "");

        payable(_owners[_tokenId]).transfer(msg.value);

        _owners[_tokenId] = msg.sender;
        _saleData[_tokenId].forSale = false;
        _saleData[_tokenId].price = 0;
    }

    function safeTransferFrom(
        address from,
        address to,
        uint256 id,
        uint256 amount,
        bytes memory data
    ) public virtual override {
        super.safeTransferFrom(from, to, id, amount, data);
        _owners[id] = to;
    }

    function uri(uint256 _tokenId) override public pure returns (string memory) {
        return string(
            abi.encodePacked(
                "https://storagenftsjson.blob.core.windows.net/jsonrookie/", Strings.toString(_tokenId), ".json")
        );
    }

    function stopClaim() public onlyOwner{
        _claimStopped = true;
    }

    function restartClaim() public onlyOwner {
        _claimStopped = false;
    }

    function getAllData() public view returns (TokenMetadata[] memory) {
        TokenMetadata[] memory tokenMetadata = new TokenMetadata[](_tokenIds.current());
        for (uint256 i = 0; i < _tokenIds.current(); i++) {
            tokenMetadata[i] = TokenMetadata({
                tokenId : i,
                tokenType : 2,
                uri : uri(i),
                owner : _owners[i],
                contractAddress : address(this),
                forSale : _saleData[i].forSale,
                price : _saleData[i].price
            });
        }

        return tokenMetadata;
    }

}
"
    },
    "/contracts/PlushieMintPriorityList2.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";

contract PlushieMintPriorityList2 is Ownable {

    mapping(uint256 => mapping(address => bool)) private _priorityLists;
    uint256 private _totalLists;
    mapping(uint256 => uint256) _listsTotalParticipants;
    mapping(address => bool) _boughtAPlushie;
    uint256 private _totalPlushiesBought;
    uint256 private _currentList;
    bool private _onPriorityList;
    bool private _stopDescount;

    constructor(){
        _onPriorityList = true;
    }

    function addParticipantToPriorityList(address[] memory participant, uint256 listId) public {
        for (uint256 i = 0; i < participant.length; i++) {
            if(listId > _totalLists) {
                require(listId == _totalLists + 1, "You can only increment the lists by one.");
                _totalLists += 1;
            }
            _priorityLists[listId][participant[i]] = true;
            _listsTotalParticipants[listId] += 1;
        }
    }

    function plushiePrice(address participant) public view returns (uint256) {
        if (_stopDescount) {
            return 123 ether;
        }
        if (_priorityLists[0][participant]) {
            return 71 ether;
        }
        else if (_priorityLists[1][participant]) {
            return 92 ether;
        }

        return 123 ether;
    }

    function boughtAPlushie(address participant) public {
        require(_canBuyAPlushie(participant), "Participant cannot buy a plushie at the moment.");
        _boughtAPlushie[participant] = true;
        _totalPlushiesBought += 1;
    }

    function _canBuyAPlushie(address participant) view internal returns (bool) {
        if (_onPriorityList) {
            for(uint256 count = 0; count <= _currentList; count++) {
                if (_priorityLists[count][participant])
                    return true;
            }
            return false;
        }

        return true;
    }

    function stopDiscount() public {
        _stopDescount = ! _stopDescount;
    }

    function setCurrentList(uint256 currentList) public {
        _currentList = currentList;
    }

    function changePriorityList() public {
        _onPriorityList = !_onPriorityList;
    }

}
"
    },
    "/contracts/ApprovalTicketStorage.sol": {
      "content": "// SPDX-License-Identifier: Apache-2.0
pragma solidity >=0.7.0 <0.9.0;

import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";

contract ApprovalTicketStorage {
    using ECDSA for bytes32;

    mapping(bytes32 => bool) internal usedTickets;
    address private _signer;

    constructor (address signer) {
        _signer = signer;
    }

    function ticketUsed(bytes32 ticket) public view returns (bool) {
        return usedTickets[keccak256(abi.encodePacked(ticket))];
    }

    function useTicket(bytes32 ticket, bytes memory signature) public {
        require(!ticketUsed(ticket), "Ticket already used.");
        require(ticket.toEthSignedMessageHash().recover(signature) == _signer, "ExternallySigned: not the correct signature.");
        usedTickets[keccak256(abi.encodePacked(ticket))] = true;
    }
}
"
    },
