    "contracts/BrokerAccepter.sol": {
      "content": "// SPDX-License-Identifier: MIT OR Apache-2.0

pragma solidity ^0.8.0;

import '@openzeppelin/contracts/access/AccessControlDefaultAdminRules.sol';
import '@openzeppelin/contracts/utils/Multicall.sol';
import '@openzeppelin/contracts/token/ERC20/IERC20.sol';
import 'zklink-contracts-interface/contracts/IZkLink.sol';
import '@openzeppelin/contracts/security/ReentrancyGuard.sol';
import '@openzeppelin/contracts/utils/cryptography/ECDSA.sol';

contract BrokerAccepter is
  AccessControlDefaultAdminRules,
  Multicall,
  ReentrancyGuard
{
  bytes32 public constant WITNESS_ROLE = keccak256('WITNESS_ROLE');
  bytes32 public constant BROKER_ROLE = keccak256('BROKER_ROLE');
  bytes32 public constant FUND_ROLE = keccak256('FUND_ROLE');

  bytes4 public constant ACCEPT_ERC20 =
    bytes4(
      keccak256(
        bytes(
          'acceptERC20(address,uint32,address,uint16,uint128,uint16,uint32,uint8,uint32,uint128)'
        )
      )
    );
  bytes4 public constant ACCEPT_ETH =
    bytes4(
      keccak256(
        bytes(
          'acceptETH(address,uint32,address,uint128,uint16,uint32,uint8,uint32)'
        )
      )
    );

  IZkLink public zkLinkInstance;

  event AcceptStatus(bool success, bytes errorInfo);
  event CallStatus(bool success, bytes errorInfo);

  constructor(
    IZkLink _zkLinkInstance,
    uint48 initialDelay
  ) AccessControlDefaultAdminRules(initialDelay, _msgSender()) {
    zkLinkInstance = _zkLinkInstance;
  }

  receive() external payable {}

  modifier mustFundRole() {
    require(
      hasRole(DEFAULT_ADMIN_ROLE, _msgSender()) ||
        hasRole(FUND_ROLE, _msgSender()),
      'must fund role'
    );
    _;
  }

  function grantWitnessRole(address account) external {
    grantRole(WITNESS_ROLE, account);
  }

  function grantBrokerRole(address account) external {
    grantRole(BROKER_ROLE, account);
  }

  function grantFundRole(address account) external {
    grantRole(FUND_ROLE, account);
  }

  function changeZkLinkInstance(
    IZkLink newAddress
  ) external onlyRole(DEFAULT_ADMIN_ROLE) {
    zkLinkInstance = newAddress;
  }

  function acceptERC20(
    bytes calldata data,
    bytes calldata signature
  ) external onlyRole(BROKER_ROLE) {
    require(bytes4(data) == ACCEPT_ERC20, 'func error erc20');
    address witnessAddress = ECDSA.recover(keccak256(data), signature);
    _checkRole(WITNESS_ROLE, witnessAddress); // if not witness, will revert
    (bool success, bytes memory errorInfo) = address(zkLinkInstance).call(data);
    emit AcceptStatus(success, errorInfo);
  }

  function batchAcceptERC20(
    bytes[] calldata data,
    bytes calldata signature
  ) external onlyRole(BROKER_ROLE) {
    bool success;
    bytes memory errorInfo;
    bytes memory digest = data[0];
    (success, errorInfo) = address(zkLinkInstance).call(data[0]);
    emit AcceptStatus(success, errorInfo);
    require(bytes4(data[0]) == ACCEPT_ERC20, 'func error erc20');
    for (uint8 i = 1; i < data.length; i++) {
      digest = abi.encodePacked(digest, data[i]);
      (success, errorInfo) = address(zkLinkInstance).call(data[i]);
      emit AcceptStatus(success, errorInfo);
      require(bytes4(data[i]) == ACCEPT_ERC20, 'func error erc20');
    }
    address witnessAddress = ECDSA.recover(keccak256(digest), signature);
    _checkRole(WITNESS_ROLE, witnessAddress); // if not witness, will revert
  }

  function acceptETH(
    bytes calldata data,
    uint128 amount,
    bytes calldata signature
  ) external onlyRole(BROKER_ROLE) {
    require(bytes4(data) == ACCEPT_ETH, 'func error eth');
    address witnessAddress = ECDSA.recover(keccak256(data), signature);
    _checkRole(WITNESS_ROLE, witnessAddress); // if not witness, will revert
    //amount * (MAX_ACCEPT_FEE_RATE - withdrawFeeRate) / MAX_ACCEPT_FEE_RATE;
    (bool success, bytes memory errorInfo) = address(zkLinkInstance).call{
      value: amount
    }(data);
    emit AcceptStatus(success, errorInfo);
  }

  function batchAcceptETH(
    bytes[] calldata data,
    uint128[] calldata amounts,
    bytes calldata signature
  ) external onlyRole(BROKER_ROLE) {
    bool success;
    bytes memory errorInfo;
    bytes memory digest = data[0];
    (success, errorInfo) = address(zkLinkInstance).call{value: amounts[0]}(
      data[0]
    );
    emit AcceptStatus(success, errorInfo);
    require(bytes4(data[0]) == ACCEPT_ETH, 'func error eth');

    for (uint i = 1; i < data.length; i++) {
      digest = abi.encodePacked(digest, data[i]);
      (success, errorInfo) = address(zkLinkInstance).call{value: amounts[i]}(
        data[i]
      );
      emit AcceptStatus(success, errorInfo);
      require(bytes4(data[i]) == ACCEPT_ETH, 'func error eth');
    }
    address witnessAddress = ECDSA.recover(keccak256(digest), signature);
    _checkRole(WITNESS_ROLE, witnessAddress);
  }

  function batchAccept(
    bytes[] calldata data,
    uint128[] calldata amounts,
    bytes calldata signature
  ) external onlyRole(BROKER_ROLE) {
    bool success;
    bytes memory errorInfo;
    bytes memory digest = data[0];
    (success, errorInfo) = address(zkLinkInstance).call{value: amounts[0]}(
      data[0]
    );
    emit AcceptStatus(success, errorInfo);
    bytes4 funcSig = bytes4(data[0]);
    require(funcSig == ACCEPT_ETH || funcSig == ACCEPT_ERC20, 'func error eth');
    for (uint i = 1; i < data.length; i++) {
      digest = abi.encodePacked(digest, data[i]);
      (success, errorInfo) = address(zkLinkInstance).call{value: amounts[i]}(
        data[i]
      );
      emit AcceptStatus(success, errorInfo);
      funcSig = bytes4(data[i]);
      require(
        funcSig == ACCEPT_ETH || funcSig == ACCEPT_ERC20,
        'func error eth'
      );
    }
    address witnessAddress = ECDSA.recover(keccak256(digest), signature);
    _checkRole(WITNESS_ROLE, witnessAddress);
  }

  // Dynamic call function
  function dynamicCall(
    address payable contractAddress,
    bytes calldata data
  ) external payable nonReentrant onlyRole(DEFAULT_ADMIN_ROLE) {
    (bool success, bytes memory errorInfo) = contractAddress.call{
      value: msg.value
    }(data);
    emit CallStatus(success, errorInfo);
  }

  function withdraw(
    IERC20 token, // Token address
    address to, // Address to withdraw to
    uint256 amount // Amount to withdraw
  ) external nonReentrant mustFundRole returns (bool) {
    return token.transfer(to, amount);
  }

  function approve(
    address contractAddress,
    address spender,
    uint256 amount
  ) internal returns (bool) {
    return IERC20(contractAddress).approve(spender, amount);
  }

  function approveZkLink(
    address contractAddress,
    uint256 amount
  ) external nonReentrant mustFundRole returns (bool) {
    return approve(contractAddress, address(zkLinkInstance), amount);
  }

  function withdrawETH(
    address to,
    uint256 amount
  ) external nonReentrant mustFundRole returns (bool res) {
    (res, ) = to.call{value: amount}(new bytes(0));
  }
}
"
    },
    "zklink-contracts-interface/contracts/IZkLink.sol": {
      "content": "// SPDX-License-Identifier: MIT OR Apache-2.0

pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

/// @title ZkLink interface contract
/// @author zk.link
interface IZkLink {
    // stored block info of ZkLink
    struct StoredBlockInfo {
        uint32 blockNumber;
        uint64 priorityOperations;
        bytes32 pendingOnchainOperationsHash;
        uint256 timestamp;
        bytes32 stateHash;
        bytes32 commitment;
        bytes32 syncHash;
    }

    /// @return Return the accept allowance of broker
    function brokerAllowance(
        uint16 tokenId,
        address acceptor,
        address broker
    ) external view returns (uint128);

    /// @notice Give allowance to broker to call accept
    /// @param tokenId token that transfer to the receiver of accept request from accepter or broker
    /// @param broker who are allowed to do accept by accepter(the msg.sender)
    /// @param amount the accept allowance of broker
    function brokerApprove(
        uint16 tokenId,
        address broker,
        uint128 amount
    ) external returns (bool);

    /// @notice A map of token address to id, 0 is invalid token id
    function tokenIds(
        address tokenAddress
    ) external view returns (uint16 tokenId);

    /// @notice Get synchronized progress of zkLink contract known on deployed chain
    function getSynchronizedProgress(
        StoredBlockInfo memory block
    ) external view returns (uint256 progress);

    /// @notice Combine the `progress` of the other chains of a `syncHash` with self
    function receiveSynchronizationProgress(
        bytes32 syncHash,
        uint256 progress
    ) external;

    /// @notice Deposit ETH to Layer 2 - transfer ether from user into contract, validate it, register deposit
    /// @param _zkLinkAddress The receiver Layer 2 address
    /// @param _subAccountId The receiver sub account
    function depositETH(
        bytes32 _zkLinkAddress,
        uint8 _subAccountId
    ) external payable;

    /// @notice Deposit ERC20 token to Layer 2 - transfer ERC20 tokens from user into contract, validate it, register deposit
    /// @dev it MUST be ok to call other external functions within from this function
    /// when the token(eg. erc777) is not a pure erc20 token
    /// @param _token Token address
    /// @param _amount Token amount
    /// @param _zkLinkAddress The receiver Layer 2 address
    /// @param _subAccountId The receiver sub account
    /// @param _mapping If true and token has a mapping token, user will receive mapping token at l2
    function depositERC20(
        IERC20 _token,
        uint104 _amount,
        bytes32 _zkLinkAddress,
        uint8 _subAccountId,
        bool _mapping
    ) external;

    /// @notice Acceptor accept a eth fast withdraw, acceptor will get a fee for profit
    /// @param acceptor Acceptor who accept a fast withdraw
    /// @param accountId Account that request fast withdraw
    /// @param receiver User receive token from acceptor (the owner of withdraw operation)
    /// @param amount The amount of withdraw operation
    /// @param withdrawFeeRate Fast withdraw fee rate taken by acceptor
    /// @param accountIdOfNonce Account that supply nonce, may be different from accountId
    /// @param subAccountIdOfNonce SubAccount that supply nonce
    /// @param nonce Account nonce, used to produce unique accept info
    function acceptETH(
        address acceptor,
        uint32 accountId,
        address payable receiver,
        uint128 amount,
        uint16 withdrawFeeRate,
        uint32 accountIdOfNonce,
        uint8 subAccountIdOfNonce,
        uint32 nonce
    ) external payable;

    /// @notice Acceptor accept a erc20 token fast withdraw, acceptor will get a fee for profit
    /// @param acceptor Acceptor who accept a fast withdraw
    /// @param accountId Account that request fast withdraw
    /// @param receiver User receive token from acceptor (the owner of withdraw operation)
    /// @param tokenId Token id
    /// @param amount The amount of withdraw operation
    /// @param withdrawFeeRate Fast withdraw fee rate taken by acceptor
    /// @param accountIdOfNonce Account that supply nonce, may be different from accountId
    /// @param subAccountIdOfNonce SubAccount that supply nonce
    /// @param nonce Account nonce, used to produce unique accept info
    /// @param amountTransfer Amount that transfer from acceptor to receiver
    /// may be a litter larger than the amount receiver received
    function acceptERC20(
        address acceptor,
        uint32 accountId,
        address receiver,
        uint16 tokenId,
        uint128 amount,
        uint16 withdrawFeeRate,
        uint32 accountIdOfNonce,
        uint8 subAccountIdOfNonce,
        uint32 nonce,
        uint128 amountTransfer
    ) external;

    /// @notice  Withdraws tokens from zkLink contract to the owner
    /// @param _owner Address of the tokens owner
    /// @param _tokenId Token id
    /// @param _amount Amount to withdraw to request.
    /// @return The actual withdrawn amount
    /// @dev NOTE: We will call ERC20.transfer(.., _amount), but if according to internal logic of ERC20 token zkLink contract
    /// balance will be decreased by value more then _amount we will try to subtract this value from user pending balance
    function withdrawPendingBalance(
        address payable _owner,
        uint16 _tokenId,
        uint128 _amount
    ) external returns (uint128);
}
"
