      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Address.sol";
import './BokkyPooBahsDateTimeLibrary.sol';

/**
 * @title Token contract for the MeebitsDAO (mVOX) governance token.
 * @author maikir
 * @notice This contract allows the minting and exchange of MeebitsDAO governance tokens.
 *
 */
contract MeebitsDAOGovernanceToken is
    ERC20,
    Ownable
{
    using ECDSA for bytes32;
    using Address for address;

    // Signer for restricted function calling
    address private signVerifier;

    /**
     * @dev Mapping of MeebitsDAO General Members to last month mVOX was minted
     */
    mapping (address => uint256) public lastMintMonth;

    /**
     * @notice The constructor for the MVOX token with signVerifier
     */
    constructor(
      address _signVerifier
    ) ERC20("MeebitsDAOGovernanceToken", "mVOX") {
        signVerifier = _signVerifier;
    }

    /**
     * @dev Sets decimals to 0
     */
    function decimals() public view virtual override returns (uint8) {
      return 0;
    }

    /**
     * @dev Sets a new signature verifier
     */
    function setSignVerifier(address verifier) external onlyOwner {
      signVerifier = verifier;
    }

    /**
     * @dev Function for creating a valid digital signature
     */
    function getSigningHash(
        address recipient
    )
        public
        pure
        returns (bytes32)
    {
        return
          keccak256(
            abi.encodePacked(
              recipient
            )
          );
    }

    /**
     * @dev Check eligibility of monthly mint for address
     * @param _address to check elegibility for
     */
    function isEligibleMonthlyMint(
        address _address,
        uint256 currentMonth
    )
        public
        view
        returns (bool)
    {
        if ((lastMintMonth[_address] == 0) || (currentMonth != lastMintMonth[_address])) {
            return true;
        } else {
            return false;
        }
    }

    // ---------- Minting ----------

    /**
     * @dev A method to allow a general member to claim (1) MVOX token a month.
     * @param sig Signature for verifying
     */
    function mintMonthlyToken(
        bytes memory sig
    )
        external
    {
        // Verify signature
        bytes32 message = getSigningHash(
          msg.sender
        ).toEthSignedMessageHash();
        require(
          ECDSA.recover(message, sig) == signVerifier,
          "Caller does not have permission to use this function"
        );

        //BokkyPooBahsDateTimeLibrary returns a month between 1 - 12
        uint256 currentMonth = BokkyPooBahsDateTimeLibrary.getMonth(block.timestamp);
        require(
            isEligibleMonthlyMint(msg.sender, currentMonth),
            "Caller has already minted a governance token this month"
        );

        _mint(msg.sender, 1);
        lastMintMonth[msg.sender] = currentMonth;
    }

    /**
     * @dev Mint monthly token given the caller is eligible to mint
     */
    function backupMint(address _address, uint256 numTokens)
        external
        onlyOwner
    {
        //BokkyPooBahsDateTimeLibrary returns a month between 1 - 12
        uint256 currentMonth = BokkyPooBahsDateTimeLibrary.getMonth(block.timestamp);
        _mint(_address, numTokens);
        lastMintMonth[_address] = currentMonth;
    }
}
"
