import "ERC1155.sol";
import "SafeMath.sol";
import "ECDSA.sol";
import "Ownable.sol";

contract LuckyCharmPoly is ERC1155(""), Ownable {
	using ECDSA for bytes32;

    uint256 constant SHARDS = 0;
    address constant BURN_ADDRESS = 0x000000000000000000000000000000000000dEaD;
	address public constant MINT_ADDRESS = 0xA6FA4fB5f76172d178d61B04b0ecd319C5d1C0aa;


    uint256 public charmReq = 100 ether;
    uint256 public legChance = 10;
	uint256 public rareChance = 250;

    uint256 claims;
	mapping(uint256 => uint256) private _supplyOf;
	mapping(address => bool) public kong;
    mapping(uint256 => string) public uris;

	modifier onlyMinter() {
		require(msg.sender == MINT_ADDRESS, "!minter");
		_;
	}

	function uri(uint256 _id) external view override returns (string memory) {
        return uris[_id];
    }

	function setUri(uint256 _id, string memory _uri) external onlyOwner {
		uris[_id] = _uri;
	}

    function updateCharmReq(uint256 _newReq) external onlyOwner {
		require(_newReq > 0, "!0");
		charmReq = _newReq;
	}

	function updateLegChance(uint256 _chance) external onlyOwner {
		require(_chance < rareChance, "!leg");
		legChance = _chance;
	}

	function updateRareChance(uint256 _chance) external onlyOwner {
		require(_chance > legChance, "!rare");
		rareChance = _chance;
	}


	function deposit(address user, bytes calldata depositData) external onlyMinter {
        (
            uint256[] memory ids,
            uint256[] memory amounts,
            bytes memory data
        ) = abi.decode(depositData, (uint256[], uint256[], bytes));
        require(user != address(0x0), "ChildERC1155: INVALID_DEPOSIT_USER");
        _mintBatch(user, ids, amounts, data);
    }



	function discoverCharms(uint256 _amount) external {
		require(kong[msg.sender], "!kong");

		uint256[3] memory charmDistro;
		uint256 rand;
		uint256 toBurn = _amount * charmReq;
		uint256 rare = rareChance;
		uint256 leg = legChance;

        _balances[0][msg.sender] = _balances[0][msg.sender].sub(toBurn, "ERC1155: insufficient balance for transfer");
        _balances[0][BURN_ADDRESS] = _balances[0][BURN_ADDRESS].add(toBurn);

        emit TransferSingle(address(this), msg.sender, BURN_ADDRESS, 0, toBurn);
		for (uint256 i = 0; i < _amount; i++) {
			rand = getRng(rand);
			charmDistro[charmOutcome(rand, rare, leg)]++;
		}
		for (uint256 i = 0; i < 3; i ++) {
			if (charmDistro[i] > 0) {
				_mint(msg.sender, i + 1, charmDistro[i], "");
				_supplyOf[i + 1] = _supplyOf[i + 1].add(charmDistro[i]);
			}
		}
		claims++;
	}

	function getRng(uint256 _seed) internal view returns(uint256 rand) {
		if (_seed == 0)
			rand = uint256(keccak256(abi.encodePacked(msg.sender, block.difficulty, block.timestamp, claims)));
		else
			rand = uint256(keccak256(abi.encodePacked(_seed)));
	}

	function charmOutcome(uint256 _seed, uint256 _rare, uint256 _leg) internal returns (uint256) {
		uint256 rng = _seed % 1000;
		if (rng < _leg)
			return 2;
		else if (rng < _rare)
			return 1;
		else
			return 0;
	}

	function meKong(string calldata _msg, bytes calldata _sig) external {
		require(keccak256(abi.encodePacked(_msg)).toEthSignedMessageHash().recover(_sig) == msg.sender, "Sig not valid");
		kong[msg.sender] = true;
	}

	function totalSupply(uint256 _id) external view returns(uint256) {
		return _supplyOf[_id] - balanceOf(BURN_ADDRESS, _id);
	}

	function safeTransferFrom(
        address from,
        address to,
        uint256 id,
        uint256 amount,
        bytes memory data
    )
        public
        virtual
        override
    {
		require(id != 0, "!shards");
        super.safeTransferFrom(from, to, id, amount, data);
    }

    function safeBatchTransferFrom(
        address from,
        address to,
        uint256[] memory ids,
        uint256[] memory amounts,
        bytes memory data
    )
        public
        virtual
        override
    {
		for (uint256 i = 0; i < ids.length; i++)
			require(ids[i] != 0, "!shards");
        super.safeBatchTransferFrom(from, to, ids, amounts, data);
	}    

	/**
     * @notice called when user wants to withdraw single token back to root chain
     * @dev Should burn user's tokens. This transaction will be verified when exiting on root chain
     * @param id id to withdraw
     * @param amount amount to withdraw
     */
    function withdrawSingle(uint256 id, uint256 amount) external {
        _burn(_msgSender(), id, amount);
    }

    /**
     * @notice called when user wants to batch withdraw tokens back to root chain
     * @dev Should burn user's tokens. This transaction will be verified when exiting on root chain
     * @param ids ids to withdraw
     * @param amounts amounts to withdraw
     */
    function withdrawBatch(uint256[] calldata ids, uint256[] calldata amounts)
        external
    {
        _burnBatch(_msgSender(), ids, amounts);
    }
}"
