    function mintNextToken(address _owner, uint256 _tokenId)
        external
        returns (bool);

    function totalSupply() external view returns (uint256);

    function cap() external view returns (uint256);

    function balanceOf(address _owner) external view returns (uint256);
}

contract Blockchain_Superheroes_Distributor is BasicAccessControl {
    using SafeERC20 for IERC20;

    address public blockchainSuperheroes;
    IERC20 public cSushi;

    constructor(address _blockchainSuperheroes, address _cSushi) {
        blockchainSuperheroes = _blockchainSuperheroes;
        cSushi = IERC20(_cSushi);
    }

    uint256 public mintingCount = 1000001999;

    uint256 public constant MINTING_CAP = 1000004999;
    // 100 Sushi per NFT.
    uint256 public constant TOKEN_PRICE = 100 * 10**18;

    mapping(address => bool) public whitelist1;
    mapping(address => bool) public whitelist2;

    bool public whitelist1Enabled = false;
    bool public whitelist2Enabled = false;
    uint256 public onWhiteList2max = 1;
    uint256 public onWhiteList2mints = 0;

    bool public _mintingPaused = true;

    function setContracts(address _blockchainSuperheroes, address _cSushi)
        external
        onlyOwner
    {
        blockchainSuperheroes = _blockchainSuperheroes;
        cSushi = IERC20(_cSushi);
    }

    function getBalance(address _owner) public view returns (uint256) {
        IBlockchain_Superheroes blockchainSuperherosContract = IBlockchain_Superheroes(
                blockchainSuperheroes
            );

        return blockchainSuperherosContract.balanceOf(_owner);
    }

    function mint() public returns (bool) {
        IBlockchain_Superheroes blockchainSuperherosContract = IBlockchain_Superheroes(
                blockchainSuperheroes
            );

        uint256 cSushiBalance = cSushi.balanceOf(msg.sender);

        require(cSushiBalance >= TOKEN_PRICE, "Insufficient amount provided");
        require(!_mintingPaused, "Minting paused");
        mintingCount++;
        require(mintingCount <= MINTING_CAP, "Minting cap reached");

        require(
            (whitelist1Enabled && whitelist1[msg.sender]) ||
                ((whitelist2Enabled && whitelist2[msg.sender])) ||
                (!whitelist1Enabled && !whitelist2Enabled),
            "ONLY WHITELIST"
        );

        // if on whitelist 2 and cap not reached
        require(
            (!whitelist2Enabled) ||
                (((whitelist1Enabled && whitelist1[msg.sender])) ||
                    (whitelist2Enabled &&
                        (whitelist2[msg.sender]) &&
                        (onWhiteList2max > onWhiteList2mints + 1))),
            "WHITELIST 2 cap reached"
        );

        if (
            !whitelist2Enabled ||
            (((whitelist1Enabled && whitelist1[msg.sender])) ||
                (whitelist2Enabled &&
                    (whitelist2[msg.sender]) &&
                    (onWhiteList2max > onWhiteList2mints + 1)))
        ) {
            onWhiteList2mints = onWhiteList2mints + 1;
        }

        cSushi.safeTransferFrom(msg.sender, address(this), TOKEN_PRICE);
        bool minted = blockchainSuperherosContract.mintNextToken(
            msg.sender,
            mintingCount
        );
        return minted;
    }

    function mintTo(address _owner) public onlyOwner returns (bool) {
        IBlockchain_Superheroes blockchainSuperherosContract = IBlockchain_Superheroes(
                blockchainSuperheroes
            );

        require(!_mintingPaused, "Minting paused");
        mintingCount++;
        require(mintingCount <= MINTING_CAP, "Minting cap reached");

        require(
            (whitelist1Enabled && whitelist1[_owner]) ||
                ((whitelist2Enabled && whitelist2[_owner])) ||
                (!whitelist1Enabled && !whitelist2Enabled),
            "ONLY WHITELIST"
        );

        // if on whitelist 2 and cap not reached
        require(
            (!whitelist2Enabled) ||
                (((whitelist1Enabled && whitelist1[_owner])) ||
                    (whitelist2Enabled &&
                        (whitelist2[_owner]) &&
                        (onWhiteList2max > onWhiteList2mints + 1))),
            "WHITELIST 2 cap reached"
        );

        if (
            !whitelist2Enabled ||
            (((whitelist1Enabled && whitelist1[_owner])) ||
                (whitelist2Enabled &&
                    (whitelist2[_owner]) &&
                    (onWhiteList2max > onWhiteList2mints + 1)))
        ) {
            onWhiteList2mints = onWhiteList2mints + 1;
        }

        bool minted = blockchainSuperherosContract.mintNextToken(
            msg.sender,
            mintingCount
        );

        return minted;
    }

    fallback() external payable {}

    receive() external payable {}

    function togglePause(bool _pause) public onlyOwner {
        require(_mintingPaused != _pause, "Already in desired pause state");
        _mintingPaused = _pause;
    }

    function adminWithdraw() external onlyOwner {
        uint256 amount = cSushi.balanceOf(address(this));
        cSushi.safeTransfer(msg.sender, amount);
    }

    // whitelist 1
    uint256 onWhitelist1Count = 0;

    function addToWhiteList1(address[] calldata entries) external onlyOwner {
        for (uint256 i = 0; i < entries.length; i++) {
            address entry = entries[i];
            require(entry != address(0), "Cannot add zero address");
            require(!whitelist1[entry], "Cannot add duplicate address");

            whitelist1[entry] = true;
            onWhitelist1Count++;
        }
    }

    function removeFromWhiteList1(address[] calldata entries)
        external
        onlyOwner
    {
        for (uint256 i = 0; i < entries.length; i++) {
            address entry = entries[i];
            require(entry != address(0), "Cannot remove zero address");

            whitelist1[entry] = false;
            onWhitelist1Count--;
        }
    }

    function toggleWhiteList1() external onlyOwner {
        whitelist1Enabled = !whitelist1Enabled;
    }

    function isOnWhiteList1(address addr) external view returns (bool) {
        return whitelist1[addr];
    }

    function totalOnWhitelist1() external view returns (uint256) {
        return onWhitelist1Count;
    }

    // whitelist 2
    uint256 onWhitelist2Count = 0;

    function addToWhiteList2() external onlyOwner {
        require(
            !whitelist2[msg.sender],
            "Cannot add duplicate address to whitelist 2"
        );

        whitelist2[msg.sender] = true;
        onWhitelist2Count++;
    }

    function removeFromWhiteList2(address[] calldata entries)
        external
        onlyOwner
    {
        for (uint256 i = 0; i < entries.length; i++) {
            address entry = entries[i];
            require(entry != address(0), "Cannot remove zero address");

            whitelist2[entry] = false;
            onWhitelist2Count--;
        }
    }

    function toggleWhiteList2() external onlyOwner {
        whitelist2Enabled = !whitelist2Enabled;
    }

    function isOnWhiteList2(address addr) external view returns (bool) {
        return whitelist2[addr];
    }

    function totalOnWhitelist2() external view returns (uint256) {
        return onWhitelist2Count;
    }

    function setWhiteList2Cap(uint256 _onWhiteList2cap) external onlyOwner {
        onWhiteList2max = _onWhiteList2cap;
    }
}
