// File: MarketPlace.sol


pragma solidity ^0.8.17;





error PriceNotMet(address nftAddress, uint256 tokenId, uint256 price);
error ItemNotForSale(address nftAddress, uint256 tokenId);
error NotListed(address nftAddress, uint256 tokenId);
error AlreadyListed(address nftAddress, uint256 tokenId);
error NoProceeds();
error NotOwner();
error NotApprovedForMarketplace();
error PriceMustBeAboveZero();

contract NftMarketplace is ReentrancyGuard, Ownable {
    //for 10% == 1000 bps
    uint256 public feeBps;
    address public beneficiary;
    IERC20 trc;

    constructor(uint256 _fee, address _trc) {
        feeBps = _fee;
        beneficiary = owner();
        trc = IERC20(_trc);
    }

    struct Listing {
        uint256 price;
        address seller;
        bool isTrc;
    }

   event ItemListed(
        address indexed seller,
        address indexed nftAddress,
        uint256 indexed tokenId,
        uint256 price
    );

    function changeFeeBps(uint256 _fee) public onlyOwner {
        feeBps = _fee;
    }

    function changeBeneficiary(address _beneficiary) public onlyOwner {
        beneficiary = _beneficiary;
    }

   // State Variables
   mapping(address => mapping(uint256 => Listing)) private s_listings;

   event ItemCanceled(
        address indexed seller,
        address indexed nftAddress,
        uint256 indexed tokenId
    );
    
    event ItemBought(
        address indexed buyer,
        address indexed nftAddress,
        uint256 indexed tokenId,
        uint256 price
    );

   // Function modifiers
   modifier notListed(
        address nftAddress,
        uint256 tokenId,
        address owner
   ) {
        Listing memory listing = s_listings[nftAddress][tokenId];
        if (listing.price > 0) {
            revert AlreadyListed(nftAddress, tokenId);
        }
        _;
    }

    modifier isListed(address nftAddress, uint256 tokenId) {
        Listing memory listing = s_listings[nftAddress][tokenId];
        if (listing.price <= 0) {
            revert NotListed(nftAddress, tokenId);
        }
        _;
    }

    modifier isOwner(
        address nftAddress,
        uint256 tokenId,
        address spender
    ) {
        IERC721 nft = IERC721(nftAddress);
        address owner = nft.ownerOf(tokenId);
        if (spender != owner) {
            revert NotOwner();
        }
        _;
    }


    function listItem(
        address nftAddress,
        uint256 tokenId,
        uint256 price
    )
        external
        notListed(nftAddress, tokenId, msg.sender)
        isOwner(nftAddress, tokenId, msg.sender)
    {
        if (price <= 0) {
            revert PriceMustBeAboveZero();
        }
        IERC721 nft = IERC721(nftAddress);
        if (!nft.isApprovedForAll(msg.sender, address(this))) {
            revert NotApprovedForMarketplace();
        }
        s_listings[nftAddress][tokenId] = Listing(price, msg.sender, false);
        emit ItemListed(msg.sender, nftAddress, tokenId, price);
    }

    function listItemTrc(
        address nftAddress,
        uint256 tokenId,
        uint256 price
    )
        external
        notListed(nftAddress, tokenId, msg.sender)
        isOwner(nftAddress, tokenId, msg.sender)
    {
        if (price <= 0) {
            revert PriceMustBeAboveZero();
        }
        IERC721 nft = IERC721(nftAddress);
        if (!nft.isApprovedForAll(msg.sender, address(this))) {
            revert NotApprovedForMarketplace();
        }
        s_listings[nftAddress][tokenId] = Listing(price, msg.sender, true);
        emit ItemListed(msg.sender, nftAddress, tokenId, price);
    }

    function cancelListing(address nftAddress, uint256 tokenId)
        external
        isOwner(nftAddress, tokenId, msg.sender)
        isListed(nftAddress, tokenId)
    {
        delete (s_listings[nftAddress][tokenId]);
        emit ItemCanceled(msg.sender, nftAddress, tokenId);
    }


    function buyItem(address nftAddress, uint256 tokenId)
        external
        payable
        isListed(nftAddress, tokenId)
        nonReentrant
    {
        Listing memory listedItem = s_listings[nftAddress][tokenId];
        if ((msg.value < listedItem.price) || (listedItem.isTrc)) {
            revert PriceNotMet(nftAddress, tokenId, listedItem.price);
        }

        transferBuying(msg.value, listedItem.seller);
        delete (s_listings[nftAddress][tokenId]);
        IERC721(nftAddress).safeTransferFrom(listedItem.seller, msg.sender, tokenId);
        emit ItemBought(msg.sender, nftAddress, tokenId, listedItem.price);
    }

    function buyItemTrc(address nftAddress, uint256 tokenId, uint256 _value)
        external
        isListed(nftAddress, tokenId)
        nonReentrant
    {
        Listing memory listedItem = s_listings[nftAddress][tokenId];
        if ((_value < listedItem.price) || (!listedItem.isTrc)) {
            revert PriceNotMet(nftAddress, tokenId, listedItem.price);
        }

        transferBuyingTrc(_value, listedItem.seller);
        delete (s_listings[nftAddress][tokenId]);
        IERC721(nftAddress).safeTransferFrom(listedItem.seller, msg.sender, tokenId);
        emit ItemBought(msg.sender, nftAddress, tokenId, listedItem.price);
    }

    function updateListing(
        address nftAddress,
        uint256 tokenId,
        uint256 newPrice
    )
        external
        isListed(nftAddress, tokenId)
        nonReentrant
        isOwner(nftAddress, tokenId, msg.sender)
    {
        if (newPrice == 0) {
            revert PriceMustBeAboveZero();
        }

        s_listings[nftAddress][tokenId].price = newPrice;
        emit ItemListed(msg.sender, nftAddress, tokenId, newPrice);
    }

    function transferBuying(uint256 _price, address _seller) internal {
        uint256 mpFee = calculate(_price, feeBps);
        uint256 onwerMoney = _price - mpFee;
        (bool successmpls, ) = payable(beneficiary).call{value: mpFee}("");
        require(successmpls, "Transfer to marketplace failed");
        (bool successseller, ) = payable(_seller).call{value: onwerMoney}("");
        require(successseller, "Transfer to seller failed");
    }

    function transferBuyingTrc(uint256 _price, address _seller) internal {
        uint256 mpFee = calculate(_price, feeBps);
        uint256 onwerMoney = _price - mpFee;
        SafeERC20.safeTransferFrom(trc, msg.sender, beneficiary, mpFee);
        SafeERC20.safeTransferFrom(trc, msg.sender, _seller, onwerMoney);
    }

    function getListing(address nftAddress, uint256 tokenId)
        external
        view
        returns (Listing memory)
    {
        return s_listings[nftAddress][tokenId];
    }

    function calculate(uint256 amount, uint256 bps) internal pure returns (uint256) {
        return amount * bps / 10_000;
    }

}
