contract PoopHeadDrops is ERC1155, Ownable {

  string public name;
  string public symbol;

  string public baseUri;
  string public baseExtension = ".poop";

  uint public minted = 0;
  uint public cap = 10000; // Collection is limited at 10k items

  constructor(string memory _name, string memory _symbol, string memory _baseUri) ERC1155("") {
    name = _name;
    symbol = _symbol;
    baseUri = _baseUri;
  }

  function poopdrop(address[] memory _addresses) external onlyOwner {
    require(_addresses.length > 0, "Addresses length should be greater than 0.");
    require(minted + _addresses.length <= cap, "Collection is limited at 10k items!");

    for (uint i = 0; i < _addresses.length; i++) {
        minted++;
        _mint(_addresses[i], minted, 1, "");
    }
  }

  function setBaseURI(string memory _baseUri) external onlyOwner {
    baseUri = _baseUri;
  }

  function setBaseExtension(string memory _baseExtension) external onlyOwner {
    baseExtension = _baseExtension;
  }

  function uri(uint _id) public override view returns (string memory) {
    if (_id > 0 && _id <= minted) {
        return string(abi.encodePacked(baseUri, Strings.toString(_id), baseExtension));
    } else {
        return "";
    }
  }

  function getMinted() public view returns (uint) {
    return minted;
  }
}
