    "contracts/DolzStakingV3.sol": {
      "content": "// SPDX-License-Identifier: NONE

pragma solidity ^0.8.7;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721Receiver.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "erc721a/contracts/extensions/ERC721AQueryable.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

struct Lock{
    // Amount of the lock
    uint256 amount;
    // When the user locked
    uint256 dateStart;
    // When will he will be able to withdraw his deposited tokens
    uint256 dateEnd;
    // How many tokens he has harvested
    uint256 harvested;
}

struct Pool {
    // For front only
    string poolName;
    // Size of the pool in $DOLZ
    uint256 poolFunds;
    // How long your tokens will be locked, in seconds, ex:
    // 90 days => 7776000 
    uint256 lockDuration;
    // APR in percents
    uint256 apr;
    // Max tokens an address can deposit into a pool
    uint256 maxWalletCap;
    // Current amount of tokens locked by address
    mapping(address => uint256) userCurrentTokensLocked;
    // Current locks by address
    mapping(address => Lock[]) currentUserLocks;
    // Start supply of the pool, needed to calculate % of completion
    uint256 totalFunds;
    // Total current tokens locked on this pool
    uint256 totalTokens;
    // How many tokens have been withdrawned
    uint256 fundsLeft; 
    // Timestamp where users can start staking their tokens
    uint256 dateStart;
    // Requires the Emy the muse NFT ?
    bool isBoosted;
    // Funds have been withdrawned ?
    bool cancelled;
}

contract DolzStakingV3 is Ownable, IERC721Receiver {
    using SafeERC20 for IERC20;

    IERC20                      public immutable DOLZ;
    IERC721AQueryable           public immutable BOOSTER;
    uint256                     public immutable BOOSTERLIMIT = 1013;
    Pool[]                      public pools;

    // PERIOD must be in seconds
    // uint256                     public immutable PERIOD = 7200;    // 2 hours
    // uint256                     public immutable PERIOD = 600;     // 10 minutes
    // uint256                     public immutable PERIOD = 60;      // 1 minute
    // uint256                     public immutable PERIOD = 6048;       // 1 week
    uint256                     public immutable PERIOD = 604800;  // 1 week
    mapping(uint256 => bool)    public isPoolPaused;
    mapping(address => uint256) public stakedBoosters;

    event PoolCreated(uint256 poolSize, uint256 lockDuration, uint256 apr, uint256 maxWalletCap, bool isBoosted);
    event ReStaked(uint256 amount, address owner, uint256 timestamp);
    event Harvested(uint256 amount, address caller, uint256 periodsElapsed);
    event EmergencyUnlock(address caller, uint256 amount, uint256 timestamp);

    modifier poolIsValid(uint256 _poolFunds, uint256 _lockDuration, uint256 _apr, uint256 _maxWalletCap, uint256 _dateStart) {
        require(_poolFunds != 0, "DolzStaking: poolFunds can not be ZERO");
        require(_lockDuration != 0, "DolzStaking: lockDuration can not be ZERO");
        require(_apr != 0, "DolzStaking: APR can not be ZERO");
        require(_maxWalletCap != 0, "DolzStaking: maxWalletCap can not be ZERO");
        require(_dateStart >= block.timestamp, "DolzStaking: dateStart cannot be before current date");
        _;
    }

    modifier notPaused(uint256 _poolId) {
        require(pools.length > _poolId, "This pool doesn't exists");
        require(isPoolPaused[_poolId] != true, "DolzStaking: This pool is currently paused");
        _;
    }

    modifier isPaused(uint256 _poolId) {
        require(pools.length > _poolId, "This pool doesn't exists");
        require(isPoolPaused[_poolId] == true, "DolzStaking: This pool is not currently paused");
        _;
    }

    modifier depositIsValid(uint256 _poolId, uint256 _amount) {
        require(_amount > 0, "DolzStaking: Please enter a valid amount");
        require(_poolId < pools.length, "DolzStaking: Pool doesn't exists");
        require(pools[_poolId].userCurrentTokensLocked[msg.sender] + _amount * 1e18 <= pools[_poolId].maxWalletCap, "DolzStaking: More than max per wallet");
        require(_amount * 1e18 <= pools[_poolId].maxWalletCap, "DolzStaking: More than max per wallet");
        require(DOLZ.balanceOf(msg.sender) >= _amount * 1e18, "DolzStaking: Not enough DOLZ");
        if (BOOSTER.tokensOfOwner(msg.sender).length == 0 && stakedBoosters[msg.sender] == 0) {
            require(block.timestamp >= pools[_poolId].dateStart, "DolzStaking: Staking has not started for this pool");
        } else {
            require(block.timestamp >= pools[_poolId].dateStart - 172800, "[BOOSTED] DolzStaking: Staking has no started for this pool");
        }
        require(calculateMaxReward(_amount * 1e18, pools[_poolId].apr, pools[_poolId].lockDuration) <= pools[_poolId].poolFunds, "DolzStaking: Not enough funds left on contract to reward this amount");
        _;
    }

    constructor(IERC20 _DOLZ, IERC721AQueryable _BOOSTER) {
        DOLZ = _DOLZ;
        BOOSTER = _BOOSTER;
    }

    /// @notice Allow the owner to create a new pool with given configuration if all parameters are valid
    function createPool(string calldata _poolName, uint256 _poolSize, uint256 _lockDuration, uint256 _apr, uint256 _maxWalletCap, uint256 _dateStart, bool _isBoosted) external onlyOwner poolIsValid(_poolSize, _lockDuration, _apr, _maxWalletCap, _dateStart){
        Pool storage newPool = pools.push();
        newPool.poolName = _poolName;
        newPool.poolFunds = calculateMaxReward(_poolSize * 1e18, _apr, _lockDuration);
        newPool.lockDuration = _lockDuration;
        newPool.apr = _apr;
        newPool.maxWalletCap = _maxWalletCap;
        newPool.dateStart = _dateStart;
        newPool.isBoosted = _isBoosted;
        newPool.totalFunds = _poolSize * 1e18;
        newPool.cancelled = false;
        newPool.fundsLeft = calculateMaxReward(_poolSize * 1e18, _apr, _lockDuration);
        DOLZ.safeTransferFrom(msg.sender, address(this),  newPool.poolFunds);
        emit PoolCreated(_poolSize, _lockDuration, _apr, _maxWalletCap, _isBoosted);
    }

     /// @notice Allow the user to create a new lock
    function deposit(uint256 _poolId, uint256 _amount) external notPaused(_poolId) depositIsValid(_poolId, _amount) {
        if(pools[_poolId].isBoosted){
            stakeBoosted(_poolId, _amount, msg.sender);
        } else {
            stake(_poolId, _amount, msg.sender);
        }
    }

    /// @notice Callable only internally. Transfer funds from caller to contract and create lock for boosted pool
    function stakeBoosted(uint256 _poolId, uint256 _amount, address _sender) private {
        if (stakedBoosters[_sender] == 0) {
            uint256[] memory boosterIds = BOOSTER.tokensOfOwner(_sender);
            if(boosterIds.length > 0 && boosterIds[0] < BOOSTERLIMIT){
                BOOSTER.safeTransferFrom(_sender, address(this), boosterIds[0]);
                stakedBoosters[_sender] = boosterIds[0] + 1;
                stake(_poolId, _amount, _sender);
            } else {
                revert("DolzStaking: No booster found");
            }
        } else {
            require(BOOSTER.ownerOf(stakedBoosters[_sender] - 1) == address(this), "DolzStaking: Booster not owned by contract.");
            stake(_poolId, _amount, _sender);
        }
    }

    /// @notice Callable only internally. Transfer funds from caller to contract and create lock
    function stake(uint256 _poolId, uint256 _amount, address _sender) private {
        DOLZ.safeTransferFrom(_sender, address(this), _amount * 1e18);
        pools[_poolId].currentUserLocks[_sender].push(Lock({
            amount: _amount * 1e18,
            dateStart: block.timestamp,
            dateEnd: block.timestamp + pools[_poolId].lockDuration,
            harvested: 0
        }));
        uint256 reward = calculateMaxReward(_amount * 1e18, pools[_poolId].apr, pools[_poolId].lockDuration);
        pools[_poolId].poolFunds -= reward;
        pools[_poolId].userCurrentTokensLocked[_sender] +=_amount * 1e18;
        pools[_poolId].totalTokens += _amount * 1e18;
    }

    function withdraw(uint256 _poolId, uint256 _lockId) external notPaused(_poolId) {
        require(_poolId < pools.length, "DolzStaking: Pool doesn't exists");
        require(pools[_poolId].currentUserLocks[msg.sender].length > _lockId, "DolzStaking: this lock doesn't exists");

        Pool storage pool = pools[_poolId];
        Lock memory lock = pool.currentUserLocks[msg.sender][_lockId];
        require(lock.dateEnd < block.timestamp, "DolzStaking: Too early to unlock");
        uint256 pending = calculateMaxReward(lock.amount, pool.apr, pool.lockDuration);
        pending -= lock.harvested;
        uint256 toTransfer = lock.amount + pending;
        removeLock(_lockId, _poolId);
        pools[_poolId].userCurrentTokensLocked[msg.sender] -= lock.amount;

        pools[_poolId].fundsLeft -= toTransfer;
        pools[_poolId].totalTokens -= lock.amount;
        DOLZ.transfer(msg.sender, toTransfer);
        if(pools[_poolId].isBoosted == true){
            if(pools[_poolId].currentUserLocks[msg.sender].length < 1){
                BOOSTER.transferFrom(address(this), msg.sender, stakedBoosters[msg.sender] - 1);
                delete(stakedBoosters[msg.sender]);
            }
        }
    }

    function harvest(uint256 _poolId, uint256 _lockId) external notPaused(_poolId) {
        require(pools.length > _poolId, "DolzStaking: This pool doesn't exists");
        require(pools[_poolId].currentUserLocks[msg.sender].length > _lockId, "DolzStaking: This lock doesn't exists");
        uint256 periodsElapsed = getPeriodsElapsed(_poolId, _lockId, block.timestamp);
        uint256 toHarvest = pendingRewards(_poolId, _lockId, msg.sender, periodsElapsed);
         if (toHarvest > 0) {
            pools[_poolId].currentUserLocks[msg.sender][_lockId].harvested += toHarvest;
            pools[_poolId].fundsLeft -= toHarvest;
            DOLZ.transfer(msg.sender, toHarvest);
            emit Harvested(toHarvest, msg.sender, periodsElapsed);
        } else {
            revert("DolzStaking: Nothing to withdraw for this lock");
        }
    }

    /// @notice Calculate the amount of tokens the user can currently withdraw based on his deposit, his APR and how many PERIODs have passed 
    function pendingRewards(uint256 _poolId, uint256 _lockId, address _sender, uint256 _periodsElapsed) internal view returns (uint256) {
        Lock memory lock = pools[_poolId].currentUserLocks[_sender][_lockId];
        uint256 harvested = lock.harvested;

        // Periods in year:
        // MONTH: 12
        // WEEK: 52
        // DAYS: 365
        // HOURS: 8760
        // SECONDS: 525960
        uint32 periodsInYear = 52;
        uint256 tokensPerPeriod = lock.amount * (pools[_poolId].apr * 1e16) / 1e18 / periodsInYear;
        return (tokensPerPeriod * _periodsElapsed - harvested);
    }

    /* -------------------------------------------------------------------------- */
    /*                            CALCULATING FUNCTION                            */
    /* -------------------------------------------------------------------------- */

    /// @notice Calculate the max amount of tokens the token can earn based on his deposit
    /// @dev Since we plan on offering harvest every weeks, this calculate how many tokens youget per week => _amount * (_apr * 1e16) / 1e18 / 52
    /// @dev And because we have different lockDuration per pool, we then must calculate how many weeks are in the given lock duration => lockDuration / PERIOD
    /// @dev We can them multiply the 2 results to get the total number of tokens withdrawable after the whole duration 
    function calculateMaxReward(uint256 _amount, uint256 _apr, uint256 _lockDuration) public pure returns(uint256) {

        uint256 maxReward = _amount * (_apr * 1e16) / 1e18 / 52 * _lockDuration / PERIOD;
        return maxReward;
    }

    /// @notice Returns the given pool configuration
    function getPool(uint _poolId) external view returns (string memory poolName, uint256 poolSize, uint256 lockDuration, uint256 apr, uint256 maxWalletCap, uint256 totalTokens, bool isBoosted, uint256 dateStart, uint256 totalFunds, uint256 fundsLeft) {
        require(_poolId < pools.length, "DolzStaking: This pool doesn't exists.");

        Pool storage pool = pools[_poolId];
        return (
            pool.poolName,
            pool.poolFunds,
            pool.lockDuration,
            pool.apr,
            pool.maxWalletCap,
            pool.totalTokens,
            pool.isBoosted,
            pool.dateStart,
            pool.totalFunds,
            pool.fundsLeft
        );
    }

    function removeLock(uint _lockId, uint256 _poolId) private {
        require(_lockId < pools[_poolId].currentUserLocks[msg.sender].length, "DolzStaking: Lock doesn't exists");

        for (uint i = _lockId; i < pools[_poolId].currentUserLocks[msg.sender].length - 1; i++) {
            pools[_poolId].currentUserLocks[msg.sender][i] = pools[_poolId].currentUserLocks[msg.sender][i + 1];
        }
        pools[_poolId].currentUserLocks[msg.sender].pop();
    }

    /* -------------------------------------------------------------------------- */
    /*                                   GETTERS                                  */
    /* -------------------------------------------------------------------------- */
    /// @notice Returns how many pools have been created
    function getPoolsNumber() external view returns (uint256) {
        return pools.length;
    }

    function getAllPools() external view returns (string[] memory, uint256[] memory poolFunds, uint256[] memory lockDurations, uint256[] memory aprs, uint256[] memory maxWalletCaps, uint256[] memory dateStarts, bool[] memory isBoosteds, uint256[] memory totalFunds, uint256[] memory totalTokens, uint256 periodDuration, uint256[] memory fundsLeft) {
        uint256 poolCount = pools.length;
        string[] memory poolNames = new string[](poolCount);
        totalFunds = new uint256[](poolCount);
        poolFunds = new uint256[](poolCount);
        lockDurations = new uint256[](poolCount);
        aprs = new uint256[](poolCount);
        maxWalletCaps = new uint256[](poolCount);
        dateStarts = new uint256[](poolCount);
        isBoosteds = new bool[](poolCount);
        totalTokens = new uint256[](poolCount);
        fundsLeft = new uint256[](poolCount);

        for (uint256 i = 0; i < poolCount; i++) {
            poolNames[i] = pools[i].poolName;
            totalFunds[i] = pools[i].totalFunds;
            poolFunds[i] = pools[i].poolFunds;
            lockDurations[i] = pools[i].lockDuration;
            aprs[i] = pools[i].apr;
            maxWalletCaps[i] = pools[i].maxWalletCap;
            dateStarts[i] = pools[i].dateStart;
            isBoosteds[i] = pools[i].isBoosted;
            totalTokens[i] = pools[i].totalTokens;
            fundsLeft[i] = pools[i].fundsLeft;
        }

        return (poolNames, poolFunds, lockDurations, aprs, maxWalletCaps, dateStarts, isBoosteds, totalFunds, totalTokens, PERIOD, fundsLeft);
    }

    function getUserCurrentTokensDeposited(address _address, uint256 _poolId) external view returns(uint256 amount){
        return pools[_poolId].userCurrentTokensLocked[_address];
    }

    /// @notice Calculate how many PERIODs have passed based on the lock dateStart, the current timestamp and the period duration
    /// @dev The result of this function may differ from the real withdrawable amount. This is because we use _currentTimestamp and 
    /// @dev block.timestamp in view function is only saved based on the last block mined
    function getPeriodsElapsed(uint256 _poolId, uint256 _lockId, uint256 _currentTimestamp) public view returns (uint256) {
        uint256 periodsElapsed = (_currentTimestamp - pools[_poolId].currentUserLocks[msg.sender][_lockId].dateStart) / PERIOD;
        if(periodsElapsed > pools[_poolId].lockDuration / PERIOD){
            return pools[_poolId].lockDuration / PERIOD;
        } else {
            return periodsElapsed;
        }
    }

    /// @notice Returns array of the user deposits informations
    function getDepositForAddress(address _user) external view returns (Lock[][] memory) {
        Lock[][] memory userLocks = new Lock[][](pools.length);

        for(uint256 i = 0; i < pools.length; i++) {
            uint256 locksCount = pools[i].currentUserLocks[_user].length;
            userLocks[i] = new Lock[](locksCount);

            for(uint256 j = 0; j < locksCount; j++) {
                userLocks[i][j] = pools[i].currentUserLocks[_user][j];
            }
        }
        return userLocks;
    }

    function getUserHasLegendaryBooster() external view returns(bool) {
        if (BOOSTER.tokensOfOwner(msg.sender).length != 0 || stakedBoosters[msg.sender] != 0) {
            if(BOOSTER.tokensOfOwner(msg.sender)[0] < BOOSTERLIMIT || stakedBoosters[msg.sender] < BOOSTERLIMIT){
                return true;
            } else {
                return false;
            }
        } else {
            return false;
        }
    }

    function getUserHasBooster(address _user) external view returns(bool) {
        uint256[] memory tokensOfOwner;
        tokensOfOwner = BOOSTER.tokensOfOwner(_user);
        if (tokensOfOwner.length != 0) {
            return true;
        } else if (stakedBoosters[_user] != 0) {
            return true;
        } else {
            return false;
        }
    }

    /* -------------------------------------------------------------------------- */
    /*                                  EMERGENCY                                 */
    /* -------------------------------------------------------------------------- */

    /// @notice Callable by anyone when a pool is paused, but deletes the caller's lock to prevent later rewards withdraw
    function emergencyUnlock(uint256 _poolId, uint256 _lockId) external isPaused(_poolId) {
        Lock memory lock = pools[_poolId].currentUserLocks[msg.sender][_lockId];
        require(lock.amount > 0, "DolzStaking: No tokens found for this lock");
        pools[_poolId].userCurrentTokensLocked[msg.sender] -= lock.amount;
        emit EmergencyUnlock(msg.sender, lock.amount, block.timestamp);
        // delete(pools[_poolId].currentUserLocks[msg.sender][_lockId]);
        removeLock(_lockId, _poolId);
        DOLZ.transfer(msg.sender, lock.amount);
         if(pools[_poolId].isBoosted == true){
            if(pools[_poolId].currentUserLocks[msg.sender].length < 1){
                BOOSTER.transferFrom(address(this), msg.sender, stakedBoosters[msg.sender] - 1);
                delete(stakedBoosters[msg.sender]);
            }
        }
    }

    function withdrawPoolFunds(uint256 _poolId) external onlyOwner isPaused(_poolId){
        require(pools[_poolId].cancelled == false, "DolzStaking: Pool is already cancelled");
        DOLZ.transfer(msg.sender, pools[_poolId].fundsLeft);
        pools[_poolId].cancelled = true;
    }

    /// @notice Used when tokens are accidentally sent to the contract
    function withdrawERC20(address _token, uint256 _amount) external onlyOwner {
        require(
            _token != address(DOLZ),
            "DolzStaking: token can not be reward token."
        );
        require(
            IERC20(_token).transfer(msg.sender, _amount),
            "DolzStaking: Transfer failed"
        );
    }

    function pausePool(uint256 _poolId) external onlyOwner notPaused(_poolId) {
        isPoolPaused[_poolId] = true;
    }

    function unpausePool(uint256 _poolId) external onlyOwner notPaused(_poolId) {
        isPoolPaused[_poolId] = false;
    }

    /// @notice Allows the contract to receive ERC721 tokens
    function onERC721Received(address operator, address from, uint256 tokenId, bytes memory data) public virtual override returns (bytes4) {
        return this.onERC721Received.selector;
    }
}
"
