    "contracts/kicktoStaking.sol": {
      "content": "// SPDX-License-Identifier: GPL-3.0
pragma solidity >=0.6.0 <0.9.0;

import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";

contract KicktoStaking is Ownable, ReentrancyGuard {
    using SafeMath for uint256;

    IERC721 public kicktoNFT;

    // address of user => nfts staked by user
    mapping(address => uint256[]) public nftsStakedByUser;
    // user nfts to bool
    mapping(address => mapping(uint256 => bool)) public isNftStakedByUser;
    // total nfts staked
    uint256 public totalStakedNFts;

    constructor(address _kicktoNFT) {
        kicktoNFT = IERC721(_kicktoNFT);
    }

    function getNftsStakedByUser(
        address _user
    ) public view returns (uint256[] memory) {
        return nftsStakedByUser[_user];
    }

    function totalNftsStakedByUser(
        address _user
    ) public view returns (uint256) {
        return nftsStakedByUser[_user].length;
    }

    function stakeNFTS(uint256[] memory tokensIds) external nonReentrant {
        for (uint256 i = 0; i < tokensIds.length; i++) {
            // require(
            //     !isNftStakedByUser[msg.sender][tokensIds[i]],
            //     "the nft has already been staked by the user"
            // );
            require(stakeToken(tokensIds[i]));
        }
    }

    function stakeToken(uint256 tokenId) internal returns (bool) {
        kicktoNFT.transferFrom(msg.sender, address(this), tokenId);

        // pushing nfts staked by user
        nftsStakedByUser[msg.sender].push(tokenId);
        // this nfts is staked by user
        isNftStakedByUser[msg.sender][tokenId] = true;

        totalStakedNFts++;
        return true;
    }

    function cancelMassUnstake(
        uint256[] memory tokensIds
    ) external nonReentrant {
        for (uint256 i = 0; i < tokensIds.length; i++) {
            require(cancelStake(tokensIds[i]));
        }
    }

    // function to cancel  NFT stake
    function cancelStake(uint256 stakingId) public returns (bool) {
        uint256[] storage _nftsStakedByUser = nftsStakedByUser[msg.sender];
        bool _isStaked;
        uint256 _indexOfStaked;

        for (uint256 i = 0; i < _nftsStakedByUser.length; i++) {
            if (_nftsStakedByUser[i] == stakingId) {
                _isStaked = true;
                _indexOfStaked = i;
                break;
            }
        }
        require(_isStaked, "can't unstake nft that is not staked by you");

        // to be removed from the array
        if (
            _nftsStakedByUser.length > 1 &&
            (_indexOfStaked + 1) != _nftsStakedByUser.length
        ) {
            for (
                uint256 i = _indexOfStaked;
                i < _nftsStakedByUser.length - 1;
                i++
            ) {
                _nftsStakedByUser[i] = _nftsStakedByUser[i + 1];
            }
        }

        _nftsStakedByUser.pop();
        nftsStakedByUser[msg.sender] = _nftsStakedByUser;

        kicktoNFT.transferFrom(address(this), msg.sender, stakingId);
        totalStakedNFts--;
        return true;
    }
}
