    "contracts/HP2023_1.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity >=0.8.0 <=0.8.13;

import "@openzeppelin/contracts-upgradeable/token/ERC721/extensions/ERC721URIStorageUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/security/PausableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/CountersUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC721/extensions/ERC721BurnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC721/extensions/ERC721RoyaltyUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts/utils/Strings.sol";

/**
 * @title HP2023_1 Contract
 * @dev HP2023_1 - a basic smart contract for non-fungible tokens based on ERC721 standards implementation
 */

contract HP2023_1 is
    Initializable,
    ERC721URIStorageUpgradeable,
    PausableUpgradeable,
    ERC721BurnableUpgradeable,
    AccessControlUpgradeable,
    ERC721RoyaltyUpgradeable,
    OwnableUpgradeable
{
    using CountersUpgradeable for CountersUpgradeable.Counter;
    //generating hash value for MANAGER role
    bytes32 public constant MANAGER = keccak256("MANAGER");
    //generating hash value for PAUSER role
    bytes32 public constant PAUSER_ROLE = keccak256("PAUSER_ROLE");
    // total number of fan cards minted
    CountersUpgradeable.Counter private _tokenIdCounter;
    //storing contract level uri in state variable
    string public contractURI;
    //address to receive royalty amount
    address private collector;
    //range for total_cards
    uint16 private totalCount;
    //Storing base URI in state variable
    string public showURI;

    /**
     * Initializing the base contract
     * Initializer requires 11 attributes
     */
    function initialize(
        string memory _name,
        string memory _symbol,
        string memory _contractURI,
        uint16 _totalCount,
        string memory _showURI,
        address _collector,
        address _manager,
        address _owner
    ) public initializer {
        __ERC721URIStorage_init();
        __ERC721_init(_name, _symbol);
        __ERC721Burnable_init();
        __Pausable_init();
        __ERC721Royalty_init_unchained();
        contractURI = _contractURI;
        totalCount = _totalCount;
        collector = _collector;
        showURI = _showURI;
        _grantRole(DEFAULT_ADMIN_ROLE, _owner);
        _grantRole(MANAGER, _owner);
        _grantRole(PAUSER_ROLE, _owner);
        _grantRole(MANAGER, _manager);
        _transferOwnership(_owner);
    }

    /**
     * Mints multiple fan cards looping token numbers entered
     * Can only be called by NFT_SENDER role
     * Also the owner must be a valid owner
     */
    function mint(uint32 numberOfTokens)
        external
        onlyRole(MANAGER)
    {
        //Check if no. of tokens requested for mint added to the previous minted token numbers is less than max_tokens
        require(
            _tokenIdCounter.current() + numberOfTokens <= totalCount,
            "The number of tokens exceed the total_count value!"
        );
        //Mint the no. of tokens at once
        for (uint16 i = 0; i < numberOfTokens; i++) {
            // increment counter
            _tokenIdCounter.increment();
            // mint the token
            _safeMint(msg.sender, _tokenIdCounter.current());
            _setTokenURI(
            _tokenIdCounter.current(),
            string(
                abi.encodePacked(showURI, Strings.toString(_tokenIdCounter.current()), ".json")
            )
        );
        }
    }

    /**
     * Burning a certain token will also reset the royalty amount
     * Can only be called by token owner
     */
    function _burn(uint256 _fcId)
        internal
        override(
            ERC721Upgradeable,
            ERC721URIStorageUpgradeable,
            ERC721RoyaltyUpgradeable
        )
    {
        super._burn(_fcId);
        _resetTokenRoyalty(_fcId);
    }

    /**
     * function to pause the contract
     */
    function pause() public onlyRole(PAUSER_ROLE) {
        _pause();
    }

    /**
     * function to unpause the contract
     */
    function unpause() public onlyRole(PAUSER_ROLE) {
        _unpause();
    }

    /**
     * Get the token uri for a given token Id
     */
    function tokenURI(uint256 _fcId)
        public
        view
        override(ERC721Upgradeable, ERC721URIStorageUpgradeable)
        returns (string memory)
    {
        return super.tokenURI(_fcId);
    }

    /**
     * Support interface required for overriding the ERC721RoyaltyUpgradeable function
     */
    function supportsInterface(bytes4 interfaceId)
        public
        view
        virtual
        override(
            ERC721Upgradeable,
            ERC721RoyaltyUpgradeable,
            AccessControlUpgradeable
        )
        returns (bool)
    {
        return super.supportsInterface(interfaceId);
    }


    /**
     * Function to override pauseable check before token transfer
     */
    function _beforeTokenTransfer(address from, address to, uint256 tokenId)
        internal
        whenNotPaused
        override
    {
        super._beforeTokenTransfer(from, to, tokenId);
    }

    /**
     * Function to let Opensea proxy operate for our contract
     */
    function isApprovedForAll(address _owner, address _operator)
        public
        view
        override
        returns (bool isOperator)
    {
        // if OpenSea's ERC721 Proxy Address is detected, auto-return true
        if (_operator == address(0x58807baD0B376efc12F5AD86aAc70E78ed67deaE)) {
            return true;
        }

        // otherwise, use the default ERC721.isApprovedForAll()
        return ERC721Upgradeable.isApprovedForAll(_owner, _operator);
    }

    /**
     * Function to set royalty per tokenId - This does not work for Opensea -Just for other market places
     * Can only be called by the DEFAULT_ADMIN_ROLE 
     */
    function setRoyalty(uint256 _fcId, uint96 _royalty)
        external
        onlyOwner
    {
        require(doesFanCardExists(_fcId), "This fanCard does not exist");
        _setTokenRoyalty(_fcId, collector, _royalty);
    }

    /**
     * Check if the token already exists
     */
    function doesFanCardExists(uint256 _fcId) public view returns (bool) {
        return _exists(_fcId);
    }

    /**
     * Get total number of tokens minted so
     */
    function getNumberOfTokensMinted() public view returns (uint256) {
        uint256 totalNumberOfTokensMinted = _tokenIdCounter.current();
        return totalNumberOfTokensMinted;
    }
    function changeLink(uint256 _fcId, string memory _link) public onlyRole(MANAGER) {

        _setTokenURI(
            _fcId,
            string(
                abi.encodePacked(_link, Strings.toString(_fcId), ".json")
            )
        );
    }
}"
    }
  },
  "settings": {
    "optimizer": {
      "enabled": true,
      "runs": 200
    },
    "outputSelection": {
      "*": {
        "*": [
          "evm.bytecode",
          "evm.deployedBytecode",
          "devdoc",
          "userdoc",
          "metadata",
          "abi"
        ]
      }
    },
    "libraries": {}
  }
}}
