pragma solidity ^0.8.9;

import "@openzeppelin/contracts/token/ERC1155/ERC1155.sol";
import "@openzeppelin/contracts/token/common/ERC2981.sol";

import "../utils/AccessProtected.sol";

/// @title ShatterpointEventsToken
/// @author The name of the author
/// @notice An ERC1155 token contract to hold all the event related tokens happen in Shatterpoint
contract ShatterpointEventsToken is ERC1155, ERC2981, AccessProtected {
    constructor()
        ERC1155(
            "https://ipfs.shatterpoint.game/ipns/k51qzi5uqu5di1ty8k2csb2smduu0f1ge2vvmjte9xho08lekmlwwtc42fw103/{id}.json"
        )
    {}

    function setURI(string memory newuri) public onlyOwner {
        _setURI(newuri);
    }

    /// @notice To set royalty on a token
    /// @dev To set royalty percent on a token id, can only be called by owner
    /// @param tokenId id of the token to set royalty on
    /// @param receiver address of the royalty receiver
    /// @param feeNumerator percent of royalty in basis points. (10000 is the denominating factor)
    /// @return true if execution is successful
    function setTokenRoyalty(
        uint256 tokenId,
        address receiver,
        uint96 feeNumerator
    ) external onlyOwner returns (bool) {
        require(receiver != address(0), "Invalid receiver");
        _setTokenRoyalty(tokenId, receiver, feeNumerator);
        return true;
    }

    /// @notice to mint tokens to the user
    /// @dev to mint tokens to a user, can be called by an admin
    /// @param account receiver of the tokens
    /// @param id id of the tokens to be minted
    /// @param amount amount of the tokens to mint
    /// @param data any data needed to be stored while minting
    function mint(
        address account,
        uint256 id,
        uint256 amount,
        bytes memory data
    ) public onlyAdmin {
        _mint(account, id, amount, data);
    }

    /// @notice to batch mint tokens to a single user
    /// @dev to batch mint tokens to a user, can be called by an admin
    /// @param to receiver of the tokens
    /// @param ids array of token ids to mint tokens
    /// @param amounts arrays of amount of tokens to mint
    /// @param data any data needed to be stored while minting
    function mintBatch(
        address to,
        uint256[] memory ids,
        uint256[] memory amounts,
        bytes memory data
    ) public onlyOwner {
        _mintBatch(to, ids, amounts, data);
    }

    /// @notice to burn tokens
    /// @dev to burn tokens that the caller holds
    /// @param tokenId id of the tokens to be minted
    /// @param amount amount of the tokens to mint
    function burn(uint256 tokenId, uint256 amount) external {
        _burn(_msgSender(), tokenId, amount);
    }

    function supportsInterface(bytes4 interfaceId)
        public
        view
        virtual
        override(ERC1155, ERC2981)
        returns (bool)
    {
        return super.supportsInterface(interfaceId);
    }
