/**
 * @title ForeverPudgyPenguin traits contract
 * @author Pudgy Penguins Penguineering Team (davidbailey.eth, eloc.eth)
 */

contract ForeverPPTraits is AbstractERC1155Factory, ERC2981, OperatorFilterer {
    // ========================================
    //    EVENTS & ERRORS
    // ========================================

    error InvalidTokenId();

    // ========================================
    //    DEFINITIONS
    // ========================================

    uint256 private _nextTokenID;
    bool public operatorFilteringEnabled = true;

    // ========================================
    //    CONSTRUCTOR AND CORE FUNCTIONS
    // ========================================

    constructor(
        string memory _name,
        string memory _symbol,
        string memory _uri,
        address payable _royaltiesReceiver
    ) ERC1155(_uri) {
        name_ = _name;
        symbol_ = _symbol;

        // royalties set to 500 BP = 5%
        setDefaultRoyalty(_royaltiesReceiver, 500);

        // enforce royalties
        _registerForOperatorFiltering(address(0), false);
    }

    /**
     * @notice Returns the metadata uri for a given id
     * @param _id the card id to return metadata for
     */
    function uri(uint256 _id) public view override returns (string memory) {
        if (!exists(_id)) revert InvalidTokenId();

        return string(abi.encodePacked(super.uri(_id), Strings.toString(_id)));
    }

    // ========================================
    //    MINT FUNCTIONS
    // ========================================

    /**
     * @notice Creates a new token type and assigns its max supply to an address
     * @param _initialOwner address which will receive all minted tokens
     * @return _id newly created token ID
     * @dev after creation, it is it not possible to edit the max supply
     */
    function create(
        address _initialOwner,
        uint256 _supply,
        bytes calldata _data
    ) external onlyOwner returns (uint256) {
        uint256 id = _nextTokenID;
        _incrementTokenTypeId();

        _mint(_initialOwner, id, _supply, _data);
        return id;
    }

    /**
     * @notice Creates a batch of new token types and assigns their max supply to an address
     * @param _initialOwner address which will receive all minted tokens
     * @return _ids an array of newly created token IDs
     * @dev after creation, it is it not possible to edit the max supply
     */
    function createBatch(
        address _initialOwner,
        uint256[] calldata _supplies,
        bytes calldata _data
    ) external onlyOwner returns (uint256[] memory) {
        uint256[] memory ids = new uint256[](_supplies.length);

        for (uint256 i; i < _supplies.length; ) {
            ids[i] = _nextTokenID;
            _incrementTokenTypeId();

            unchecked {
                ++i;
            }
        }

        _mintBatch(_initialOwner, ids, _supplies, _data);
        return ids;
    }

    // ========================================
    //    ADMIN FUNCTIONS
    // ========================================

    /**
     * @notice Sets new base URI used in uri function
     * @param _baseURI new base URI
     */
    function setURI(string calldata _baseURI) external onlyOwner {
        _setURI(_baseURI);
    }

    /**
     * @notice Sets the default royalty info
     * @param _receiver address that will receive all royalties
     * @param _numerator royalty fee in basis points
     * @dev ERC2981
     */
    function setDefaultRoyalty(
        address payable _receiver,
        uint96 _numerator
    ) public onlyOwner {
        _setDefaultRoyalty(_receiver, _numerator);
    }

    /**
     * @notice Sets the royalty info per tokenId
     * @param _tokenId token which will have separate royalty settings from default
     * @param _receiver address that will receive royalties from _tokenId
     * @param _numerator royalty fee in basis points
     * @dev ERC2981
     */
    function setTokenRoyalty(
        uint256 _tokenId,
        address payable _receiver,
        uint96 _numerator
    ) external onlyOwner {
        _setTokenRoyalty(_tokenId, _receiver, _numerator);
    }

    /**
     * @notice Deletes the royalty info per tokenId
     * @param _tokenId token which royalties will be reset to default
     * @dev ERC2981
     */
    function resetTokenRoyalty(uint256 _tokenId) external onlyOwner {
        _resetTokenRoyalty(_tokenId);
    }

    /**
     * @notice Switches enforsing of royalties on/off
     * @param _value boolean, true = enforce, false = do not enforce
     */
    function setOperatorFilteringEnabled(bool _value) external onlyOwner {
        operatorFilteringEnabled = _value;
    }

    // ========================================
    //    OTHER FUNCTIONS
    // ========================================

    /**
     * @notice Increments the value of _nextTokenID
     */
    function _incrementTokenTypeId() private {
        ++_nextTokenID;
    }

    /**
     * @notice Returns all created token IDs
     */
    function createdIds() external view returns (uint256[] memory) {
        uint256[] memory ids = new uint256[](_nextTokenID);
        for (uint256 i; i < _nextTokenID; ) {
            ids[i] = i;
            unchecked {
                ++i;
            }
        }
        return ids;
    }

    /**
     * @notice Returns if internface is supported
     * @dev ERC165 & ERC2981
     */
    function supportsInterface(
        bytes4 _interfaceId
    ) public view virtual override(ERC1155, ERC2981) returns (bool) {
        return
            ERC1155.supportsInterface(_interfaceId) ||
            ERC2981.supportsInterface(_interfaceId);
    }

    // ========================================
    //    ROYALTY ENFORCEMENT FUNCTIONS
    // ========================================

    /**
     * @notice Returns if operator is preferred marketplace
     * @param _operator address of operator
     * @return boolean, true = is preferred marketplace, false = is not preferred marketplace
     * @dev OperatorFilterer
     */
    function _isPriorityOperator(
        address _operator
