pragma solidity ^0.8.9;





contract Komet is ERC1155, AccessControl, Ownable {
    string public name = "";
    uint256 public totalSupply = 0;
    string internal _uri = "";

    address public treasury;

    bytes32 public constant URI_SETTER_ROLE = keccak256("URI_SETTER_ROLE");
    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");

    uint256 internal maxCount;

    uint256 public mintLimit;
    uint256 public mintPrice;
    bool internal paused;
    bool internal WLMint;
    bool internal pMint;

    mapping(address => uint256) public whitelistmint_amount;

    mapping(address => bool) public whitelistmint_address;
    mapping(address => uint256) internal public_mint;

    event UserPaid(address indexed _user, uint256 indexed amount);

    constructor(
        address komet_minter,
        string memory _name,
        string memory _ipfsUri,
        uint256 _supply,
        uint256 _mintLimit,
        uint256 _mintPrice,
        address _treasury
    ) ERC1155(_ipfsUri) {
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(URI_SETTER_ROLE, msg.sender);
        _grantRole(MINTER_ROLE, msg.sender);

        _grantRole(DEFAULT_ADMIN_ROLE, komet_minter);
        _grantRole(URI_SETTER_ROLE, komet_minter);
        _grantRole(MINTER_ROLE, komet_minter);

        totalSupply = 0;

        name = _name;
        _uri = _ipfsUri;
        mintLimit = _mintLimit;
        mintPrice = _mintPrice;
        maxCount = _supply;
        treasury = _treasury;
    }

    receive() external payable {
        emit UserPaid(msg.sender, msg.value);
    }

    modifier isValid(uint256 _amount) {
        require(
            totalSupply + _amount <= maxCount,
            "Maximum mint limit reached"
        );
        require(!paused, "Contract is paused");
        _;
    }

    function publicMint(uint256 _amount) public payable isValid(_amount) {
        require(pMint, "public mint is not open");
        require(_amount <= mintLimit, "Maximum public mint limit reached");

        require(msg.value >= _amount * mintPrice, "amount is low");

        public_mint[msg.sender] += _amount;
        totalSupply += _amount;
        _mint(msg.sender, 1, _amount, "0x00");
    }

    function wlMint(uint256 _amount) public payable isValid(_amount) {
        require(WLMint, "whitelist mint is not open");
        require(whitelistmint_address[msg.sender], "You are not whitelisted");
        require(
            whitelistmint_amount[msg.sender] <= mintLimit,
            "User has already minted tokens in whitelist mint"
        );

        require(
            whitelistmint_amount[msg.sender] + _amount <= mintLimit,
            "Maximum whitelist mint limit reached"
        );
        require(msg.value >= _amount * mintPrice, "amount is low");

        whitelistmint_amount[msg.sender] += _amount;
        totalSupply += _amount;
        _mint(msg.sender, 1, _amount, "0x00");
    }

    function treasuryMint(uint256 amount) public onlyRole(MINTER_ROLE) {
        require(totalSupply + amount <= maxCount, "total supply limit reached");
        totalSupply += amount;
        _mint(treasury, 1, amount, "0x00");
    }

    function setMintType(
        uint256 _price,
        uint256 _limit,
        bool _wl,
        bool _public
    ) public onlyRole(DEFAULT_ADMIN_ROLE) {
        mintPrice = _price;
        mintLimit = _limit;
        WLMint = _wl;
        pMint = _public;
    }

    function setWhiteListUsers(address[] memory _users)
        public
        onlyRole(DEFAULT_ADMIN_ROLE)
    {
        for (uint256 i = 0; i < _users.length; i++) {
            whitelistmint_amount[_users[i]] = 0;
            whitelistmint_address[_users[i]] = true;
        }
    }

    function setTreasuryWallet(address _treasury) public onlyRole(DEFAULT_ADMIN_ROLE) {
        treasury = _treasury;
    }

    function setPaused(bool _paused) public onlyRole(DEFAULT_ADMIN_ROLE) {
        paused = _paused;
    }

    function setMaxCount(uint256 _max) public onlyRole(DEFAULT_ADMIN_ROLE) {
        maxCount = _max;
    }

    function setMintPrice(uint256 _max) public onlyRole(DEFAULT_ADMIN_ROLE) {
        mintPrice = _max;
    }

    function setMintLimit(uint256 _max) public onlyRole(DEFAULT_ADMIN_ROLE) {
        mintLimit = _max;
    }

    function setURI(string memory newuri) public onlyRole(URI_SETTER_ROLE) {
        _uri = newuri;
    }

    function withdrawAdmin() external onlyRole(DEFAULT_ADMIN_ROLE) {
        bool success = payable(msg.sender).send(address(this).balance);
        require(success, "Ether transfer failed.");
    }

    function uri(uint256 _tokenid)
        public
        view
        override
        returns (string memory)
    {
        return
            string(abi.encodePacked(_uri, Strings.toString(_tokenid), ".json"));
    }

    function supportsInterface(bytes4 interfaceId)
        public
        view
        override(ERC1155, AccessControl)
        returns (bool)
    {
        return super.supportsInterface(interfaceId);
    }
}
