    "contracts/LegendsNFT.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity 0.8.10;

import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/token/ERC1155/IERC1155.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721Enumerable.sol";
import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";

contract LegendsNFT is ERC721Enumerable, Ownable, ReentrancyGuard {
	using Strings for uint256;

	string private _baseTokenURI = "https://api.cyborglegends.io/test1/nft/metadata/";
	string private _contractURI = "ipfs://QmR9v5CEfCi6QoZKNvUiaVzgrqQawdzBYMNgNM9gQTJUEb";

	uint256 public mysteryBoxMaxSupply = 10000;
	uint256 public mysteryBoxCounter = 0;
	uint256 public initialMaxSupplyPurchaseT4 = 48;
	uint256 public initialMaxSupplyPurchaseT5 = 16;
	uint256 public counterT4InitialSale = 0;
	uint256 public counterT5InitialSale = 0;
	uint256 public tokenCounter = 0;

	uint256 public priceMysteryBox = 10000000000000000; // TODO: change to 100 MATIC (100000000000000000000) for production
	uint256 public pricePerTokenT4Utility = 20000000000000000; // TODO: change to 2500 MATIC (2500000000000000000000) for production
	uint256 public pricePerTokenT5Utility = 30000000000000000; // TODO: change to 10000 MATIC (10000000000000000000000) for production

	string public defaultUtility = "playable_hero";

	mapping(uint256 => string) public utility;

	uint256 public mergeStartTime = block.timestamp; //update to correct
	uint256 public saleStartTime = block.timestamp; //update to correct

	mapping(address => bool) public engineers;
	mapping(address => bool) public minters;

	event MysteryBoxPurchase(address indexed account, uint256 tokenID, string utility);
	event T4Purchase(address indexed account, uint256 tokenID, string utility);
	event T5Purchase(address indexed account, uint256 tokenID, string utility);
	event AdminPurchase(address indexed account, uint256 tokenID, uint256 tier, string utility);
	event Merge(uint256 indexed tokenID, uint256[] t1IDs, string utility, address indexed creator);

	constructor() ERC721("Cyborg Legends", "CLE") {
		//transfer to L: 0x6C67c0FC112f64Dd6FDb43b3bb42E2e4B181a452, K: 0x33a2Ce8C986b3321C82cB127f3090F6c10D8D990
		setMinter(msg.sender, true); // TODO: set deployer as minter, disable in production
        setEngineer(msg.sender, true); // TODO: set deployer as engineer, disable in production
	}

	//general public sale
	function mysteryBoxPurchase(uint256 qty) external payable nonReentrant {
		require(block.timestamp >= saleStartTime, "sale not live");
		require(qty <= 20, "no more than 20");
		require(priceMysteryBox * qty == msg.value, "exact MATIC amount needed");
		require(tokenCounter + qty <= mysteryBoxMaxSupply, "out of stock (total supply)");

		for (uint256 i = 0; i < qty; i++) {
			utility[getNextTokenID()] = defaultUtility;

			emit MysteryBoxPurchase(msg.sender, getNextTokenID(), utility[getNextTokenID()]);
			_safeMint(msg.sender, getNextTokenID());
			tokenCounter++;
		}
		mysteryBoxCounter += qty;
	}

	//guaranteed mint T4
	function purchaseT4() external payable nonReentrant {
		require(block.timestamp >= saleStartTime, "sale not live");
		require(pricePerTokenT4Utility == msg.value, "exact MATIC amount needed");
		require(counterT4InitialSale <= initialMaxSupplyPurchaseT4, "out of stock (T4)");
		counterT4InitialSale++;
		utility[getNextTokenID()] = defaultUtility;

		emit T4Purchase(msg.sender, getNextTokenID(), utility[getNextTokenID()]);
		_safeMint(msg.sender, getNextTokenID());
		tokenCounter++;
	}

	//guaranteed mint T5
	function purchaseT5() external payable nonReentrant {
		require(block.timestamp >= saleStartTime, "sale not live");
		require(pricePerTokenT5Utility == msg.value, "exact MATIC amount needed");
		require(counterT5InitialSale <= initialMaxSupplyPurchaseT5, "out of stock (T5)");
		counterT5InitialSale++;
		utility[getNextTokenID()] = defaultUtility;

		emit T5Purchase(msg.sender, getNextTokenID(), utility[getNextTokenID()]);
		_safeMint(msg.sender, getNextTokenID());
		tokenCounter++;
	}

	//create merging
	function mergeNFTs(uint256[] memory t1IDs) external nonReentrant {
		require(block.timestamp >= mergeStartTime, "merge not live");
		require(t1IDs.length >= 2, "please pass min 2 nfts");

		for (uint256 i = 0; i < t1IDs.length; i++) {
			require(ownerOf(t1IDs[i]) == msg.sender, "you are not the owner");
			require(compareStrings(utility[t1IDs[i]], utility[t1IDs[0]]), "incorrect utility");
		}

		burnBatch(t1IDs);

		//all good
		utility[getNextTokenID()] = utility[t1IDs[0]]; //set the utility

		emit Merge(getNextTokenID(), t1IDs, utility[t1IDs[0]], msg.sender);
		_safeMint(msg.sender, getNextTokenID());
		tokenCounter++;
	}

	//admin can mint for giveaways, contests etc.
	function adminMint(
		uint256 qty,
		address _to,
		uint256 _tier,
		string memory _utility
	) external {
		require(minters[msg.sender] == true, "only minters");
		require(qty <= 20, "maximum 20");
		for (uint256 i = 0; i < qty; i++) {
			utility[getNextTokenID()] = _utility;

			emit AdminPurchase(_to, getNextTokenID(), _tier, _utility);
			_safeMint(_to, getNextTokenID());
			tokenCounter++;
		}
	}

	// sets the utility
	function setUtility(uint256 nftID, string memory _utility) external {
		require(engineers[msg.sender] == true, "only engineers");
		utility[nftID] = _utility;
	}

	// sets the default utility
	function setDefaultUtility(string memory _utility) external onlyOwner {
		defaultUtility = _utility;
	}

	//----------------------------------
	//---------- other things ----------
	//----------------------------------
	function tokensOfOwner(address _owner) external view returns (uint256[] memory) {
		uint256 tokenCount = balanceOf(_owner);
		if (tokenCount == 0) {
			return new uint256[](0);
		} else {
			uint256[] memory result = new uint256[](tokenCount);
			uint256 index;
			for (index = 0; index < tokenCount; index++) {
				result[index] = tokenOfOwnerByIndex(_owner, index);
			}
			return result;
		}
	}

	//internal, used in burnBatch
	function burn(uint256 tokenId) public virtual {
		require(_isApprovedOrOwner(_msgSender(), tokenId), "caller is not owner nor approved");
		_burn(tokenId);
	}

	//internal, used for merging NFTs
	function burnBatch(uint256[] memory tokensId) public {
		for (uint256 i = 0; i < tokensId.length; i++) {
			burn(tokensId[i]);
		}
	}

	function exists(uint256 _tokenId) external view returns (bool) {
		return _exists(_tokenId);
	}

	function isApprovedOrOwner(address _spender, uint256 _tokenId) external view returns (bool) {
		return _isApprovedOrOwner(_spender, _tokenId);
	}

	function tokenURI(uint256 _tokenId) public view override returns (string memory) {
		require(_exists(_tokenId), "ERC721Metadata: URI query for nonexistent token");
		return string(abi.encodePacked(_baseTokenURI, _tokenId.toString()));
	}

	function setBaseURI(string memory newBaseURI) public onlyOwner {
		_baseTokenURI = newBaseURI;
	}

	function setContractURI(string memory newuri) public onlyOwner {
		_contractURI = newuri;
	}

	function contractURI() public view returns (string memory) {
		return _contractURI;
	}

	// withdrawals
	function withdrawEarnings() external onlyOwner {
		payable(msg.sender).transfer(address(this).balance);
	}

	function reclaimERC20(IERC20 erc20Token) external onlyOwner {
		erc20Token.transfer(msg.sender, erc20Token.balanceOf(address(this)));
	}

	function reclaimERC721(IERC721 erc721Token, uint256 id) external onlyOwner {
		erc721Token.safeTransferFrom(address(this), msg.sender, id);
	}

	function reclaimERC1155(IERC1155 erc1155Token, uint256 id) external onlyOwner {
		erc1155Token.safeTransferFrom(address(this), msg.sender, id, 1, "");
	}

	function setEngineer(address _newWallet, bool _isEngineer) public onlyOwner {
		engineers[_newWallet] = _isEngineer;
	}

	function setMinter(address _newWallet, bool _isMinter) public onlyOwner {
		minters[_newWallet] = _isMinter;
	}

	function setSaleStartTime(uint256 _saleStartTime) external onlyOwner {
		saleStartTime = _saleStartTime;
	}

	function setMergeStartTime(uint256 _mergeStartTime) external onlyOwner {
		mergeStartTime = _mergeStartTime;
	}

	function setMaxSupplyMysteryBox(uint256 _newMaxSupply) external onlyOwner {
		mysteryBoxMaxSupply = _newMaxSupply;
	}

	function setMaxSupplyPurchaseT4(uint256 _newMaxSupply) external onlyOwner {
		initialMaxSupplyPurchaseT4 = _newMaxSupply;
	}

	function setMaxSupplyPurchaseT5(uint256 _newMaxSupply) external onlyOwner {
		initialMaxSupplyPurchaseT5 = _newMaxSupply;
	}

	function resetMysteryBoxCounter() external onlyOwner {
		mysteryBoxCounter = 0;
	}

	function resetMT4Counter() external onlyOwner {
		counterT4InitialSale = 0;
	}

	function resetT5Counter() external onlyOwner {
		counterT5InitialSale = 0;
	}

    // change mystery price
    function changeMysteryBoxPrice(uint256 newPrice) external onlyOwner {
        priceMysteryBox = newPrice;
    }

	//change the price per NFT according to their tier (only T4 & T5)
	function changeTierPrice(uint256 where, uint256 newPrice) external onlyOwner {
		if (where == 4) {
			pricePerTokenT4Utility = newPrice;
		}
		if (where == 5) {
			pricePerTokenT5Utility = newPrice;
		}
	}

	//easy for some devs
	function getUtility(uint256 nftID) public view returns (string memory) {
		return utility[nftID];
	}

	function getNextTokenID() internal view returns (uint256) {
		return tokenCounter + 1;
	}

	function compareStrings(string memory a, string memory b) internal pure returns (bool) {
		return (keccak256(abi.encodePacked((a))) == keccak256(abi.encodePacked((b))));
	}
}
"
