    uint256 private _totalSupply;
    uint256 public maticDistributionLimit = 0;
    uint256 public royaltyPercentage = 10; // 10% royalty

    uint256[] public tokenIds; 

    mapping(uint256 => address) public originalCreators;
    mapping(uint256 => uint256) public tokenPrices;
    mapping(uint256 => bool) public isTokenForSale;
    
    mapping(address => uint256) public holderTokensCount;

    // Tracking unique owners
    mapping(address => bool) private isOwnerAdded;
    address[] public owners;

    event PriceChanged(string currency, uint256 newPrice);
    event NFTMinted(address indexed buyer, uint256 indexed tokenId, string tokenURI);
    event RoyaltyPaid(address indexed originalCreator, uint256 amount);
    event MATICDistributed(uint256 totalAmount);
    event TransferFailed(address recipient, uint256 amount);

    function initialize(string memory name, string memory symbol, uint256 _nftPriceInMATIC, uint256 _maxSupply) public initializer {
        __ERC721_init(name, symbol);
        __Ownable_init();
        __AccessControl_init();
        __ReentrancyGuard_init();

        _setupRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _setupRole(MINTER_ROLE, msg.sender);

        nftPriceInMATIC = _nftPriceInMATIC;
        maxSupply = _maxSupply;
    }


    // Gets the contract balance
    function getContractBalance() public view returns (uint256) {
        return address(this).balance;
    }

    // Checks if the current wallet is the contract owner
    function isCallerTheOwner() public view returns (bool) {
        return msg.sender == owner();
    }

    function totalSupplyList() public view returns (uint256) {
        return tokenIds.length;
    }    

    function setPriceInMATIC(uint256 newPrice) external onlyRole(DEFAULT_ADMIN_ROLE) {
        nftPriceInMATIC = newPrice;
        emit PriceChanged("MATIC", newPrice);
    }

    function setMaxSupply(uint256 _maxSupply) external onlyRole(DEFAULT_ADMIN_ROLE) {
        maxSupply = _maxSupply;
    }

    function setRoyaltyPercentage(uint256 newPercentage) external onlyRole(DEFAULT_ADMIN_ROLE) {
        require(newPercentage <= 100, "Percentage cannot be more than 100");
        royaltyPercentage = newPercentage;
        emit RoyaltyPercentageChanged(newPercentage);
    }

    function setMATICDistributionLimit(uint256 newLimit) external onlyRole(DEFAULT_ADMIN_ROLE) {
        maticDistributionLimit = newLimit;
        emit MATICDistributionLimitChanged(newLimit);
    }

    function mintTo(address _to, uint256 _tokenId, string memory _tokenURI) external nonReentrant {
        require(_totalSupply < maxSupply, "Max supply reached");
        require(_tokenId <= maxSupply, "Token ID exceeds max supply");
        require(_exists(_tokenId) == false, "Token ID already exists");
        require(hasRole(MINTER_ROLE, msg.sender) || hasRole(DEFAULT_ADMIN_ROLE, msg.sender), "Caller is not a minter or admin");

        _totalSupply += 1;

        holderTokensCount[_to] += 1;
        originalCreators[_tokenId] = msg.sender;

        _mint(_to, _tokenId);
        _setTokenURI(_tokenId, _tokenURI);
        //store the tokenID
        tokenIds.push(_tokenId);
        emit NFTMinted(_to, _tokenId, _tokenURI);
    }


	function transferFrom(address from, address to, uint256 tokenId) public virtual override {

		super.transferFrom(from, to, tokenId);
		_afterTokenTransfer(from, to, tokenId);

	}


    function safeTransferFrom(address from, address to, uint256 tokenId, bytes memory _data) public virtual override {
        super.safeTransferFrom(from, to, tokenId, _data);
        _afterTokenTransfer(from, to, tokenId);
    }

    function safeTransferFrom(address from, address to, uint256 tokenId) public virtual override {
        require(hasRole(MINTER_ROLE, msg.sender) || hasRole(DEFAULT_ADMIN_ROLE, msg.sender), "Caller is not a minter or admin");
        super.safeTransferFrom(from, to, tokenId);
        _afterTokenTransfer(from, to, tokenId);
    }

	function _afterTokenTransfer(address from, address to, uint256 /*_tokenId*/) private {
		// tokenId is unused but kept to match the expected signature.
        // Decrease the token count for the sender
		holderTokensCount[from] -= 1;

		// Increase the token count for the receiver
		holderTokensCount[to] += 1;

		// If the receiver has never owned a token, add them to the owners list
		if (!isOwnerAdded[to]) {
			isOwnerAdded[to] = true;
			owners.push(to);
		}
	}


    function setTokenPrice(uint256 tokenId, uint256 price) external {
        require(ownerOf(tokenId) == msg.sender, "Only the owner can set the price");
        tokenPrices[tokenId] = price;
        if(price > 0) {
            isTokenForSale[tokenId] = true;
        } else {
            isTokenForSale[tokenId] = false;
        }
    }

    function purchaseToken(address from, uint256 tokenId) public payable {
        require(ownerOf(tokenId) == from, "Token not owned by seller");
        require(isTokenForSale[tokenId], "Token is not for sale");

        uint256 sellingPrice = tokenPrices[tokenId];
        require(sellingPrice > 0, "Token not for sale");
        require(msg.value >= sellingPrice, "Insufficient funds sent");

        uint256 royaltyAmount = sellingPrice * royaltyPercentage / 100;
        uint256 sellerAmount = sellingPrice - royaltyAmount;

        // Pay out the royalty
        address originalCreator = originalCreators[tokenId];
        payable(originalCreator).transfer(royaltyAmount);
        emit RoyaltyPaid(originalCreator, royaltyAmount);


        if (msg.value > sellingPrice) {
            payable(msg.sender).transfer(msg.value - sellingPrice);
        }

        // Transfer the remaining amount to the seller
        payable(from).transfer(sellerAmount);

        // Transfer the token
        super.transferFrom(from, msg.sender, tokenId);

        holderTokensCount[from] -= 1;
        holderTokensCount[msg.sender] += 1;

        if (!isOwnerAdded[msg.sender]) {
            isOwnerAdded[msg.sender] = true;
            owners.push(msg.sender);
        }

        // Reset the token price to indicate it's no longer for sale
        tokenPrices[tokenId] = 0;
        isTokenForSale[tokenId] = false;
    }

    function calculateTotalCost(uint256 tokenId) public view returns (uint256) {
        require(isTokenForSale[tokenId], "Token is not for sale");
        return tokenPrices[tokenId];
    }

    function distributeMATIC() external onlyOwner {
        require(_totalSupply > 0, "No NFTs have been minted");
        uint256 totalMATIC = maticDistributionLimit;
        require(totalMATIC > 0, "MATIC distribution limit must be set");
        require(address(this).balance >= totalMATIC, "Contract does not have enough MATIC");

        for(uint256 i = 0; i < _totalSupply; i++) {
            uint256 tokenId = tokenIds[i];
            address holder = ownerOf(tokenId);
            uint256 holderTokenCount = holderTokensCount[holder];
            
            require(holderTokenCount > 0, "Holder does not own any tokens");
            
            //uint256 amount = (totalMATIC * holderTokenCount) / _totalSupply;
            uint256 amount = (totalMATIC) / _totalSupply;
            if(amount > 0) {
                (bool success, ) = payable(holder).call{value: amount}("");
                if (!success) {
                    emit TransferFailed(holder, amount);
                    continue;
                }
            }
        }

        emit MATICDistributed(totalMATIC);
    }

    event MATICReceived(address sender, uint256 amount);
    receive() external payable {
        // This function enables the contract to receive MATIC
        emit MATICReceived(msg.sender, msg.value);
    }

    function supportsInterface(bytes4 interfaceId) public view virtual override(ERC721Upgradeable, AccessControlUpgradeable) returns (bool) {
        return super.supportsInterface(interfaceId);
    }
}
