    "contracts/PingPongGame.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

contract PingPongGame {
    address public tokenAddress;
    uint256 public tokenAmount = 0; // Make tokenAmount public
    address public owner; // Make owner public

    mapping(address => uint256) private userDetails;

    constructor(address _tokenAddress) {
        tokenAddress = _tokenAddress; // Removed unnecessary payable cast
        owner = msg.sender;
    }

    modifier ownerRequire() {
        require(msg.sender == owner, "Only owner can call this function");
        _;
    }

    function reward(uint256 _points) public {
        address currentUser = msg.sender;
        userDetails[currentUser] += _points;
        // token transfer
        IERC20(tokenAddress).transfer(currentUser, _points); // Use currentUser, not payable(msg.sender)
    }

    function getReward() view public returns (uint256) {
        address currentUser = msg.sender;
        return userDetails[currentUser];
    }

    function addToken(uint256 _amount) public ownerRequire {
    // Token approval step
    IERC20(tokenAddress).approve(address(this), _amount);
    
    // Token deposit
    IERC20(tokenAddress).transferFrom(msg.sender, address(this), _amount);
    tokenAmount += _amount; // Update tokenAmount
}
    function getToken() view public ownerRequire returns (uint256) {
        return tokenAmount;
    }
}"
    }
