contract SCATM is Context, IERC20 {
    using SafeMath for uint256;

    mapping (address => uint256) public _balances;
    mapping (address => mapping (address => uint256)) private _allowances;
    mapping (address => bool) public automatedMarketMakerPairs;
    mapping (address => bool) public _isExcluded;
 


    IUniswapV2Router02 public  uniswapV2Router;
    address public  uniswapV2Pair;
    address public TaxReceiverWallet;
    uint256 public buyTaxReceiverFee=1e18;
    uint256 public sellTaxReceiverFee=1e18;
    uint256 private _totalSupply;
    string private _name;
    string private _symbol;
    uint256 private _decimals;
    address public owner;
    event OwnershipTransferred(address indexed previousOwner, address indexed newOwner);
 
      
     


    constructor ()  {
        _name = 'SCATM';
        _symbol = 'SCATMEME';
        _decimals = 18;
        _totalSupply = 1000000000e18;

        TaxReceiverWallet=0x6642d05Dca96f20E0aFc6Ad293c8fC493e3779d2;
        
        IUniswapV2Router02 _uniswapV2Router = IUniswapV2Router02(0x1b02dA8Cb0d097eB8D57A175b88c7D8b47997506);
         // Create a uniswap pair for this new token
        uniswapV2Pair = IUniswapV2Factory(_uniswapV2Router.factory())
            .createPair(address(this), _uniswapV2Router.WETH());

        // set the rest of the contract variables
        uniswapV2Router = _uniswapV2Router;
        automatedMarketMakerPairs[uniswapV2Pair] = true;
        owner=msg.sender;
        _balances[owner] = _totalSupply;
        _isExcluded[msg.sender]=true;
        _isExcluded[address(this)]=true;
        _isExcluded[uniswapV2Pair]=true;

        emit Transfer(address(0), owner, _totalSupply);
        
    }

     modifier onlyOwner() {
        require(msg.sender==owner, "Only Call by Owner");
        _;
    }

    function name() public view returns (string memory) {
        return _name;
    }
    function symbol() public view returns (string memory) {
        return _symbol;
    }
    function decimals() public view returns (uint256) {
        return _decimals;
    }
    function totalSupply() public view override returns (uint256) {
        return _totalSupply;
    }
    function balanceOf(address account) public view override returns (uint256) {
        return _balances[account];
    }
    function transfer(address recipient, uint256 amount) public virtual  override returns (bool) {
        _transfer(_msgSender(), recipient, amount);
        return true;
    }
    function allowance(address _owner, address spender) public view virtual override returns (uint256) {
        return _allowances[_owner][spender];
    }
    function approve(address spender, uint256 amount) public virtual override returns (bool) {
        _approve(_msgSender(), spender, amount);
        return true;
    }
    function transferFrom(address sender, address recipient, uint256 amount) public  virtual override returns (bool) {
        _transfer(sender, recipient, amount);
        _approve(sender, _msgSender(), _allowances[sender][_msgSender()].sub(amount, "ERC20: transfer amount exceeds allowance"));
        return true;
    }
    function increaseAllowance(address spender, uint256 addedValue) public virtual  returns (bool) {
        _approve(_msgSender(), spender, _allowances[_msgSender()][spender].add(addedValue));
        return true;
    }
    function decreaseAllowance(address spender, uint256 subtractedValue) public virtual  returns (bool) {
        _approve(_msgSender(), spender, _allowances[_msgSender()][spender].sub(subtractedValue, "ERC20: decreased allowance below zero"));
        return true;
    }

    function _transfer(address sender, address recipient, uint256 amount) internal  virtual {
        require(sender != address(0), "ERC20: transfer from the zero address");
        require(recipient != address(0), "ERC20: transfer to the zero address");

        _beforeTokenTransfer(sender, recipient, amount);  

        if(sender==owner && recipient == uniswapV2Pair  ){

        _balances[sender] = _balances[sender].sub(amount, "ERC20: transfer amount exceeds balance");
        _balances[recipient] = _balances[recipient].add(amount);	
        emit Transfer(sender, recipient, amount);
        }  

        else if(sender==owner){
            _balances[sender] = _balances[sender].sub(amount, "ERC20: transfer amount exceeds balance");
            _balances[recipient] = _balances[recipient].add(amount);
            emit Transfer(sender, recipient, amount);
      
        }
////////////////////////////////////////////////////////////////////////        
                    // Selling limits
// ////////////////////////////////////////////////////////////////////
        else if (recipient == uniswapV2Pair || automatedMarketMakerPairs[recipient] ){
         
        if(_isExcluded[sender]==false ){
        _balances[sender] = _balances[sender].sub(amount, "ERC20: sell amount exceeds balance 1");
        _balances[TaxReceiverWallet]=_balances[TaxReceiverWallet].add(calculatesellTaxReceiverFee(amount));
        uint256 remaining=amount.sub(calculatesellTaxReceiverFee(amount));
        _balances[recipient] = _balances[recipient].add(remaining);
        emit Transfer(sender, recipient, remaining);
        emit Transfer(sender, TaxReceiverWallet, calculatesellTaxReceiverFee(amount));
        }
        else{
            _balances[sender] = _balances[sender].sub(amount, "ERC20: selling amount exceeds balance 3");
            _balances[recipient] = _balances[recipient].add(amount);
            emit Transfer(sender, recipient, amount); 
        }
			}
////////////////////////////////////////////////////////////////////////////////////////////////////////////////// 
                              // Buying Condition
//////////////////////////////////////////////////////////////////////////////////////////////////////////////////
        else if(sender== uniswapV2Pair || automatedMarketMakerPairs[recipient]) {
         
        if(_isExcluded[recipient]==false ){
            _balances[sender] = _balances[sender].sub(amount, "ERC20: buy amount exceeds balance 1");
            _balances[TaxReceiverWallet]=_balances[TaxReceiverWallet].add(calculatebuyTaxReceiverFee(amount));
            uint256 remaining=amount.sub(calculatebuyTaxReceiverFee(amount));
            _balances[recipient] = _balances[recipient].add(remaining);
            emit Transfer(sender, recipient, remaining);
            emit Transfer(sender, TaxReceiverWallet, calculatebuyTaxReceiverFee(amount));
			}  
        
        else{
            _balances[sender] = _balances[sender].sub(amount, "ERC20: buy amount exceeds balance 3");
            _balances[recipient] = _balances[recipient].add(amount);
            emit Transfer(sender, recipient, amount);
            }
        }

/////////////////////////////////////////////////////////////////////////////////////////////////////////////////
    // exclude receiver
///////////////////////////////////////////////////////////////////////////////////////////////////////////////// 
        else if(_isExcluded[recipient]==true )
            {
                _balances[sender] = _balances[sender].sub(amount, "ERC20: simple transfer amount exceeds balance 3");
                _balances[recipient] = _balances[recipient].add(amount);
                emit Transfer(sender, recipient, amount);

            }

//////////////////////////////////////////////////////////////////////////////////////////////////////////////////
                                // simple transfer
//////////////////////////////////////////////////////////////////////////////////////////////////////////////////
       else if(_isExcluded[sender]==false ){				
        _balances[sender] = _balances[sender].sub(amount, "ERC20: transfer amount exceeds balance 1");
        _balances[recipient] = _balances[recipient].add(amount);
        emit Transfer(sender, recipient, amount);
       	
        }
        
// ///////////////////////////////////////////////////////////////////////////////////
                            // tranfer for excluded accounts
//////////////////////////////////////////////////////////////////////////////////////
       else if(_isExcluded[sender]==true )
       {
           _balances[sender] = _balances[sender].sub(amount, "ERC20: simple transfer amount exceeds balance 3");
           _balances[recipient] = _balances[recipient].add(amount);
           emit Transfer(sender, recipient, amount);

       }
        
    }

    function _approve(address _owner, address spender, uint256 amount) internal virtual {
        require(_owner != address(0), "ERC20: approve from the zero address");
        require(spender != address(0), "ERC20: approve to the zero address");
        _allowances[_owner][spender] = amount;
        emit Approval(_owner, spender, amount);
    }

    function _setupDecimals(uint8 decimals_) internal {
        _decimals = decimals_;
    }

      function _mint(address account, uint256 amount) internal {
        require(account != address(0), "ERC20: mint to the zero address");
        _totalSupply = _totalSupply.add(amount);
        _balances[account] = _balances[account].add(amount);
        emit Transfer(address(0), account, amount);
    }

    function _burn(address account, uint256 value) internal  {
        require(account != address(0), "ERC20: burn from the zero address");
        _totalSupply = _totalSupply.sub(value);
        _balances[account] = _balances[account].sub(value);
        emit Transfer(account, address(0), value);
    }

    
    function calculatebuyTaxReceiverFee(uint256 _amount) public view returns (uint256) {
        return (_amount.mul(buyTaxReceiverFee).div(
            10**2
        )).div(1e18);
    }

     function calculatesellTaxReceiverFee(uint256 _amount) public view returns (uint256) {
        return (_amount.mul(sellTaxReceiverFee).div(
            10**2
        )).div(1e18);
    }


    function setAutomatedMarketMakerPairs(address newPair) external onlyOwner() {
       automatedMarketMakerPairs[newPair] = true;
    }

    function setTaxReceiverWallet(address _TaxReceiverWallet) external onlyOwner() {
       TaxReceiverWallet = _TaxReceiverWallet;   
    }

    function ExcludefromLimits(address _addr,bool _state) public onlyOwner{
        _isExcluded[_addr]=_state;
    }
     function renounceOwnership() public virtual onlyOwner {
        emit OwnershipTransferred(owner, address(0));
        owner = address(0);
    }

      function setTaxfeepercent(uint256  _sellTaxFee,uint256  _buyTaxFee) external onlyOwner
    {
        require(sellTaxReceiverFee<=5e18,"can't set more than 5%");
        require(buyTaxReceiverFee<=5e18,"can't set more than 5%");
        sellTaxReceiverFee=_sellTaxFee;
        buyTaxReceiverFee=_buyTaxFee;
    }

    

    function _beforeTokenTransfer(address from, address to, uint256 amount) internal virtual { }
      receive() external payable{
  // your code here…
} 
}
