{{
  "language": "Solidity",
  "sources": {
    "contracts/WORLD.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity >=0.6.0 <0.8.0;
pragma experimental ABIEncoderV2;

import "@openzeppelin/contracts/math/SafeMath.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "./interfaces/IGAME_ERC20.sol";
import "./interfaces/IGAME_Game.sol";
import "./interfaces/IUniswapV2Router02.sol";
import "./interfaces/IGenesis.sol";
import "./interfaces/ILocalContract.sol";
import "./common/NFTbase.sol";

/// @title ERC-1155 bonded NFTs
/// @author Paul Barclay, Daniel Lee
/// @notice You can use this contract for bonded NFT sales
/// @dev All function calls are currently implemented without side effects
contract WORLD is NFTbase, ReentrancyGuard, ILocalContract {
    using SafeMath for uint256;

    bool public burningActive;
    bool public mintingActive;

    IGAME_ERC20 public gameToken;
    IUniswapV2Router02 public uniswapRouter;

    address public feeReceiver;
    address public foundationAddress;
    address public governanceAddress;
    IGENESIS public genesis;

    mapping(uint256 => uint256) public saleStarts;
    mapping(uint256 => uint256) public tokenSupply;
    mapping(address => uint256) public userUpdateTimes;
    mapping(address => uint256) public userEmissions;
    mapping(address => uint256) public userTotals;
    uint256 public systemUpdateTime;
    uint256 public systemTotal;
    uint256 public startTime;
    uint256 public maxBuyAmount = 10;

    uint256 public constant twoYears = 63113904;
    uint256 public constant sevenDays = 604800;
    uint256 public constant twentyTwoDecimals = 10**22;

    uint256 public minimumPrice = 500 * 10**18; // << stored in GAME (so 500*10^18)
    uint256 public dutchStartPrice = 500 * 10**18;
    uint256 public dutchDuration = 4000;
    uint256 public priceDivisor = 25;
    address public deadAddress = 0x000000000000000000000000000000000000dEaD;

    event MinimumPriceUpdated(uint256 minimumPrice);
    event DutchStartPriceUpdated(uint256 dutchStartPrice);
    event DutchDurationUpdated(uint256 dutchDuration);

    event TokenData(uint256 tokenId, uint256 onSaleDate);

    event TokenBought(
        address indexed to,
        uint256 id,
        uint256 pricePaid,
        uint256 amount,
        uint256 nextPrintPrice,
        uint256 nextBurnPrice,
        uint256 tokenSupply,
        uint256 reserve
    );

    event TokenBurned(
        address indexed to,
        uint256 id,
        uint256 priceReceived,
        uint256 nextPrintPrice,
        uint256 nextBurnPrice,
        uint256 printsSupply,
        uint256 reserve
    );

    event EmissionUpdated(
        address user,
        uint256 amount
    );

    event EmissionCollected(
        address user,
        uint256 amount
    );

    modifier beforeBuy(uint256 tokenId) {
        require(mintingActive, "minting is not active");
        require(block.timestamp >= saleStarts[tokenId] && saleStarts[tokenId] > 0, "sale has not started yet");
        _;
    }

    constructor(
        address foundation_,
        address governance_,
        address genesisERC20_,
        address gameToken_,
        address feeReceiver_
    ) payable NFTbase("no_url_set") {
        // constructor arguments
        foundationAddress = foundation_;
        governanceAddress = governance_;
        genesis = IGENESIS(genesisERC20_);
        gameToken = IGAME_ERC20(gameToken_);
        feeReceiver = feeReceiver_;

        systemUpdateTime = block.timestamp;
    }

    function setUniswapRouter(address uniswapRouter_) public isGlobalAdmin {
        uniswapRouter = IUniswapV2Router02(uniswapRouter_);
    }

    function setMinimumPrice(uint256 minimumPrice_) public isGlobalAdmin {
        minimumPrice = minimumPrice_;
        emit MinimumPriceUpdated(minimumPrice_);
    }

    function setDutchStartPrice(uint256 dutchStartPrice_) public isGlobalAdmin {
        dutchStartPrice = dutchStartPrice_;
        emit DutchStartPriceUpdated(dutchStartPrice_);
    }

    function setDutchDuration(uint256 dutchDuration_) public isGlobalAdmin {
        dutchDuration = dutchDuration_;
        emit DutchDurationUpdated(dutchDuration_);
    }

    function setFeeReceiver(address account) public isGlobalAdmin {
        feeReceiver = account;
    }

    function setFoundationAddress(address account) public isGlobalAdmin {
        foundationAddress = account;
    }

    function setGenesisAddress(address account) public isGlobalAdmin {
        genesis = IGENESIS(account);
    }

    function setTokenMintingActive(bool active) public isGlobalAdmin {
        mintingActive = active;
    }

    function setTokenBurningActive(bool active) public isGlobalAdmin {
        burningActive = active;
    }

    function setMaxBuyAmount(uint256 amount) public isGlobalAdmin {
        maxBuyAmount = amount;
    }

    function setPriceDivisor(uint256 newDivisor) public isGlobalAdmin {
        require(block.timestamp < saleStarts[1], "must before first token sale");
        priceDivisor = newDivisor;
    }

    function getMinimumPrice(uint256 tokenId) public view returns (uint256) {
        uint256 saleStartTime = saleStarts[tokenId];
        uint256 dutchEnd = saleStartTime.add(dutchDuration);
        uint256 timeNow = block.timestamp;

        if (timeNow >= dutchEnd)
            return minimumPrice;

        if(timeNow <= saleStartTime)
            return dutchStartPrice > minimumPrice ? dutchStartPrice : minimumPrice;

        uint256 dutchPrice = dutchStartPrice.mul(dutchEnd.sub(timeNow)).div(dutchDuration);
        return dutchPrice > minimumPrice ? dutchPrice : minimumPrice;
    }

    function getBuyPrice(uint256 tokenId, uint256 buyAmount) public view returns (uint256 buyPrice) {
        uint256 supply = tokenSupply[tokenId];
        uint256 minPrice = getMinimumPrice(tokenId);
        for(uint256 i = 1; i <= buyAmount; i++) {
          uint256 printPrice = getPrintPrice(supply + i);
          buyPrice = buyPrice.add(printPrice > minPrice ? printPrice : minPrice);
        }
        return buyPrice;
    }

    function getSellPrice(uint256 tokenId) public view returns (uint256) {
        return getPrintPrice(tokenSupply[tokenId]).mul(95).div(100);
    }

    function getPrintPrice(uint256 printNumber) public view returns (uint256) {
        uint256 price = (printNumber**3).mul(1000).div(9**8);
        price = price.add(printNumber.mul(125));
        price = price.add(50000);
        price = price.div(priceDivisor).mul(10**17);
        return price;
    }

    function updateTokenData(
        uint256 tokenId,
        uint256 onSaleDate
    ) public isTokenDataManager {
        require(tokenId > 0, "token Ids start at 1");
        require(saleStarts[tokenId] == 0 || saleStarts[tokenId] >= block.timestamp, "token sale must not have started");
        require(onSaleDate > block.timestamp, "sale date must be in the future");

        if (tokenId == 1) {
            startTime = onSaleDate.add(sevenDays);
            for (uint256 i = 2; i <= 24; i++) {
                require(saleStarts[i] == 0 || saleStarts[i] >= onSaleDate, "token 1 can't go on sale after other tokens");
            }
        } else {
            uint256 minOnSaleDate = saleStarts[1];
            require(minOnSaleDate > 0, "can't define this token until token 1 is defined");
            if (tokenId > 24) {
                require(block.timestamp >= minOnSaleDate, "can't update this token until token 1 goes on sale");
                // This enforces 24 tokens + 1 per 28 days.
                minOnSaleDate = minOnSaleDate.add(tokenId.sub(24).mul(sevenDays * 4));
            }
            require(onSaleDate >= minOnSaleDate, "can't sell this token before its minimum on sale date");
        }

        saleStarts[tokenId] = onSaleDate;

        emit TokenData(tokenId, onSaleDate);
    }

    function buyNFTwithGAME(
      uint256 tokenId, 
      uint256 pricePaid, 
      uint256 buyAmount
    ) public beforeBuy(tokenId) {
        address sender = _msgSender();
        uint256 price = getBuyPrice(tokenId, buyAmount);

        gameToken.transferByContract(sender, address(this), pricePaid);

        _buyNFT(tokenId, price, pricePaid, sender, buyAmount);
    }

    function buyNFTwithMatic(
      uint256 tokenId, 
      address[] calldata path, 
      uint256 buyAmount
    ) public payable nonReentrant beforeBuy(tokenId) {
        require(path.length > 1 && path[path.length - 1] == address(gameToken), "invalid path");

        address sender = _msgSender();
        uint256 price = getBuyPrice(tokenId, buyAmount);

        uint256[] memory amounts = uniswapRouter.swapExactETHForTokens{value: msg.value}(
            price,
            path,
            address(this),
            block.timestamp + 100
        );

        uint256 pricePaid = amounts[amounts.length - 1];
        _buyNFT(tokenId, price, pricePaid, sender, buyAmount);
    }

    function buyNFTwithERC20(
        uint256 tokenId,
        uint256 erc20amount,
        address[] calldata path,
        uint256 buyAmount
    ) public nonReentrant beforeBuy(tokenId) {
        require(path.length > 1 && path[path.length - 1] == address(gameToken), "invalid path");

        address sender = _msgSender();
        uint256 price = getBuyPrice(tokenId, buyAmount);
        address pathStart = path[0];

        require(IERC20(pathStart).transferFrom(sender, address(this), erc20amount));
        require(IERC20(pathStart).approve(address(uniswapRouter), erc20amount));

        uint256[] memory amounts = uniswapRouter.swapExactTokensForTokens(
            erc20amount,
            price,
            path,
            address(this),
            block.timestamp + 100
        );

        uint256 pricePaid = amounts[amounts.length - 1];
        _buyNFT(tokenId, price, pricePaid, sender, buyAmount);
    }

    function burn(uint256 tokenId) public {
        require(burningActive, "burning is not active");
        require(balanceOf(_msgSender(), tokenId) >= 1, "sender must have at least one token");
        uint256 price = getSellPrice(tokenId);
        tokenSupply[tokenId] -= 1;
        _burn(_msgSender(), tokenId, 1);
        emit TokenBurned(
            _msgSender(),
            tokenId,
            price,
            getBuyPrice(tokenId, 1),
            getSellPrice(tokenId),
            tokenSupply[tokenId],
            gameToken.balanceOf(address(this))
        );
        gameToken.transferByContract(address(this), _msgSender(), price);
    }

    function getCurrentEmissions(address user) public view returns(uint256) {
        uint256 prevTime = userUpdateTimes[user];
        uint256 claimBalance = userTotals[user];
        uint256 currentTime = block.timestamp;
        uint256 emission = _calculateEmission(prevTime, currentTime, claimBalance);
        return emission + userEmissions[user];
    }

    function collectEmissions() public {
        // This can be called to collect the emissions for the caller's account
        //   and update the foundation and governance emissions.
        address user = _msgSender();
        _updateUserBalance(user, userTotals[user]);
        _updateSystemBalances(systemTotal);

        uint256 emission = userEmissions[user];
        userEmissions[user] = 0;
        genesis.mintToAddress(user, emission);
        emit EmissionCollected(user, emission);
    }

    function _updateSystemBalances(uint prevSysTotal) internal {
        uint256 prevTime = systemUpdateTime;
        uint256 currentTime = block.timestamp;
        systemUpdateTime = currentTime;
        uint256 foundationEmission = _calculateEmission(prevTime, currentTime, prevSysTotal);
        // governance emission starts 28 days after other rewards / 35 days after first token purchase;
        uint256 governanceEmission = _calculateEmission(prevTime.sub(2419200), currentTime.sub(2419200), prevSysTotal);

        if (foundationEmission > 0) genesis.mintToAddress(foundationAddress, foundationEmission);
        if (governanceEmission > 0) {
            // We divide the governance emission by 2 here rather than above, because it's slightly more gas efficient
            genesis.mintToAddress(governanceAddress, governanceEmission.div(2));
        }
    }

    function _calculateEmission(
        uint prevTime,
        uint currentTime,
        uint stakes
    ) internal view returns (uint256) {
        if (prevTime == currentTime || currentTime <= startTime || stakes == 0) return 0;
        uint time1 = prevTime <= startTime ? 0 : prevTime.sub(startTime);
        uint time2 = currentTime.sub(startTime);
        uint256 existingEmissionLevel = twentyTwoDecimals.mul(time1).div(time1.add(twoYears));
        uint256 newEmissionLevel = twentyTwoDecimals.mul(time2).div(time2.add(twoYears));
        return newEmissionLevel.sub(existingEmissionLevel).mul(stakes);
    }

    function _updateUserBalance(address user, uint prevTotal) internal {
        uint256 prevTime = userUpdateTimes[user];
        uint256 currentTime = block.timestamp;
        userUpdateTimes[user] = currentTime;
        uint256 emission = _calculateEmission(prevTime, currentTime, prevTotal);
        if (emission > 0) {
            userEmissions[user] += emission;
            emit EmissionUpdated(user, emission);
        }
    }

    function _buyNFT(
        uint256 tokenId,
        uint256 price,
        uint256 pricePaid,
        address sender,
        uint256 buyAmount
    ) internal {
        require(pricePaid >= price, "must pay at least the base price");
        require(buyAmount <= maxBuyAmount, "can't buy more than max #");

        uint256 basePrice = 0;
        uint256 newSupply = tokenSupply[tokenId];

        for(uint256 i = 1; i <= buyAmount; i++) {
          newSupply = newSupply.add(1);
          uint256 printPrice = getPrintPrice(newSupply);
          basePrice = basePrice.add(printPrice.mul(95).div(100));
        }
        uint256 fee = pricePaid.sub(basePrice);
        
        emit TokenBought(
            sender,
            tokenId,
            buyAmount,
            price,
            getBuyPrice(tokenId, 1),
            getSellPrice(tokenId),
            newSupply,
            gameToken.balanceOf(address(this))
        );

        tokenSupply[tokenId] = newSupply;
        _mint(sender, tokenId, buyAmount, "");

        if (fee > 0) {
            gameToken.transferByContract(address(this), feeReceiver, fee);
        }
    }

    function _beforeTokenTransfer(
        address operator,
        address from,
        address to,
        uint256[] memory ids,
        uint256[] memory amounts,
        bytes memory data
    ) internal override {
        uint256 totalChange = 0;
        for (uint256 i = 0; i < amounts.length; i++) {
            totalChange = totalChange.add(amounts[i]);
        }
        if (from == address(0) && to != address(0)) {
            uint256 prevSysTotal = systemTotal;
            _updateSystemBalances(prevSysTotal);
            systemTotal = prevSysTotal.add(totalChange);
        }
        if (to == address(0) && from != address(0)) {
            uint256 prevSysTotal = systemTotal;
            _updateSystemBalances(prevSysTotal);
            systemTotal = prevSysTotal.sub(totalChange);
        }
        if (to != address(0) && to != deadAddress) {
            uint256 prevToTotal = userTotals[to];
            _updateUserBalance(to, prevToTotal);
            userTotals[to] = prevToTotal.add(totalChange);
        }
        if (from != address(0) && to != deadAddress) {
            uint256 prevFromTotal = userTotals[from];
            _updateUserBalance(from, prevFromTotal);
            userTotals[from] = prevFromTotal.sub(totalChange);
        }
    }
}
"
    },
