    "contracts/common/NFTbase.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity >=0.6.0 <0.8.0;

import "@openzeppelin/contracts/utils/Strings.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/token/ERC1155/ERC1155.sol";

/**
 * @title ERC1155Mock
 * This mock just publicizes internal functions for testing purposes
 */
contract NFTbase is ERC1155, AccessControl {
    using Strings for uint256;

    bytes32 public constant TOKENDATA_MANAGER_ROLE = keccak256("TOKENDATA_MANAGER_ROLE");

    string _baseURI;

    modifier isGlobalAdmin() {
        require(hasRole(DEFAULT_ADMIN_ROLE, _msgSender()));
        _;
    }

    modifier isTokenDataManager() {
        require(hasRole(TOKENDATA_MANAGER_ROLE, _msgSender()));
        _;
    }

    constructor(string memory uri) ERC1155(uri) {
        _baseURI = uri;
        _setupRole(DEFAULT_ADMIN_ROLE, _msgSender());
        _setupRole(TOKENDATA_MANAGER_ROLE, _msgSender());
        _setRoleAdmin(TOKENDATA_MANAGER_ROLE, DEFAULT_ADMIN_ROLE);
    }

    mapping(uint256 => string) public _tokenURIs;

    function setURI(string memory newuri, uint256 id) public isGlobalAdmin() {
        _tokenURIs[id] = newuri;
    }

    function setBaseURI(string memory newuri) public isGlobalAdmin() {
        _baseURI = newuri;
    }

    function uri(uint256 tokenid) public view override returns (string memory) {
        string memory _tokenURI = _tokenURIs[tokenid];

        // If there is no base URI, return the token URI.
        if (bytes(_baseURI).length == 0) {
            return _tokenURI;
        }
        // If both are set, concatenate the baseURI and tokenURI (via abi.encodePacked).
        if (bytes(_tokenURI).length > 0) {
            return string(abi.encodePacked(_baseURI, _tokenURI));
        }
        // If there is a baseURI but no tokenURI, concatenate the tokenID to the baseURI.
        return string(abi.encodePacked(_baseURI, tokenid.toString()));
    }
}
"
