pragma solidity ^0.8.2;




contract SimpleEIP712 is Ownable {
    string private constant EIP712_DOMAIN =
        "EIP712Domain(string name,string version,uint256 chainId,address verifyingContract)";
    bytes32 private constant EIP712_DOMAIN_TYPEHASH =
        keccak256(abi.encodePacked(EIP712_DOMAIN));

    address public SIGNER;
    mapping(bytes32 => bool) public USED_SIGNATURES;
    mapping(address => mapping(uint256 => bool)) public USED_NONCES;

    struct EIP712Domain {
        string name;
        string version;
        uint256 chainId;
        address verifyingContract;
    }

    bytes32 internal domainSeparator;

    constructor(string memory name, string memory version, address _signer) Ownable(msg.sender) {
        SIGNER = _signer;
        domainSeparator = keccak256(
            abi.encode(
                EIP712_DOMAIN_TYPEHASH,
                keccak256(bytes(name)),
                keccak256(bytes(version)),
                block.chainid,
                address(this)
            )
        );
    }

    function getDomainSeparator() public view returns (bytes32) {
        return domainSeparator;
    }

    // The structure for a user withdrawal operation
    bytes32 public constant USER_WITHDRAW_TYPE = keccak256("UserWithdraw(address token,uint256 amount,uint256 nonce,address user)");

    // Events to log the state changes
    event OwnerDeposited(address indexed tokenAddress, uint256 amount);
    event OwnerWithdrew(address indexed tokenAddress, uint256 amount);
    event UserWithdrew(address indexed user, address indexed tokenAddress, uint256 amount);

    // Function to allow the owner to deposit tokens into the contract
    function ownerDeposit(address tokenAddress, uint256 amount) public onlyOwner {
        IERC20 token = IERC20(tokenAddress);
        token.transferFrom(msg.sender, address(this), amount);

        emit OwnerDeposited(tokenAddress, amount);
    }

    // Function to allow the owner to withdraw tokens from the contract
    function ownerWithdraw(address tokenAddress, uint256 amount) public onlyOwner {
        IERC20 token = IERC20(tokenAddress);
        token.transfer(msg.sender, amount);

        emit OwnerWithdrew(tokenAddress, amount);
    }

    // Function to allow a user to withdraw tokens with a valid signature
    function userWithdraw(address tokenAddress, uint256 amount, uint256 nonce, bytes memory signature) public {
        bytes32 digest = keccak256(
            abi.encodePacked(
                "\x19\x01",
                getDomainSeparator(),
                keccak256(abi.encode(USER_WITHDRAW_TYPE, tokenAddress, amount, nonce, msg.sender))
            )
        );

        require(ECDSA.recover(digest, signature) == SIGNER, "Invalid signature");
        require(!USED_SIGNATURES[digest], "Signature has already been used");
        require(!USED_NONCES[msg.sender][nonce], "Nonce has already been used");
        USED_SIGNATURES[digest] = true;
        USED_NONCES[msg.sender][nonce] = true;

        // Perform the token transfer
        IERC20 token = IERC20(tokenAddress);
        token.transfer(msg.sender, amount);

        emit UserWithdrew(msg.sender, tokenAddress, amount);
    }
}
