// File: src\contracts\Samsara\NftCrud.sol

// Created by Bansclaw

pragma solidity ^0.8.19;
abstract contract NftCrud is SamsaraStorage, ERC2981, AccessControl, Pausable, ERC1155Burnable {

    // Events
    event NftCreated(address sender, Nft nft);
    event NftUpdated(address sender, Nft nft);
    event NftMinted(address to, uint nftId, uint amount);
    event NftPurchased(address purchaser, uint nftId, uint amount);


    function getNfts(uint32[] memory nftIds) public view returns (Nft[] memory) {
        Nft[] memory values = new Nft[](nftIds.length);

        for (uint32 i = 0; i < nftIds.length; i++) {
            values[i] = nfts[nftIds[i]];
            //values[i].supply = super.totalSupply(nftIds[i]);
        }

        return values;
    }

    function addNft(NftCreate calldata nftCreate) public onlyRole(DEFAULT_ADMIN_ROLE) returns (uint id)
    {
        require(nftCreate.maxSupply > 0, "Supply cant be empty");
        
        Nft memory nft = Nft({
            id: nfts.length,
            author: msg.sender,
            ipfsHash: nftCreate.ipfsHash,
            maxSupply: nftCreate.maxSupply,
            price: nftCreate.price
        });

        super._setTokenRoyalty(nft.id, msg.sender, nftCreate.authorRoyalty);
        
        nfts.push(nft);

        emit NftCreated(_msgSender(), nft);

        return nft.id;
    }

    function updateNft(uint64 nftId, NftUpdate calldata nftUpdate) public {
        require(nfts.length > nftId, "Nft not initialized!");
        require(nfts[nftId].author == msg.sender, "Only author can modify this Nft."); // are you sure? or do we handle this in BE?

        nfts[nftId].ipfsHash = nftUpdate.ipfsHash;
        nfts[nftId].maxSupply = nftUpdate.maxSupply;

        emit NftUpdated(msg.sender, nfts[nftId]);
    }

    function _beforeTokenTransfer(address operator, address from, address to, uint256[] memory ids, uint256[] memory amounts, bytes memory data)
        internal virtual whenNotPaused override(ERC1155, ERC1155Supply)
    {
        super._beforeTokenTransfer(operator, from, to, ids, amounts, data);
    }

    function supportsInterface(bytes4 interfaceId)
        public
        virtual
        view
        override(ERC1155, ERC2981, AccessControl)
        returns (bool)
    {
        return super.supportsInterface(interfaceId);
    }
}

// File: src\contracts\Samsara\Samsara.sol


pragma solidity ^0.8.19;
// import "hardhat/console.sol";
/// @custom:security-contact marco.medrano@bansclaw.com
contract Samsara is NftCrud, DefaultPriceCalculator {

    string public constant name = "Bansclaw Samsara V1";
    string public constant description = "Bansclaw Samsara is the market for bambix and community tokens.";

    using SafeERC20 for IERC20;

    constructor()
    ERC1155("https://ipfs.io/ipfs/") 
    {
    	_grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        bansclawWallet = msg.sender;
    }

    function setURI(string memory newuri) public onlyRole(DEFAULT_ADMIN_ROLE) {
        _setURI(newuri);
    }

    function setNftUri(uint nftId, string memory newUri) public {
        require(address(nftUri) != address(0), "INftUri needs to be set");
        require(nfts[nftId].author == msg.sender, "Only author can modify this Nft.");

        nftUri.setUri(nftId, newUri);
    }

    function uri(uint nftId) override public view returns (string memory) {
        require(nfts.length > nftId, "Nft not created");

        if(address(nftUri) != address(0)) return nftUri.uri(nftId);

        return string(abi.encodePacked(super.uri(0), nfts[nftId].ipfsHash));
    }

    function pause() public onlyRole(DEFAULT_ADMIN_ROLE) {
        _pause();
    }

    function unpause() public onlyRole(DEFAULT_ADMIN_ROLE) {
        _unpause();
    }

    // Mainly used for airdrops.
    function mintToByAdmin(address to, uint64 nftId, uint amount) onlyRole(DEFAULT_ADMIN_ROLE) public
    {
        internalMint(to, nftId, amount);
    }

    // Mint others.
    function mintByAdmin(uint nftId, uint amount) onlyRole(DEFAULT_ADMIN_ROLE) public
    {
        internalMint(msg.sender, nftId, amount);
    }

//uint32 id, uint256 amount) public onlyActive(id) nonReentrant
    function mint(uint nftId, uint amount) public payable
    {
        require(currencyAddress != address(0), "Currency Payment must be set first.");

        IPriceCalculator priceCalc = getPriceCalculator(nftId);
        uint _totalPrice = priceCalc.totalPrice(nftId, amount);
        //uint weiSent = msg.value; // we just calculate ourselves, and then try to transfer ownershp of the currency
        // which is different than what we ussed to (up to this point), but only good way of doing it
        uint weiSent = amount * _totalPrice;
        address buyer = msg.sender;

        // have to check and ensure number of decimal values
        uint decimal = IERC20Metadata(currencyAddress).decimals();
        // check if decimal is less than 18, if so, we need to convert the difference
        if(decimal < 18) {
            // if using USDT, 6 decimal value... so 18 - 6 = 12
            // thus, would need to kill 12 0's in order to properly compare the two
            _totalPrice = _totalPrice / (10**(18-decimal));
        }

        // uint bal = IERC20(currencyAddress).balanceOf(msg.sender);
        uint allowance = IERC20(currencyAddress).allowance(msg.sender, address(this));
        
        // require(bal >= _totalPrice, "Not enough to cover this process!");
        require(allowance >= _totalPrice, "Not enough allowance has been set!"); 
        require(amount > 0, "Amount must be greater than zero");
        require(amount<= amount, "Amount not available");

        // since this is different, we dont have to run this
        // require(weiSent >= _totalPrice, "Insufficient funds");
        require(totalSupply(nftId) + amount <= nfts[nftId].maxSupply,  "The total supply you want to mint needs to be less than max supply!");

        // Pay
        IERC20(currencyAddress).safeTransferFrom(msg.sender, address(bansclawWallet), _totalPrice);

        internalMint(msg.sender, nftId, amount);

        // Refund any excess payment back to the buyer
        if (weiSent > _totalPrice) {
            //console.log("Refunding ", weiSent - _totalPrice);
            IERC20(currencyAddress).safeTransferFrom(address(bansclawWallet), msg.sender, weiSent - _totalPrice);
        }

        emit NftPurchased(buyer, nftId, amount);
    }

    function mintBatch(uint[] memory ids, uint[] memory amounts) public onlyRole(DEFAULT_ADMIN_ROLE)
    {
        _mintBatch(msg.sender, ids, amounts,  bytes(""));
    }

    function internalMint(address to, uint nftId, uint amount) internal
    {
        require(nfts.length > nftId, "Nft not created");
        require(totalSupply(nftId) + amount <= nfts[nftId].maxSupply,  "The  total supply to mint needs to be less than max supply!");

        _mint(to, nftId, amount, bytes(""));

        emit NftMinted(to, nftId, amount);
    }

    function balance() public view returns (uint) {
        return address(this).balance;
    }

    function setBansclawWallet(address _newAddress) external onlyRole(DEFAULT_ADMIN_ROLE) {
        bansclawWallet = _newAddress;
    }

    function setCurrencyPaymentAddress(address newPaymentAddress) external onlyRole(DEFAULT_ADMIN_ROLE)  {
        currencyAddress = newPaymentAddress;
    } 

    function setUriInterface(address _uriContract) onlyRole(DEFAULT_ADMIN_ROLE) public {
        nftUri = INftUriStorage(_uriContract);
    }

    function setPriceCalculator(uint nftId, address _contract) onlyRole(DEFAULT_ADMIN_ROLE) public {
        priceCalculators[nftId] = _contract;
    }

    function getPriceCalculator(uint nftId) public virtual view returns (IPriceCalculator) {
        address addressPriceCalc = priceCalculators[nftId];
        if(addressPriceCalc == address(0)) return IPriceCalculator(this);
        else return IPriceCalculator(addressPriceCalc);
    }

    function _beforeTokenTransfer(address operator, address from, address to, uint[] memory ids, uint[] memory amounts, bytes memory data)
        internal whenNotPaused override(NftCrud, ERC1155Supply)
    {
        super._beforeTokenTransfer(operator, from, to, ids, amounts, data);
    }

    function supportsInterface(bytes4 interfaceId) public virtual view
    override(NftCrud, ERC1155)
    returns (bool)
    {
        return super.supportsInterface(interfaceId);
    }
}
