      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.13;

import {ISafeProtocolManager} from "safe-protocol/interfaces/Manager.sol";
import {SafeTransaction, SafeProtocolAction} from "safe-protocol/DataTypes.sol";
import {PLUGIN_PERMISSION_EXECUTE_CALL} from "safe-protocol/common/Constants.sol";
import {ISafeProtocolPlugin} from "safe-protocol/interfaces/Modules.sol";

import {AutomationCompatibleInterface} from "@chainlink/v0.8/automation/interfaces/AutomationCompatibleInterface.sol";

import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";

import {EnumerableSet} from "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";
import {Strings} from "@openzeppelin/contracts/utils/Strings.sol";

import {IRouter} from "./interfaces/IRouter.sol";

/// @title AutoSingleSwapperModule
contract AutoSingleSwapperModule is AutomationCompatibleInterface, ISafeProtocolPlugin {
    using EnumerableSet for EnumerableSet.AddressSet;

    struct ResponseApi {
        // Min amout expected of token out
        uint256 minOut;
        // Execution path
        bytes path;
    }

    struct SafeSwapConfig {
        // Token which is going to be sent to the router to process
        address tokenIn;
        // Token which is going to be received by gnosis safe
        address tokenOut;
        // Address where they tokenOut will be sent to
        address destination;
        // Time based condition of the cadence for the swap to be processed
        uint64 cadence;
        // Timestamp of the last time that tokens were swapped
        uint64 lastTokenProcessing;
    }

    /*//////////////////////////////////////////////////////////////////////////
                                   CONSTANTS
    //////////////////////////////////////////////////////////////////////////*/

    uint32 constant REFERRAL_CODE = 4242127944;

    /*//////////////////////////////////////////////////////////////////////////
                                   PUBLIC STORAGE
    //////////////////////////////////////////////////////////////////////////*/

    address public governance;

    address public manager;

    address public gelatoSender;

    address public routerSwap;

    /// @notice Address of contract that will execute the path
    address public executorSwap;

    /// @notice DON ID for the Functions DON to which the requests are sent
    bytes32 public donId;

    /// @notice The subscription ID that will be charged to service the request
    uint64 public subscriptionId;

    /// @notice The amount of gas that will be available for the fulfillment callback
    uint32 public callbackGasLimit;

    /// @notice Represents the inline JS code read in string and converted to bytes
    bytes public endpointFunction;

    /// @notice The last block where a trade request was performed
    uint256 public lastTradeRequestBlock;

    /// @dev Maps requestID -> gnosis safe
    mapping(bytes32 => address) public requestIdToSafe;

    /// @dev gnosis safe -> trade nonce
    mapping(address => uint256) public safeTradeNonce;

    /// @dev Maps gnosis safe -> SafeSwapConfig
    mapping(address => SafeSwapConfig) private safeConfigs;

    /// @dev Array of gnosis safe accepting the plugin for iteration
    EnumerableSet.AddressSet internal _safesSet;

    /*//////////////////////////////////////////////////////////////////////////
                                       ERRORS
    //////////////////////////////////////////////////////////////////////////*/

    error PluginNotAccepted(address safe);

    error NotManager(address origin);
    error NotGovernance(address origin);
    error NotTraderAgent(address origin);

    error ZeroAddressValue();
    error ZeroUintValue();

    error RequestIdNotTrigger(bytes32 requestId, uint256 timestamp);

    /*//////////////////////////////////////////////////////////////////////////
                                       EVENTS
    //////////////////////////////////////////////////////////////////////////*/

    event SafeUserTradeRequest(address indexed safeAddress, address tokenOut, bytes32 requestId, uint256 timestamp);
    event SafeUserTradeFilled(address indexed safeAddress, address tokenOut, bytes32 requestId, uint256 timestamp);

    /*//////////////////////////////////////////////////////////////////////////
                                     CONSTRUCTOR
    //////////////////////////////////////////////////////////////////////////*/
    constructor(
        address _manager,
        address _router,
        uint64 _subscriptionId,
        uint32 _callbackGasLimit,
        bytes32 _donId,
        address _routerSwap,
        address _executorSwap
    ) {
        // Safe Protocol config
        manager = _manager;

        // CL config
        subscriptionId = _subscriptionId;
        callbackGasLimit = _callbackGasLimit;
        donId = _donId;

        // Swap config
        routerSwap = _routerSwap;
        executorSwap = _executorSwap;

        governance = msg.sender;
    }

    /*//////////////////////////////////////////////////////////////////////////
                                      MODIFIERS
    //////////////////////////////////////////////////////////////////////////*/

    modifier onlyManager() {
        if (msg.sender != manager) revert NotManager(msg.sender);
        _;
    }

    modifier onlyGovernance() {
        if (msg.sender != governance) revert NotGovernance(msg.sender);
        _;
    }

    /// @notice Checks whether a call is from governance or gelato assigned `msg.sender`.
    modifier onlyTraderAgents() {
        if (msg.sender != governance && msg.sender != gelatoSender) revert NotTraderAgent(msg.sender);
        _;
    }

    /*//////////////////////////////////////////////////////////////////////////
                                    PUBLIC VIEWS
    //////////////////////////////////////////////////////////////////////////*/

    /// @notice method that is simulated by the keepers to see if any work actually needs to be performed.
    function checkUpkeep(bytes calldata) public view override returns (bool upkeepNeeded_, bytes memory performData_) {
        for (uint256 i; i < _safesSet.length();) {
            address safeAddr = _safesSet.at(i);

            if ((block.timestamp - safeConfigs[safeAddr].lastTokenProcessing) >= safeConfigs[safeAddr].cadence) {
                upkeepNeeded_ = true;
                // NOTE: encode the `safeAddr` & `tokenOut` based on safe configuration
                performData_ = abi.encode(safeAddr, safeConfigs[safeAddr].tokenOut);
                break;
            }
        }
    }

    /// @dev Returns all safes addresses whom accepted the module
    function getSafeSet() public view returns (address[] memory) {
        return _safesSet.values();
    }

    /// @notice A funtion that returns name of the plugin
    function name() external view override returns (string memory name) {
        return "AUTO_SWAPPER";
    }

    /// @notice A function that returns version of the plugin
    function version() external view override returns (string memory version) {
        return "v0.0.1";
    }

    function metadataProvider() external view override returns (uint256 providerType, bytes memory location) {}

    /// @notice A function that indicates permissions required
    function requiresPermissions() external view override returns (uint8 permissions) {
        return PLUGIN_PERMISSION_EXECUTE_CALL;
    }

    /// @dev Returns true if this contract implements the interface defined by interfaceId
    function supportsInterface(bytes4 interfaceId) external view override returns (bool) {
        return interfaceId == type(ISafeProtocolPlugin).interfaceId || interfaceId == 0x01ffc9a7;
    }

    function getSafeAddrPerRequestId(bytes32 _requestId) external view returns (address) {
        return requestIdToSafe[_requestId];
    }

    /*//////////////////////////////////////////////////////////////////////////
                        EXTERNAL FUNCTIONS: GOV CONFIG
    //////////////////////////////////////////////////////////////////////////*/

    /// @notice Setter for the JS logic interacting with the external API
    function setEndpointFunction(bytes calldata _endpointFunction) external onlyGovernance {
        endpointFunction = _endpointFunction;
    }

    /// @notice Setter for the amount of gas that will be available for the fulfillment callback
    function setCallbackGasLimit(uint32 _callbackGasLimit) external onlyGovernance {
        callbackGasLimit = _callbackGasLimit;
    }

    function setGelatoSender(address _gelatoSender) external onlyGovernance {
        gelatoSender = _gelatoSender;
    }

    /*//////////////////////////////////////////////////////////////////////////
                        EXTERNAL FUNCTIONS: MANAGER CONFIG
    //////////////////////////////////////////////////////////////////////////*/

    function setSafeConfigs(address _safe, bytes memory _config) external onlyManager {
        _safesSet.add(_safe);

        (address _tokenIn, address _tokenOut, address _destination, uint64 _cadence) =
            abi.decode(_config, (address, address, address, uint64));

        // NOTE: validate decode values
        // `_tokenOut` could be `address(0)` representing native tkn
        if (_destination == address(0)) revert ZeroAddressValue();
        if (_cadence == 0) revert ZeroUintValue();

        SafeSwapConfig storage configUpdate = safeConfigs[_safe];

        configUpdate.tokenIn = _tokenIn;
        configUpdate.tokenOut = _tokenOut;
        configUpdate.destination = _destination;
        configUpdate.cadence = _cadence;
    }

    /*//////////////////////////////////////////////////////////////////////////
                        EXTERNAL FUNCTIONS: CL KEEPERS
    //////////////////////////////////////////////////////////////////////////*/

    /// @param _performData The calldata to be passed to the upkeep function
    function performUpkeep(bytes calldata _performData) external override {
        (address safeAddress, address tokenOut) = abi.decode(_performData, (address, address));
        /// @dev uses the number of requests that the safe has sent as a nonce
        bytes32 requestId = keccak256(abi.encode(safeAddress, tokenOut, safeTradeNonce[safeAddress] + 1, block.number));
        requestIdToSafe[requestId] = safeAddress;
        lastTradeRequestBlock = block.number;
        emit SafeUserTradeRequest(safeAddress, tokenOut, requestId, block.timestamp);
    }

    /// @param _requestId The request ID, generated during `performUpkeep`
    /// @param _response Aggregated response from the web3 function
    function tradeFill(bytes32 _requestId, bytes memory _response) external onlyTraderAgents {
        address safe = requestIdToSafe[_requestId];
        if (safe == address(0)) revert RequestIdNotTrigger(_requestId, block.timestamp);

        SafeSwapConfig storage config = safeConfigs[safe];

        address tokenIn = config.tokenIn;
        uint256 tokenInBalance = IERC20(tokenIn).balanceOf(safe);

        // NOTE: length of the approvals + swap (router holds gnosis safe approvals)
        SafeProtocolAction[] memory calls = new SafeProtocolAction[](2);
        calls[0] = SafeProtocolAction({
            to: payable(tokenIn),
            value: 0,
            data: abi.encodeWithSelector(IERC20.approve.selector, routerSwap, tokenInBalance)
        });

        IRouter.swapTokenInfo memory swapTokenData = IRouter.swapTokenInfo({
            inputToken: tokenIn,
            inputAmount: tokenInBalance,
            inputReceiver: executorSwap,
            outputToken: config.tokenOut,
            outputQuote: 1,
            outputMin: 1,
            outputReceiver: safe
        });

        calls[1] = SafeProtocolAction({
            to: payable(routerSwap),
            value: 0,
            data: abi.encodeWithSelector(IRouter.swap.selector, swapTokenData, _response, executorSwap, REFERRAL_CODE)
        });

        SafeTransaction memory transaction = SafeTransaction({
            actions: calls,
            // NOTE: find uniqueness via `block.number`
            nonce: uint256(keccak256(abi.encode(safe, block.number))),
            metadataHash: bytes32(0)
        });

        ISafeProtocolManager(manager).executeTransaction(safe, transaction);

        emit SafeUserTradeFilled(safe, config.tokenOut, _requestId, block.timestamp);

        // Storage updates after exec
        requestIdToSafe[_requestId] = address(0);
        safeTradeNonce[safe] += 1;
        config.lastTokenProcessing = uint64(block.timestamp);
    }
}
