// File: contracts/ShareCoinHub.sol

pragma solidity 0.6.12;
pragma experimental ABIEncoderV2;







/**
 * @title ShareCoin Hub Contract
 */

contract ShareCoinHub is OwnableUpgradeable {
    using SafeMath for uint256;

    address public qid;
    address public qrToken;
    address public shareCoinSrc;

    uint256 public defaultRatio = 5e21;
    uint256 public defaultCreatorFee = 500; // 5 %
    uint256 public defaultDeployerFee = 25; // 0.25 %

    bool private isInitialized = false;

    address[] public shareCoinClones;
    mapping(uint256 => address) public shareCoinMap;

    address public deployer;

    struct History {
        address txOwner;
        uint256 targetPool;
        uint256 amount;
        uint256 price;
        uint256 historyType;
        uint256 timestamp;
    }
    History[] public historyQueue;

    event NewHistory(
        address indexed owner,
        uint256 target,
        uint256 amount,
        uint256 price,
        uint256 historyType,
        uint256 timestamp
    );

    modifier onlyIdContract() {
        require(
            msg.sender == owner || qid == msg.sender,
            "ShareCoin HUB: NOT_WHITELISTED"
        );
        _;
    }

    modifier onlyInitializing() {
        require(!isInitialized, "initialized");
        _;
        isInitialized = true;
    }

    /// @notice Contract constructor
    constructor() public {}

    function initialize(
        address _id,
        address _qrToken,
        address _shareCoinSrc
    ) public onlyInitializing {
        __Ownable_init();
        qid = _id;
        qrToken = _qrToken;
        shareCoinSrc = _shareCoinSrc;
    }

    function deployShareCoin(
        uint256 _fromTokenId
    ) external onlyIdContract returns (address) {
        require(shareCoinMap[_fromTokenId] == address(0), "already exist");
        address _newTarget = ClonesUpgradeable.clone(shareCoinSrc);
        shareCoinClones.push(_newTarget);
        shareCoinMap[_fromTokenId] = _newTarget;
        IShareCoin(_newTarget).initialize(
            qid,
            qrToken,
            defaultRatio,
            defaultDeployerFee,
            defaultCreatorFee,
            _fromTokenId,
            deployer
        );
    }

    function setVariables(
        address _qid,
        address _qrToken,
        uint256 _priceRatio,
        uint256 _deployerFee,
        uint256 _creatorFee,
        address _deployer
    ) external onlyOwner {
        qid = _qid;
        qrToken = _qrToken;
        defaultRatio = _priceRatio;
        defaultCreatorFee = _creatorFee;
        defaultDeployerFee = _deployerFee;
        deployer = _deployer;
    }

    function setShareCoinSrc(address _src) external onlyOwner {
        shareCoinSrc = _src;
    }

    function getShareCoinLength() public view returns (uint256) {
        return shareCoinClones.length;
    }

    function getUserBalances(
        address _userAddress
    ) public view returns (address[] memory, uint256[] memory) {
        uint256 shareCoinsCount = shareCoinClones.length;
        address[] memory shareCoinAddresses = new address[](shareCoinsCount);
        uint256[] memory shareCoinBalances = new uint256[](shareCoinsCount);
        for (uint256 i = 0; i < shareCoinsCount; i++) {
            shareCoinAddresses[i] = shareCoinClones[i];
            shareCoinBalances[i] = IERC20(shareCoinClones[i]).balanceOf(
                _userAddress
            );
        }
        return (shareCoinAddresses, shareCoinBalances);
    }

    function addHistory(
        address txOwner,
        uint256 targetPool,
        uint256 amount,
        uint256 price,
        uint256 historyType
    ) public {
        require(msg.sender == shareCoinMap[targetPool], "Invalid Owner");
        History memory _newHistory = History({
            txOwner: txOwner,
            targetPool: targetPool,
            amount: amount,
            price: price,
            historyType: historyType,
            timestamp: block.timestamp
        });

        historyQueue.push(_newHistory);

        if (historyType == 0) {
            if (IQrolliID(qid).balanceOf(txOwner) > 0) {
                uint256 _txOwnerId = IQrolliID(qid).tokenOfOwnerByIndex(
                    txOwner,
                    0
                );
                IQrolliID(qid).addShareCoinVolume(amount, _txOwnerId);
            }
            IQrolliID(qid).addShareCoinVolume(amount, targetPool);
        }
        emit NewHistory(
            txOwner,
            targetPool,
            amount,
            price,
            historyType,
            block.timestamp
        );
    }

    function getHistoryLength() public view returns (uint256) {
        return historyQueue.length;
    }

    function getHistory(
        uint256 _startIndex,
        uint256 _count
    ) public view returns (History[] memory) {
        History[] memory _histories = new History[](_count);
        for (uint256 i = 0; i < _count; i++) {
            _histories[i] = historyQueue[_startIndex + i];
        }
        return _histories;
    }

    uint256[50] private __gap;
}
