      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.7;

import '@openzeppelin/contracts/utils/cryptography/MerkleProof.sol';

import './MerkleAirdrop.sol';

contract OpenPack {
    address public constant BURN_ADDRESS = 0x000000000000000000000000000000000000dEaD;

    MerkleAirdrop public merkleAirdrop;

    modifier isReady() {
        require(merkleAirdrop.isRandomnessReady(), "MerkleAirdrop: randomness not ready");
        _;
    }

    constructor(address _merkleAirdrop) {
        merkleAirdrop = MerkleAirdrop(_merkleAirdrop);
    }

    function openPack(bytes32[] memory _proof, MerkleAirdrop.Pack memory _pack, uint256 _sealedPackId) public isReady {
        require(merkleAirdrop.ownerOf(_sealedPackId) == msg.sender, "MerkleAirdrop: invalid owner");

        merkleAirdrop.transferFrom(msg.sender, BURN_ADDRESS, _sealedPackId);

        require(_pack.packId == merkleAirdrop.getPackId(_sealedPackId), "MerkleAirdrop: invalid pack");

        bytes32 leaf = merkleAirdrop.getPackLeaf(_pack);
        require(MerkleProof.verify(_proof, merkleAirdrop.openedPackMerkleRoot(), leaf), "MerkleAirdrop: proof invalid");

        // TODO: pagination?
        for (uint256 i = 0; i < _pack.nfts.length; i++) {
            SlingshotNFT(_pack.nfts[i]).bulkMint(msg.sender, _pack.amounts[i]);
        }
    }
}
"
