    "contracts/MerkleAirdrop.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.7;

import '@openzeppelin/contracts/utils/cryptography/MerkleProof.sol';

import './Randomness.sol';
import './BaseNFT.sol';
import './NFTFactory.sol';
import './SlingshotAirdrop.sol';

contract MerkleAirdrop is BaseNFT, Randomness {
    using Counters for Counters.Counter;
    Counters.Counter internal _sealedPackIds;

    address public feeRecipient;
    address public airdrop;

    struct Pack {
        uint256 packId;
        address[] nfts;
        uint256[] amounts;
    }

    struct InitVars {
        address airdrop;
        uint256 maxSupply;
        uint256 saleAllocation;
        uint256 airdropAllocation;
        uint256 price;
        address feeRecipient;
    }

    bytes32 public sealedPackMerkleRoot;
    bytes32 public openedPackMerkleRoot;

    uint256 public immutable maxSupply;
    uint256 public price;
    uint256 public saleAllocation;
    // amount of pack that slingshot can mint and airdrop manually at wil, in exmple during discord call
    uint256 public airdropAllocation;

    modifier isReady() {
        require(isRandomnessReady(), "MerkleAirdrop: randomness not ready");
        _;
    }

    modifier onlyAirdrop() {
        require(msg.sender == airdrop, "MerkleAirdrop: !onlyAirdrop");
        _;
    }

    constructor(
        // BaseNFT
        string memory _name,
        string memory _symbol,
        string memory _uri,
        // Randomness
        address _vrfCoordinator,
        address _linkToken,
        bytes32 _keyHash,
        uint256 _fee,
        // MerkleAirdrop
        InitVars memory _initVars
    ) BaseNFT(_name, _symbol, _uri) Randomness(_vrfCoordinator, _linkToken, _keyHash, _fee) {
        airdrop = _initVars.airdrop;
        maxSupply = _initVars.maxSupply;
        saleAllocation = _initVars.saleAllocation;
        airdropAllocation = _initVars.airdropAllocation;
        price = _initVars.price;
        feeRecipient = _initVars.feeRecipient;

        _sealedPackIds._value = _initVars.maxSupply - _initVars.saleAllocation - _initVars.airdropAllocation;
    }

    function getSealedPackLeaf(address _user, uint256 _sealedPackId) public pure returns (bytes32) {
        return keccak256(abi.encodePacked(_user, _sealedPackId));
    }

    function getPackLeaf(Pack memory _pack) public pure returns (bytes32) {
        return keccak256(abi.encodePacked(_pack.packId, _pack.nfts, _pack.amounts));
    }

    function isRandomnessReady() public view returns (bool) {
        return randomNumber != 0;
    }

    function getPackId(uint256 _sealedPackId) public view isReady returns (uint256 packId) {
        uint256 random = randomNumber % maxSupply;
        packId = _sealedPackId + random;
        if (packId >= maxSupply) {
            packId -= maxSupply;
        }
    }

    function canClaimSealedPack(bytes32[] memory proof, address _user, uint256 _sealedPackId) public view returns (bool) {
        bytes32 leaf = getSealedPackLeaf(_user, _sealedPackId);
        return MerkleProof.verify(proof, sealedPackMerkleRoot, leaf);
    }

    function claimSealedPack(bytes32[] memory _proof, uint256 _sealedPackId) public {
        bytes32 leaf = getSealedPackLeaf(msg.sender, _sealedPackId);
        require(MerkleProof.verify(_proof, sealedPackMerkleRoot, leaf), "MerkleAirdrop: proof invalid");

        _mint(msg.sender, _sealedPackId);
    }

    function claimSealedPackMultiple(bytes32[][] memory _proof, uint256[] memory _sealedPackId) public {
        for (uint256 i = 0; i < _sealedPackId.length; i++) {
            claimSealedPack(_proof[i], _sealedPackId[i]);
        }
    }

    function claimAirdrop(address _user) external onlyAirdrop {
        require(airdropAllocation > 0, "MerkleAirdrop: nothing left to airdrop");
        airdropAllocation--;

        uint256 sealedPackId = _sealedPackIds.current();
        _sealedPackIds.increment();
        _mint(_user, sealedPackId);
    }

    function buySealedPack(uint256 _amount) public payable {
        require(price > 0, "MerkleAirdrop: price not set");
        require(saleAllocation >= _amount, "MerkleAirdrop: amount too big");
        require(msg.value == _amount * price, "MerkleAirdrop: Payment incorrect");

        for (uint256 i = 0; i < _amount; i++) {
            saleAllocation--;
            uint256 sealedPackId = _sealedPackIds.current();
            _sealedPackIds.increment();
            _mint(msg.sender, sealedPackId);
        }

        withdrawPayment();
    }

    function openPack(bytes32[] memory _proof, Pack memory _pack, uint256 _sealedPackId) public isReady {
        require(ownerOf(_sealedPackId) == msg.sender, "MerkleAirdrop: invalid owner");

        _burn(_sealedPackId);

        require(_pack.packId == getPackId(_sealedPackId), "MerkleAirdrop: invalid pack");

        bytes32 leaf = getPackLeaf(_pack);
        require(MerkleProof.verify(_proof, openedPackMerkleRoot, leaf), "MerkleAirdrop: proof invalid");

        // TODO: pagination?
        for (uint256 i = 0; i < _pack.nfts.length; i++) {
            SlingshotNFT(_pack.nfts[i]).bulkMint(msg.sender, _pack.amounts[i]);
        }
    }

    function withdrawPayment() public {
        if (address(this).balance > 0) {
            (bool success, ) = feeRecipient.call{value: address(this).balance}("");
            require(success, "MerkleAirdrop: Transfer failed.");
        }
    }

    function _mint(address _user, uint256 _sealedPackId) internal override {
        super._mint(_user, _sealedPackId);
        require(totalSupply() <= maxSupply, "MerkleAirdrop: max supply minted");
    }

    // ADMIN

    function setSealedPackMerkleRoot(bytes32 _sealedPackMerkleRoot) public onlyOwner {
        sealedPackMerkleRoot = _sealedPackMerkleRoot;
    }

    function setPackMerkleRoot(bytes32 _openedPackMerkleRoot) public onlyOwner {
        openedPackMerkleRoot = _openedPackMerkleRoot;
    }

    function setPrice(uint256 _price) public onlyOwner {
        price = _price;
    }

    function setAirdrop(address _airdrop) public onlyOwner {
        airdrop = _airdrop;
    }

    function setFeeRecipient(address _feeRecipient) public onlyOwner {
        feeRecipient = _feeRecipient;
    }

    function reveal() external onlyOwner {
        getRandomNumber();
    }
}
"
