    "contracts/Randomness.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.7;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@chainlink/contracts/src/v0.8/VRFConsumerBase.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721Enumerable.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";

contract Randomness is VRFConsumerBase {
    using Strings for uint256;

    address public immutable LINK_TOKEN;
    address public immutable VRF_COORDINATOR;

    bytes32 public keyHash;
    uint256 public fee;
    bytes32 public randomnessRequestId;
    uint256 public randomNumber;

    constructor(
        address _vrfCoordinator,
        address _linkToken,
        bytes32 _keyHash,
        uint256 _fee
    )
        VRFConsumerBase(_vrfCoordinator, _linkToken)
    {
        LINK_TOKEN = _linkToken;
        VRF_COORDINATOR = _vrfCoordinator;
        keyHash = _keyHash;
        fee = _fee;
    }

    function getRandomNumber() internal returns (bytes32 requestId) {
        require(LINK.balanceOf(address(this)) >= fee, "Randomness: Not enough LINK - fill contract");
        requestId = requestRandomness(keyHash, fee);
        randomnessRequestId = requestId;
    }

    /// @param \requestId randomness request ID
    /// @param randomness generated randomness by chainlink
    function fulfillRandomness(bytes32, uint256 randomness) internal override {
        randomNumber = randomness;
    }

    // Admin functions

    function _setLinkFee(uint256 newFee) internal {
        fee = newFee;
    }

    function _withdrawLink(address _to) internal {
        LINK.transfer(_to, LINK.balanceOf(address(this)));
    }
}
"
