    "contracts/BaseNFT.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.7;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721Enumerable.sol";

import "./MinterControl.sol";

contract BaseNFT is ERC721Enumerable, MinterControl {
    string private nameStorage;
    string private symbolStorage;
    string public baseURI;

    constructor(
        string memory _name,
        string memory _symbol,
        string memory _uri
    ) ERC721(_name, _symbol) {
        setName(_name);
        setSymbol(_symbol);
        setBaseURI(_uri);
    }

    function supportsInterface(bytes4 interfaceId)
        public
        view
        virtual
        override(ERC721Enumerable, AccessControl)
        returns (bool)
    {
        return super.supportsInterface(interfaceId);
    }

    function name() public view override returns (string memory) {
        return nameStorage;
    }

    function symbol() public view override returns (string memory) {
        return symbolStorage;
    }

    function setBaseURI(string memory _URI) public onlyOwner {
        baseURI = _URI;
    }

    function setName(string memory _name) public onlyOwner {
        nameStorage = _name;
    }

    function setSymbol(string memory _symbol) public onlyOwner {
        symbolStorage = _symbol;
    }

    function _baseURI() internal view override returns (string memory) {
        return baseURI;
    }
}
"
