    "contracts/NFTFactory.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.7;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";

import "./SlingshotNFT.sol";

contract NFTFactory is Ownable, ReentrancyGuard {
    address[] public allNFTs;

    event NFTDeployed(address nftContract, string name, string symbol, string baseURI);

    function deployNFTContract(
        string memory _name,
        string memory _symbol,
        string memory _baseURI,
        uint256 _maxSupply,
        address _minter
    ) external onlyOwner {
        SlingshotNFT nft = new SlingshotNFT(_name, _symbol, _baseURI, _maxSupply);
        nft.grantMinter(_minter);
        nft.grantOwner(owner());
        nft.renounceOwner();

        allNFTs.push(address(nft));
        emit NFTDeployed(address(nft), _name, _symbol, _baseURI);
    }

    function getAllNFTs() public view returns (address[] memory) {
        return allNFTs;
    }

    function getAllNFTsLength() public view returns (uint256) {
        return allNFTs.length;
    }
}
"
    },
    "contracts/SlingshotAirdrop.sol": {
      "content": "// SPDX-License-Identifier: AGPLv3
pragma solidity 0.8.7;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/math/Math.sol";
import "./MerkleAirdrop.sol";

contract SlingshotAirdrop is Ownable {
    uint256 public constant MAX_BATCH = 50;

    uint256 public totalClaims;
    MerkleAirdrop public merkleAirdrop;

    mapping(address => uint256) public claims;

    function leftToClaim(address _user) public view returns (uint256) {
        return claims[_user];
    }

    function claim() public {
        uint256 len = Math.min(leftToClaim(msg.sender), MAX_BATCH);
        require(len > 0, "Nothing to claim");
        claims[msg.sender] -= len;

        for (uint256 i = 0; i < len; i++) {
            merkleAirdrop.claimAirdrop(msg.sender);
        }
    }

    function addClaims(address[] memory _users, uint256[] memory _amounts) external onlyOwner {
        require(_users.length == _amounts.length, "Invalid data");

        for (uint256 i = 0; i < _users.length; i++) {
            claims[_users[i]] += _amounts[i];
            totalClaims += _amounts[i];
        }

        require(merkleAirdrop.airdropAllocation() <= totalClaims, "Airdrop allocation exceeded");
    }

    function removeClaims(address[] memory _users, uint256[] memory _amounts) external onlyOwner {
        require(_users.length == _amounts.length, "Invalid data");

        for (uint256 i = 0; i < _users.length; i++) {
            claims[_users[i]] -= _amounts[i];
            totalClaims -= _amounts[i];
        }
    }

    function setMerkleAirdrop(MerkleAirdrop _merkleAirdrop) public onlyOwner {
        merkleAirdrop = _merkleAirdrop;
    }
}
"
