    "contracts/MinterControl.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.7;

import "@openzeppelin/contracts/access/AccessControl.sol";

abstract contract MinterControl is AccessControl {
    bytes32 public constant SLINGSHOT_OWNER_ROLE = keccak256("SLINGSHOT_OWNER_ROLE");
    bytes32 public constant SLINGSHOT_MINTER_ROLE = keccak256("SLINGSHOT_MINTER_ROLE");

    modifier onlyOwner() {
        require(hasRole(SLINGSHOT_OWNER_ROLE, _msgSender()), "MinterControl: not a SLINGSHOT_OWNER_ROLE");
        _;
    }

    modifier onlyMinter() {
        require(isMinter(_msgSender()), "MinterControl: not a SLINGSHOT_MINTER_ROLE");
        _;
    }

    constructor() {
        _setRoleAdmin(SLINGSHOT_OWNER_ROLE, SLINGSHOT_OWNER_ROLE);
        _setRoleAdmin(SLINGSHOT_MINTER_ROLE, SLINGSHOT_OWNER_ROLE);

        _setupRole(SLINGSHOT_OWNER_ROLE, _msgSender());
    }

    function grantMinter(address _minter) external {
        grantRole(SLINGSHOT_MINTER_ROLE, _minter);
    }

    function renounceMinter() external {
        renounceRole(SLINGSHOT_MINTER_ROLE, _msgSender());
    }

    function isMinter(address _minter) public view returns (bool) {
        return hasRole(SLINGSHOT_MINTER_ROLE, _minter);
    }

    function grantOwner(address _owner) external {
        grantRole(SLINGSHOT_OWNER_ROLE, _owner);
    }

    function renounceOwner() external {
        renounceRole(SLINGSHOT_OWNER_ROLE, _msgSender());
    }

    function isOwner(address _owner) public view returns (bool) {
        return hasRole(SLINGSHOT_OWNER_ROLE, _owner);
    }
}
"
    },
