    "contracts/SlingshotNFT.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.7;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721Enumerable.sol";
import "@openzeppelin/contracts/utils/Counters.sol";

import "./BaseNFT.sol";
import "./NFTFactory.sol";

contract SlingshotNFT is BaseNFT {
    using Counters for Counters.Counter;
    Counters.Counter private _tokenIds;

    uint256 public immutable maxSupply;

    uint256 public airdropMinted;

    constructor(
        string memory _name,
        string memory _symbol,
        string memory _uri,
        uint256 _maxSupply
    ) BaseNFT(_name, _symbol, _uri) {
        maxSupply = _maxSupply;
    }

    function bulkMint(address _user, uint256 _amount) external onlyMinter {
        for (uint256 i = 0; i < _amount; i++) {
            _mint(_user);
        }
    }

    function _mint(address _user) internal returns (uint256) {
        require(totalSupply() < maxSupply, "SlingshotNFT: total supply reached");

        uint256 newItemId = _tokenIds.current();
        _tokenIds.increment();

        super._mint(_user, newItemId);

        return newItemId;
    }
}
"
    },
