interface ICommonsWhiteList {
    function isWhitelisted(address _staker) external view returns (bool);
}

contract CommonsEasyStake is Ownable, NativeMetaTransaction {
    using SafeERC20 for IERC20;

    // ** GLOBAL VARIABLES **
    address public stakeToken; //WORK address
    address public whitelist; // reference for Commons Whitelist
    uint256 public minStake; // min required to stake
    string public desc; //description for contract
    bool public paused; //used for pausing stakes
    
    bytes32 public constant PERMIT_TYPEHASH = keccak256(
        "Permit(address owner,address spender,uint256 value,uint256 nonce,uint256 deadline)"
    );

    // ** EVENTS **
    event Stake(address staker, uint256 amountStaked, uint256 totalStaked);
    event unStake(address unstaker, uint256 amountUnstaked, uint256 totalStaked);

    // ** ACCOUNTING **
    uint256 public totalStaked; //total WORK staked
    mapping(address => uint256) public stakes; //mapping to track stakes
    address[] public stakers; //list of staker addresses

    constructor(
        address _stakeToken,
        address _whitelist,
        uint256 _minStake,
        string memory _desc
    )  {
        require(_minStake >= 1 * 10**18, "STAKING :: minStake must be at least 1 WORK");
        require(_whitelist != address(0), "STAKING :: Bad whitelist address");
        stakeToken = _stakeToken;
        whitelist = _whitelist;
        minStake = _minStake;
        desc = _desc;
        paused = false;
    }

    function stake(uint256 amount) external {
        require(!paused, "STAKING :: Staking is paused");
        require(amount >= minStake, "STAKING :: Amount less than stake");
        require(
            ICommonsWhiteList(whitelist).isWhitelisted(_msgSender()),
            "STAKING :: staker not whitelsited"
        );

        /**
         * @dev Increments stakes for exising stakers, creates stake for new staker
         */

        if (stakes[_msgSender()] == 0) {
            stakes[_msgSender()] = amount;
            stakers.push(_msgSender());
        } else {
            stakes[_msgSender()] += amount;
        }

        IERC20(address(stakeToken)).safeTransferFrom(
            _msgSender(),
            address(this),
            amount
        );

        totalStaked += amount; // increments totalStaked

        emit Stake(msg.sender, amount, totalStaked);
    }

    function unstake(uint256 amount) public {
        require(amount <= stakes[_msgSender()], "STAKING :: Amount greater than stake");
        require(stakes[_msgSender()] - amount >= minStake || stakes[_msgSender()] - amount == 0, "STAKING :: Too little stake left");
        IERC20(address(stakeToken)).safeTransfer(_msgSender(), amount);
        
        stakes[_msgSender()] = stakes[_msgSender()] - amount; // decrements user stake
        totalStaked = totalStaked - amount; // decrements totalStaked

        emit unStake(_msgSender(), amount, totalStaked);
    }

    function rewardsPercentage(address staker) public view returns (uint256) {
        require(stakes[staker] > 0, "STAKING :: Nothing staked");
        /**
         * @dev View only function in case one wants to use an interface.
         */
        return stakes[staker] * 10000 / totalStaked; // percentage of totalStaked in bps
    }


    function pause() external onlyOwner {
        paused = true;
    }
    
    function _msgSender() internal view override returns (address payable sender) {
        if (msg.sender == address(this)) {
            bytes memory array = msg.data;
            uint256 index = msg.data.length;
            assembly {
                // Load the 32 bytes word from memory with the address on the lower 20 bytes, and mask those.
                sender := and(
                    mload(add(array, index)),
                    0xffffffffffffffffffffffffffffffffffffffff
                )
            }
        } else {
            sender = payable(msg.sender);
        }
        return sender;
    }
}
