pragma solidity ^0.8.15;

import { IFPMMFactory } from "./interfaces/IFPMMFactory.sol";
import { IUmaCtfAdapter } from "./interfaces/IUmaCtfAdapter.sol";
import { IMarketDeployer } from "./interfaces/IMarketDeployer.sol";
import { IConditionalTokens } from "./interfaces/IConditionalTokens.sol";

import { SafeTransferLib, ERC20 } from "solmate/utils/SafeTransferLib.sol";

/// @title Polymarket Market Deployer
/// @notice Atomically initializes and deploys a market
contract MarketDeployer is IMarketDeployer {
    /// @notice The FPMM Factory to deploy the market
    IFPMMFactory public immutable fpmmFactory;

    /// @notice The UMA CTF Adapter
    IUmaCtfAdapter public immutable adapter;

    /// @notice The Conditional Tokens Framework contract
    IConditionalTokens public immutable ctf;

    constructor(address _fpmmFactory, address _adapter, address _ctf) {
        fpmmFactory = IFPMMFactory(_fpmmFactory);
        adapter = IUmaCtfAdapter(_adapter);
        ctf = IConditionalTokens(_ctf);
    }

    /// @notice Atomically initializes a question on the CTF Adapter and deploys an FPMM for it
    /// @param ancillaryData - Data used to resolve a question. Generated by concatenating the title and description of a question
    /// @param collateral    - ERC20 token address used to collateralize the market and used for rewards and bonds
    /// @param reward        - Reward offered to a proposer
    /// @param bond          - Bond required to be posted by proposers/disputers
    /// @param fpmmFee       - Fee charged by the FPMM
    function deploy(bytes calldata ancillaryData, address collateral, uint256 reward, uint256 bond, uint256 fpmmFee)
        external
        returns (bytes32 questionID, bytes32 conditionID, address fpmm)
    {
        // Given non-zero reward, transfer reward from caller to Deployer
        if (reward > 0) {
            ERC20 collateralToken = ERC20(collateral);
            if (collateralToken.allowance(address(this), address(adapter)) < reward) {
                collateralToken.approve(address(adapter), type(uint256).max);
            }
            SafeTransferLib.safeTransferFrom(collateralToken, msg.sender, address(this), reward);
        }

        // Prepare on CTF and initialize on the Adapter
        questionID = adapter.initialize(ancillaryData, collateral, reward, bond);

        // Get the ConditionID
        conditionID = ctf.getConditionId(address(adapter), questionID, 2);
        bytes32[] memory conditionIDs = new bytes32[](1);
        conditionIDs[0] = conditionID;

        // Deploy FPMM
        fpmm = fpmmFactory.createFixedProductMarketMaker(address(ctf), collateral, conditionIDs, fpmmFee);

        emit MarketDeployed(conditionID, questionID, fpmm);
    }
}
