      "content": "// SPDX-License-Identifier: Apache-2.0
pragma solidity >=0.8.19;

import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import { AccessControl } from "@openzeppelin/contracts/access/AccessControl.sol";
import { IRollupProcessor } from "./IRollupProcessor.sol";
import { IAtricrypto3CurvePool } from "./IAtricrypto3CurvePool.sol";
import { IStargateReceiver } from "./IStargateReceiver.sol";
import { Strings } from "@openzeppelin/contracts/utils/Strings.sol";
import { Initializable } from "@openzeppelin/contracts/proxy/utils/Initializable.sol";
import { IStargateRouter } from "./IStargateRouter.sol";

contract NataGatewayV1 is IStargateReceiver, AccessControl, Initializable {
    struct SGWithdrawDetails {
        uint16 sgChainId;
        uint256 srcPoolId;
        uint256 dstPoolId;
        uint256 assetId;
        uint256 amount;
        address destination;
        bool complete;
    }

    struct SGDepositDetails {
        address owner;
        address incomingToken;
        uint256 incomingAmount;
        uint256 assetId;
        bool complete;
    }

    mapping(address => SGDepositDetails[]) public deposits;
    mapping(uint256 => SGWithdrawDetails) public withdraws;
    uint256[] public readyWithdraws;

    uint256 private nextWithdrawalId;
    address public constant ROLLUP_PROCESSOR = 0x03ebC6d159C41419747354bc819dF274Da9948B5;

    bytes32 public constant WITHDRAW_MANAGER_ROLE = keccak256("WITHDRAW_MANAGER_ROLE");
    bytes32 public constant ROLLUP_PROCESSOR_ROLE = keccak256("ROLLUP_PROCESSOR_ROLE");
    uint256 private constant MAX_UINT256 = 2**256 - 1;

    // Curve
    address private constant CURVE_POOL = 0x1d8b86e3D88cDb2d34688e87E72F388Cb541B7C8; // Polygon atricrypto3
    address private constant DAI = 0x8f3Cf7ad23Cd3CaDbD9735AFf958023239c6A063; // 0
    address private constant USDC = 0x2791Bca1f2de4661ED88A30C99A7a9449Aa84174; // 1
    address private constant USDT = 0xc2132D05D31c914a87C6611C10748AEb04B58e8F; // 2
    address private constant WBTC = 0x1BFD67037B42Cf73acF2047067bd4F2C47D9BfD6; // 3
    address private constant WETH = 0x7ceB23fD6bC0adD59E62ac25578270cFf1b9f619; // 4    

    // Stargate
    address private constant STARGATE_ROUTER = 0xeCc19E177d24551aA7ed6Bc6FE566eCa726CC8a9;

    event XChainWithdrawalReady(SGWithdrawDetails withdrawal);
    event XChainWithdrawalComplete(SGWithdrawDetails withdrawal);
    event XChainDeposit(uint256 assetId, uint256 amount, address owner);
    event XChainWithdrawCreated(uint256 id);

    constructor() {
        _disableInitializers();
    }

    function initialize(address _owner, address _withdrawManager) external initializer() {
        nextWithdrawalId = 1;
        _grantRole(DEFAULT_ADMIN_ROLE, _owner);
        _grantRole(ROLLUP_PROCESSOR_ROLE, ROLLUP_PROCESSOR);
        _grantRole(WITHDRAW_MANAGER_ROLE, _withdrawManager);
    }

    function readyWithdrawsLength() external view returns (uint256 length) {
        length = readyWithdraws.length;
    }

    function userDepositsLength(address owner) external view returns (uint256 length) {
        length = deposits[owner].length;
    }

    function createWithdraw(
        uint16 _sgChainId, 
        uint256 _srcPoolId,
        uint256 _dstPoolId,
        uint256 _assetId, // Nata Network assetId
        uint256 _amount, // Amount withdrawn from Nata Network
        address _destination
    ) 
        external
        onlyRole(WITHDRAW_MANAGER_ROLE)
        returns (uint256 id)
    {
        require(_srcPoolId == _dstPoolId, "Token swaps not supported");
        require(_srcPoolId == 1, "Only USDC supported");
        require(_sgChainId != 151 && _sgChainId != 183 && _sgChainId != 102, "BNB/Metis/Linea not supported");
        require(_assetId == 1 || _assetId == 2, "Only DAI/WETH withdrawals supported");

        id = nextWithdrawalId;
        nextWithdrawalId++;

        withdraws[id] = SGWithdrawDetails({
            sgChainId: _sgChainId,
            srcPoolId: _srcPoolId,
            dstPoolId: _dstPoolId,
            assetId: _assetId,
            amount: _amount,
            destination: _destination,
            complete: false
        });

        emit XChainWithdrawCreated(id);
    }

    function xChainNotify(uint256 _amount) external onlyRole(ROLLUP_PROCESSOR_ROLE) {
        string memory amount = Strings.toString(_amount);
        uint256 amountLength = bytes(amount).length;

        if (amountLength < 3) {
            revert("Amount too small");
        }

        // Extract id from amount
        // e.g. if amount = 10123456789123456789, id = 123456789123456789
        //      if amount = 123450123, id = 123
        uint256 id = 0;
        bytes memory zero = bytes("0");
        for (uint256 i = 0; i < amountLength; i++) {
            bytes memory character = new bytes(1);
            character[0] = bytes(amount)[amountLength - 1 - i];

            if (keccak256(character) == keccak256(zero)) break;

            id += (uint8(character[0]) - 48) * (10 ** i);
        }

        SGWithdrawDetails memory withdrawal = withdraws[id];

        if (withdrawal.complete) {
            revert("Invalid xChain xfer id");
        }

        readyWithdraws.push(id);   

        emit XChainWithdrawalReady(withdrawal);
    }

    function actionReadyWithdrawal() external onlyRole(WITHDRAW_MANAGER_ROLE) {
        // Going to action the most recent first because removing an element from the back of an array is easier than
        // the front.
        uint256 id = readyWithdraws[readyWithdraws.length - 1];
        SGWithdrawDetails memory withdrawal = withdraws[id];

        require(withdrawal.srcPoolId == withdrawal.dstPoolId, "Token swaps not supported");
        require(withdrawal.srcPoolId == 1, "Only USDC supported");
        require(
            withdrawal.sgChainId != 151 && withdrawal.sgChainId != 183 && withdrawal.sgChainId != 102,
            "BNB/Metis/Linea not supported"
        );
        require(withdrawal.assetId == 1 || withdrawal.assetId == 2, "Only DAI/WETH withdrawals supported");

        IERC20 tokenToSend = IERC20(USDC);
        IERC20 tokenWithdrawn = withdrawal.assetId == 1 ? IERC20(DAI) : IERC20(WETH);
        uint256 amountToSend = withdrawal.amount;

        if (address(tokenWithdrawn) != address(tokenToSend)) {
            amountToSend = _curveSwap(address(tokenWithdrawn), address(tokenToSend), withdrawal.amount);
        }

        sgSend(
            withdrawal.sgChainId, 
            withdrawal.srcPoolId, 
            withdrawal.dstPoolId, 
            withdrawal.amount, 
            withdrawal.destination
        );

        withdraws[id].complete = true;
        emit XChainWithdrawalComplete(withdraws[id]);
        readyWithdraws.pop();        
    }

    function sgSend(
        uint16 _sgChainId,
        uint256 _srcPoolId,
        uint256 _dstPoolId,
        uint256 _amount,
        address _destination
    ) 
        public
        onlyRole(WITHDRAW_MANAGER_ROLE)
    {
        require(_srcPoolId == _dstPoolId, "Token swaps not supported");
        require(_srcPoolId == 1, "Only USDC supported");
        require(_sgChainId != 151 && _sgChainId != 183 && _sgChainId != 102, "BNB/Metis/Linea not supported");

        uint256 sgAllowance = IERC20(USDC).allowance(address(this), STARGATE_ROUTER);
        if (sgAllowance < _amount) {
            IERC20(USDC).approve(STARGATE_ROUTER, MAX_UINT256);
        }

        IStargateRouter.lzTxObj memory lzTxParams = IStargateRouter.lzTxObj(0, 0, "0x");
        bytes memory destinationBytes = abi.encodePacked(_destination);

        uint256 feeWei = _sgFee(_sgChainId, _destination);

        uint256 minAmount = (_amount * 99) / 100; // 1% slippage

        IStargateRouter(STARGATE_ROUTER).swap{ value: feeWei }(
            _sgChainId,
            _srcPoolId,
            _dstPoolId,            
            payable(address(this)),
            _amount,
            minAmount,
            lzTxParams,
            destinationBytes,
            bytes("")
        );
    }

    function sgReceive(
        uint16 _chainId,
        bytes memory _srcAddress,
        uint256 _nonce,
        address _token,
        uint256 amountLD,
        bytes memory payload
    ) 
        external 
    {
        require(msg.sender == STARGATE_ROUTER, "Sender not SG router");

        uint256 assetId;
        address owner;
        assembly {
            assetId := mload(add(payload, 0x20))
            owner := mload(add(payload, 0x34))
        }

        require(_token == USDC, "Must x-chain transfer USDC");
        require(assetId == 1 || assetId == 2, "Only DAI/WETH deposits supported");

        deposits[owner].push(
            SGDepositDetails({
                owner: owner,
                incomingToken: _token,
                incomingAmount: amountLD,
                assetId: assetId,
                complete: false
            })
        );

        IERC20 depositToken = assetId == 1 ? IERC20(DAI) : IERC20(WETH);
        uint256 amountToDeposit = amountLD;

        if (_token != address(depositToken)) {
            amountToDeposit = _curveSwap(_token, address(depositToken), amountLD);
        }

        uint256 rpAllowance = depositToken.allowance(address(this), ROLLUP_PROCESSOR);
        if (rpAllowance < amountToDeposit) {
            depositToken.approve(ROLLUP_PROCESSOR, MAX_UINT256);
        }

        IRollupProcessor(ROLLUP_PROCESSOR).depositPendingFunds(assetId, amountToDeposit, owner, 0x0);

        emit XChainDeposit(assetId, amountToDeposit, owner);

        deposits[owner][deposits[owner].length - 1].complete = true;
    }

    function getVersion() public view virtual returns (uint8 version) {
        return 1;
    }

    receive() external payable {
    }

    function _tokenToCurveId(address _token) internal pure returns (uint256 id) {
        if (_token == DAI) id = 0;
        if (_token == USDC) id = 1;
        if (_token == USDT) id = 2;
        if (_token == WBTC) id = 3;
        if (_token == WETH) id = 4;
    }

    function _curveSwap(
        address _fromTokenAddress,
        address _toTokenAddress,
        uint256 _amount
    )
        internal
        returns (uint256 amount)
    {
        IERC20 fromToken = IERC20(_fromTokenAddress);
        IERC20 toToken = IERC20(_toTokenAddress);

        uint256 cpAllowance = fromToken.allowance(address(this), CURVE_POOL);
        if (cpAllowance < _amount) {
            fromToken.approve(CURVE_POOL, MAX_UINT256);
        }

        IAtricrypto3CurvePool curvePool = IAtricrypto3CurvePool(CURVE_POOL);
        uint256 i = _tokenToCurveId(_fromTokenAddress);
        uint256 j = _tokenToCurveId(_toTokenAddress);

        // get_dy_underlying is unreliable for WETH (reverts a lot), get contract balance before and after
        // to get the amount swapped
        uint256 balanceBefore = toToken.balanceOf(address(this));

        curvePool.exchange_underlying(i, j, _amount, 0);

        amount = toToken.balanceOf(address(this)) - balanceBefore;
    }

    function _sgFee(uint16 _sgChainId, address _destination) internal view returns (uint256) {
        bytes memory destinationBytes = abi.encodePacked(_destination);

        (uint256 feeWei,) = IStargateRouter(STARGATE_ROUTER).quoteLayerZeroFee(
            _sgChainId, 
            1, 
            destinationBytes, 
            bytes(""), 
            IStargateRouter.lzTxObj(0, 0, "0x")
        );

        return feeWei;
    }
}"
