import { ERC1155 } from "@openzeppelin/contracts/token/ERC1155/ERC1155.sol";
import { IERC1155 } from "@openzeppelin/contracts/token/ERC1155/IERC1155.sol";
import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import { Ownable } from "@openzeppelin/contracts/access/Ownable.sol";
import { IMintableERC1155 } from "../interfaces/IMintableERC1155.sol";
import { NativeMetaTransaction } from "../common/NativeMetaTransaction.sol";
import { ContextMixin } from "../common/ContextMixin.sol";
import { AccessControl } from "@openzeppelin/contracts/access/AccessControl.sol";
import { IERC165 } from "@openzeppelin/contracts/interfaces/IERC165.sol";
import { AccessControlMixin } from "../common/AccessControlMixin.sol";
import { Strings } from "@openzeppelin/contracts/utils/Strings.sol";

contract NobleOnyxPolygonPets is
	ERC1155,
	AccessControlMixin,
	NativeMetaTransaction,
	ContextMixin,
	IMintableERC1155,
	Ownable
{
	using Strings for uint256;

	bytes32 public constant PREDICATE_ROLE = keccak256("PREDICATE_ROLE");
	// Contract name
	string public constant name = "Noble Onyx Polygon Pets";
	// Contract symbol
	string public constant symbol = "PETS";
	string private constant _uri =
		"https://api.ppw.digital/api/mysterybox/metadata/box/";

	constructor() ERC1155(_uri) {
		_setupContractId(symbol);
		_setupRole(DEFAULT_ADMIN_ROLE, _msgSender());
		_setupRole(PREDICATE_ROLE, _msgSender());
		_initializeEIP712(name);
	}

	function uri(uint256 _id)
		public
		view
		virtual
		override
		returns (string memory)
	{
		return string(abi.encodePacked(_uri, _id.toString()));
	}

	function collect(address _token) external onlyOwner {
		if (_token == address(0)) {
			payable(msg.sender).transfer(address(this).balance);
		} else {
			uint256 amount = IERC20(_token).balanceOf(address(this));
			IERC20(_token).transfer(msg.sender, amount);
		}
	}

	function collectNFTs(address _token, uint256 _tokenId) external onlyOwner {
		uint256 amount = IERC1155(_token).balanceOf(address(this), _tokenId);
		IERC1155(_token).safeTransferFrom(
			address(this),
			msg.sender,
			_tokenId,
			amount,
			""
		);
	}

	function mint(
		address account,
		uint256 id,
		uint256 amount,
		bytes calldata data
	) external override only(PREDICATE_ROLE) {
		_mint(account, id, amount, data);
	}

	function mintBatch(
		address to,
		uint256[] calldata ids,
		uint256[] calldata amounts,
		bytes calldata data
	) external override only(PREDICATE_ROLE) {
		_mintBatch(to, ids, amounts, data);
	}

	function burn(
		address account,
		uint256 id,
		uint256 value
	) public virtual {
		require(
			account == _msgSender() || isApprovedForAll(account, _msgSender()),
			"ERC1155: caller is not owner nor approved"
		);
		_burn(account, id, value);
	}

	function burnBatch(
		address account,
		uint256[] memory ids,
		uint256[] memory values
	) public virtual {
		require(
			account == _msgSender() || isApprovedForAll(account, _msgSender()),
			"ERC1155: caller is not owner nor approved"
		);
		_burnBatch(account, ids, values);
	}

	function _msgSender() internal view virtual override returns (address) {
		return ContextMixin.msgSender();
	}

	function supportsInterface(bytes4 interfaceId)
		public
		view
		virtual
		override(AccessControl, IERC165, ERC1155)
		returns (bool)
	{
		return
			interfaceId == type(IMintableERC1155).interfaceId ||
			super.supportsInterface(interfaceId);
	}
}
